INSERT INTO ability VALUES ( 1, 'Ability Score Increase (Str +1, Dex +1, Con +1, Int +1, Wis +1, Cha +1)', 'Strength +1, Dexterity +1, Constitution +1, Intelligence +1, Wisdom +1, Charisma +1.', 0, 'Ability' );
INSERT INTO ability VALUES ( 2, 'Ability Score Increase (Str +2, Cha +1)', 'Strength +2, Charisma +1.', 0, 'Ability' );
INSERT INTO ability VALUES ( 3, 'Draconic Ancestry', 'You have draconic ancestry. Choose one type of dragon from the Draconic Ancestry table. Your breath weapon and damage resistance are determined by the dragon type, as shown in the table.', 0, 'Ability' );
INSERT INTO ability VALUES ( 4, 'Breath Weapon', 'You can use your action to exhale destructive energy. Your draconic ancestry determines the size, shape, and damage type of the exhalation. When you use your breath weapon, each creature in the area of the exhalation must make a saving throw, the type of which is determined by your draconic ancestry. The DC for this saving throw equals 8 + your Constitution modifier + your proficiency bonus. A creature takes 2d6 damage on a failed save, and half as much damage on a successful Not for resale. Permission granted to print or photocopy this document for personal use only. System Reference Document 5.1 6 one. The damage increases to 3d6 at 6th level, 4d6 at 11th level, and 5d6 at 16th level. After you use your breath weapon, you canâ€™t use it again until you complete a short or long rest.', 0, 'Ability' );
INSERT INTO ability VALUES ( 5, 'Damage Resistance', 'You have resistance to the damage type associated with your draconic ancestry.', 0, 'Ability' );
INSERT INTO ability VALUES ( 6, 'Ability Score Increase (Con +2)', 'Constitution +2.', 0, 'Ability' );
INSERT INTO ability VALUES ( 7, 'Darkvision', 'Accustomed to life underground, you have superior vision in dark and dim conditions. You can see in dim light within 60 feet of you as if it were bright light, and in darkness as if it were dim light. You can''t discern color in darkness, only shades of gray.', 0, 'Ability' );
INSERT INTO ability VALUES ( 8, 'Dwarven Resilience', 'You have advantage on saving throws against poison, and you have resistance against poison damage.', 0, 'Ability' );
INSERT INTO ability VALUES ( 9, 'Dwarven Combat Training', 'You have proficiency with the battleaxe, handaxe, light hammer, and warhammer.', 0, 'Ability' );
INSERT INTO ability VALUES ( 10, 'Tool Proficiency', 'You gain proficiency with the artisan''s tools of your choice: smith''s tools, brewer''s supplies, or mason''s tools.', 0, 'Ability' );
INSERT INTO ability VALUES ( 11, 'Stonecunning', 'Whenever you make an Intelligence (History) check related to the origin of stonework, you are considered proficient in the History skill and add double your proficiency bonus to the check, instead of your normal proficiency bonus.', 0, 'Ability' );
INSERT INTO ability VALUES ( 12, 'Ability Score Increase (Dex +2)', 'Dexterity +2.', 0, 'Ability' );
INSERT INTO ability VALUES ( 13, 'Keen Senses', 'You have proficiency in the Perception skill.', 0, 'Ability' );
INSERT INTO ability VALUES ( 14, 'Fey Ancestry', 'You have advantage on saving throws against being charmed, and magic can''t put you to sleep.', 0, 'Ability' );
INSERT INTO ability VALUES ( 15, 'Trance', 'Elves don''t need to sleep. Instead, they meditate deeply, remaining semiconscious, for 4 hours a day. (The Common word for such meditation is "trance.") While meditating, you can dream after a fashion, such dreams are actually mental exercises that have become reflexive through years of practice. After resting in this way, you gain the same benefit that a human does from 8 hours of sleep.', 0, 'Ability' );
INSERT INTO ability VALUES ( 16, 'Ability Score Increase (Int +2)', 'Intelligence +2.', 0, 'Ability' );
INSERT INTO ability VALUES ( 17, 'Gnome Cunning', 'You have advantage on all Intelligence, Wisdom, and Charisma saving throws against magic.', 0, 'Ability' );
INSERT INTO ability VALUES ( 18, 'Ability Score Increase (Cha +2. Two other ability scores of your choice increase by 1)', 'Charisma +2. Two other ability scores of your choice increase by 1.', 0, 'Ability' );
INSERT INTO ability VALUES ( 19, 'Skill Versatility', 'You gain proficiency in two skills of your choice.', 0, 'Ability' );
INSERT INTO ability VALUES ( 20, 'Ability Score Increase (Str +2, Con +1)', 'Strength +2, Constitution +1.', 0, 'Ability' );
INSERT INTO ability VALUES ( 21, 'Menacing', 'You gain proficiency in the Intimidation skill.', 0, 'Ability' );
INSERT INTO ability VALUES ( 22, 'Relentless Endurance', 'When you are reduced to 0 hit points but not killed outright, you can drop to 1 hit point instead. You can''t use this feature again until you finish a long rest.', 0, 'Ability' );
INSERT INTO ability VALUES ( 23, 'Savage Attacks', 'When you score a critical hit with a melee weapon attack, you can roll one of the weapon''s damage dice one additional time and add it to the extra damage of the critical hit.', 0, 'Ability' );
INSERT INTO ability VALUES ( 24, 'Ability Score Increase (Dex +2)', 'Dexterity +2.', 0, 'Ability' );
INSERT INTO ability VALUES ( 25, 'Lucky', 'When you roll a 1 on the d20 for an attack roll, ability check, or saving throw, you can reroll the die and must use the new roll.', 0, 'Ability' );
INSERT INTO ability VALUES ( 26, 'Brave', 'You have advantage on saving throws against being frightened.', 0, 'Ability' );
INSERT INTO ability VALUES ( 27, 'Halfling Nimbleness', 'You can move through the space of any creature that is of a size larger than yours.', 0, 'Ability' );
INSERT INTO ability VALUES ( 28, 'Ability Score Increase (Int +1, Cha +2)', 'Intelligence +1, Charisma +2.', 0, 'Ability' );
INSERT INTO ability VALUES ( 29, 'Hellish Resistance', 'You have resistance to fire damage.', 0, 'Ability' );
INSERT INTO ability VALUES ( 30, 'Infernal Legacy', 'You know the thaumaturgy cantrip. When you reach 3rd level, you can cast the hellish rebuke spell as a 2nd-level spell once with this trait and regain the ability to do so when you finish a long rest. When you reach 5th level, you can cast the darkness spell once with this trait and regain the ability to do so when you finish a long rest. Charisma is your spellcasting ability for these spells.', 0, 'Ability' );
INSERT INTO ability VALUES ( 31, 'Rage', 'In battle, you fight with primal ferocity. On your turn, you can enter a rage as a bonus action.<br/><br/>While raging, you gain the following benefits if you aren''t wearing heavy armor:<br/><br/>- You have advantage on Strength checks and Strength saving throws.<br/>- When you make a melee weapon attack using Strength, you gain a bonus to the damage roll that increases as you gain levels as a barbarian, as shown in the Rage Damage column of the Barbarian table.<br/>- You have resistance to bludgeoning, piercing, and slashing damage.<br/><br/>If you are able to cast spells, you can''t cast them or concentrate on them while raging.<br/><br/>Your rage lasts for 1 minute. It ends early if you are knocked unconscious or if your turn ends and you haven''t attacked a hostile creature since your last turn or taken damage since then. You can also end your rage on your turn as a bonus action.<br/><br/>Once you have raged the number of times shown for your barbarian level in the Rages column of the Barbarian table, you must finish a long rest before you can rage again.', 0, 'Ability' );
INSERT INTO ability VALUES ( 32, 'Unarmored Defense', 'While you are not wearing any armor, your Armor Class equals 10 + your Dexterity modifier + your Constitution modifier. You can use a shield and still gain this benefit.', 0, 'Ability' );
INSERT INTO ability VALUES ( 33, 'Reckless Attack', 'Starting at 2nd level, you can throw aside all concern for defense to attack with fierce desperation. When you make your first attack on your turn, you can decide to attack recklessly. Doing so gives you advantage on melee weapon attack rolls using Strength during this turn, but attack rolls against you have advantage until your next turn.', 0, 'Ability' );
INSERT INTO ability VALUES ( 34, 'Danger Sense', 'At 2nd level, you gain an uncanny sense of when things nearby aren''t as they should be, giving you an edge when you dodge away from danger.<br/><br/>You have advantage on Dexterity saving throws against effects that you can see, such as traps and spells. To gain this benefit, you can''t be blinded, deafened, or incapacitated.', 0, 'Ability' );
INSERT INTO ability VALUES ( 35, 'Ability Score Improvement', 'When you reach 4th level, and again at 8th, 12th, 16th, and 19th level, you can increase one ability score of your choice by 2, or you can increase two ability scores of your choice by 1. As normal, you can''t increase an ability score above 20 using this feature.', 0, 'Ability' );
INSERT INTO ability VALUES ( 36, 'Extra Attack', 'Beginning at 5th level, you can attack twice, instead of once, whenever you take the Attack action on your turn.', 0, 'Ability' );
INSERT INTO ability VALUES ( 37, 'Fast Movement', 'Starting at 5th level, your speed increases by 10 feet while you aren''t wearing heavy armor.', 0, 'Ability' );
INSERT INTO ability VALUES ( 38, 'Feral Instinct', 'By 7th level, your instincts are so honed that you have advantage on initiative rolls.<br/><br/>Additionally, if you are surprised at the beginning of combat and aren''t incapacitated, you can act normally on your first turn, but only if you enter your rage before doing anything else on that turn.', 0, 'Ability' );
INSERT INTO ability VALUES ( 39, 'Brutal Critical', 'Beginning at 9th level, you can roll one additional weapon damage die when determining the extra damage for a critical hit with a melee attack.<br/><br/>This increases to two additional dice at 13th level and three additional dice at 17th level.', 0, 'Ability' );
INSERT INTO ability VALUES ( 40, 'Relentless Rage', 'Starting at 11th level, your rage can keep you fighting despite grievous wounds. If you drop to 0 hit points while you''re raging and don''t die outright, you can make a DC 10 Constitution saving throw. If you succeed, you drop to 1 hit point instead.<br/><br/>Each time you use this feature after the first, the DC increases by 5. When you finish a short or long rest, the DC resets to 10.', 0, 'Ability' );
INSERT INTO ability VALUES ( 41, 'Persistent Rage', 'Beginning at 15th level, your rage is so fierce that it ends early only if you fall unconscious or if you choose to end it.', 0, 'Ability' );
INSERT INTO ability VALUES ( 42, 'Indomitable Might', 'Beginning at 18th level, if your total for a Strength check is less than your Strength score, you can use that score in place of the total.', 0, 'Ability' );
INSERT INTO ability VALUES ( 43, 'Primal Champion', 'At 20th level, you embody the power of the wilds. Your Strength and Constitution scores increase by 4. Your maximum for those scores is now 24.', 0, 'Ability' );
INSERT INTO ability VALUES ( 44, 'Frenzy', 'Starting at 3rd level, you can go into a frenzy when you rage. If you do so, for the duration of your rage you can make a single melee weapon attack as a bonus action on each of your turns after this one. When your rage ends, you suffer one level of exhaustion.', 0, 'Ability' );
INSERT INTO ability VALUES ( 45, 'Mindless Rage', 'Beginning at 6th level, you can''t be charmed or frightened while raging. If you are charmed or frightened when you enter your rage, the effect is suspended for the duration of the rage.', 0, 'Ability' );
INSERT INTO ability VALUES ( 46, 'Intimidating Presence', 'Beginning at 10th level, you can use your action to frighten someone with your menacing presence. When you do so, choose one creature that you can see within 30 feet of you. If the creature can see or hear you, it must succeed on a Wisdom saving throw (DC equal to 8 + your proficiency bonus + your Charisma modifier) or be frightened of you until the end of your next turn. On subsequent turns, you can use your action to extend the duration of this effect on the frightened creature until the end of your next turn. This effect ends if the creature ends its turn out of line of sight or more than 60 feet away from you.<br/><br/>If the creature succeeds on its saving throw, you can''t use this feature on that creature again for 24 hours.', 0, 'Ability' );
INSERT INTO ability VALUES ( 47, 'Retaliation', 'Starting at 14th level, when you take damage from a creature that is within 5 feet of you, you can use your reaction to make a melee weapon attack against that creature.', 0, 'Ability' );
INSERT INTO ability VALUES ( 48, 'Spellcasting - Bard', 'You have learned to untangle and reshape the fabric of reality in harmony with your wishes and music.<br/><br/>Your spells are part of your vast repertoire, magic that you can tune to different situations.<br/><h1>Cantrips</h1>You know two cantrips of your choice from the Bard Spell List. You learn additional bard cantrips of your choice at higher levels, as shown in the Cantrips Known column of the Bard table.<br/><h1>Spell Slots</h1>The Bard table shows how many spell slots you have to cast your spells of 1st level and higher. To cast one of these spells, you must expend a slot of the spell''''s level or higher. You regain all expended spell slots when you finish a long rest.<br/><br/>For example, if you know the 1st-level spell cure wounds and have a 1st-level and a 2nd-level spell slot available, you can cast cure wounds using either slot.<br/><h1>Spells Known of 1st Level and Higher</h1>You know four 1st-level spells of your choice from the Bard Spell List.<br/><br/>The Spells Known column of the Bard table shows when you learn more bard spells of your choice. Each of these spells must be of a level for which you have spell slots, as shown on the table. For instance, when you reach 3rd level in this class, you can learn one new spell of 1st or 2nd level.<br/><br/>Additionally, when you gain a level in this class, you can choose one of the bard spells you know and replace it with another spell from the bard spell list, which also must be of a level for which you have spell slots.<br/><h1>Spellcasting Ability</h1>Charisma is your spellcasting ability for your bard spells. Your magic comes from the heart and soul you pour into the performance of your music or oration. You use your Charisma whenever a spell refers to your spellcasting ability. In addition, you use your Charisma modifier when setting the saving throw DC for a bard spell you cast and when making an attack roll with one.<br/><br/>- <b>Spell save D</b>C = 8 + your proficiency bonus + your Charisma modifier<br/><br/>- <b>Spell attack modifier</b> = your proficiency bonus + your Charisma modifier<br/><br/><h1>Ritual Casting</h1>You can cast any bard spell you know as a ritual if that spell has the ritual tag.<br/><h1>Spellcasting Focus</h1>You can use a musical instrument (see adventuring-gear) as a spellcasting focus for your bard spells.', 0, 'Ability' );
INSERT INTO ability VALUES ( 49, 'Bardic Inspiration', 'You can inspire others through stirring words or music. To do so, you use a bonus action on your turn to choose one creature other than yourself within 60 feet of you who can hear you. That creature gains one Bardic Inspiration die, a d6.<br/><br/>Once within the next 10 minutes, the creature can roll the die and add the number rolled to one ability check, attack roll, or saving throw it makes. The creature can wait until after it rolls the d20 before deciding to use the Bardic Inspiration die, but must decide before the GM says whether the roll succeeds or fails. Once the Bardic Inspiration die is rolled, it is lost. A creature can have only one Bardic Inspiration die at a time.<br/><br/>You can use this feature a number of times equal to your Charisma modifier (a minimum of once). You regain any expended uses when you finish a long rest.<br/><br/>Your Bardic Inspiration die changes when you reach certain levels in this class. The die becomes a d8 at 5th level, a d10 at 10th level, and a d12 at 15th level.', 0, 'Ability' );
INSERT INTO ability VALUES ( 50, 'Jack of All Trades', 'Starting at 2nd level, you can add half your proficiency bonus, rounded down, to any ability check you make that doesn''t already include your proficiency bonus.', 0, 'Ability' );
INSERT INTO ability VALUES ( 51, 'Song of Rest', 'Beginning at 2nd level, you can use soothing music or oration to help revitalize your wounded allies during a short rest. If you or any friendly creatures who can hear your performance regain hit points at the end of the short rest by spending one or more Hit Dice, each of those creatures regains an extra 1d6 hit points.<br/><br/>The extra hit points increase when you reach certain levels in this class: to 1d8 at 9th level, to 1d10 at 13th level, and to 1d12 at 17th level.', 0, 'Ability' );
INSERT INTO ability VALUES ( 52, 'Bard College', 'At 3rd level, you delve into the advanced technicques of a bard college of your choice, such as the College of Lore. Your choice grants you features at 3rd level and again at 6th and 14th level.', 0, 'Ability' );
INSERT INTO ability VALUES ( 53, 'Expertise', 'At 3rd level, choose two of your skill proficiencies. Your proficiency bonus is doubled for any ability check you make that uses either of the chosen proficiencies. At 10th level, you can choose another two skill proficiencies to gain this benefit.', 0, 'Ability' );
INSERT INTO ability VALUES ( 54, 'Font of Inspiration', 'Beginning when you reach 5th level, you regain all of your expended uses of Bardic Inspiration when you finish a short or long rest.', 0, 'Ability' );
INSERT INTO ability VALUES ( 55, 'Countercharm', 'At 6th level, you gain the ability to use musical notes or words of power to disrupt mind-influencing effects. As an action, you can start a performance that lasts until the end of your next turn. During that time, you and any friendly creatures within 30 feet of you have advantage on saving throws against being frightened or charmed. A creature must be able to hear you to gain this benefit. The performance ends early if you are incapacitated or silenced or if you voluntarily end it (no action required).', 0, 'Ability' );
INSERT INTO ability VALUES ( 56, 'Magical Secrets', 'By 10th level, you have plundered magical knowledge from a wide spectrum of disciplines. Choose two spells from any class, including this one. A spell you choose must be of a level you can cast, as shown on the Bard table, or a cantrip.<br/><br/>The chosen spells count as bard spells for you and are included in the number in the Spells Known column of the Bard table.<br/><br/>You learn two additional spells from any class at 14th level and again at 18th level.', 0, 'Ability' );
INSERT INTO ability VALUES ( 57, 'Superior Inspiration', 'At 20th level, when you roll initiative and have no uses of Bardic Inspiration left, you regain one use.', 0, 'Ability' );
INSERT INTO ability VALUES ( 58, 'College of Lore', 'Bards of the College of Lore know something about most things, collecting bits of knowledge from sources as diverse as scholarly tomes and peasant tales. Whether singing folk ballads in taverns or elaborate compositions in royal courts, these bards use their gifts to hold audiences spellbound. When the applause dies down, the audience members might find themselves questioning everything they held to be true, from their faith in the priesthood of the local temple to their loyalty to the king.<br/><br/>The loyalty of these bards lies in the pursuit of beauty and truth, not in fealty to a monarch or following the tenets of a deity. A noble who keeps such a bard as a herald or advisor knows that the bard would rather be honest than politic.<br/><br/>The college''s members gather in libraries and sometimes in actual colleges, complete with classrooms and dormitories, to share their lore with one another. They also meet at festivals or affairs of state, where they can expose corruption, unravel lies, and poke fun at self-important figures of authority.', 0, 'Ability' );
INSERT INTO ability VALUES ( 59, 'College of Lore - Bonus Proficiencies', 'When you join the College of Lore at 3rd level, you gain proficiency with three skills of your choice.', 0, 'Ability' );
INSERT INTO ability VALUES ( 60, 'College of Lore - Cutting Words', 'Also at 3rd level, you learn how to use your wit to distract, confuse, and otherwise sap the confidence and competence of others. When a creature that you can see within 60 feet of you makes an attack roll, an ability check, or a damage roll, you can use your reaction to expend one of your uses of Bardic Inspiration, rolling a Bardic Inspiration die and subtracting the number rolled from the creature''s roll. You can choose to use this feature after the creature makes its roll, but before the GM determines whether the attack roll or ability check succeeds or fails, or before the creature deals its damage. The creature is immune if it can''t hear you or if it''s immune to being charmed.', 0, 'Ability' );
INSERT INTO ability VALUES ( 61, 'College of Lore - Additional Magical Secrets', 'At 6th level, you learn two spells of your choice from any class. A spell you choose must be of a level you can cast, as shown on the Bard table, or a cantrip. The chosen spells count as bard spells for you but don''t count against the number of bard spells you know.', 0, 'Ability' );
INSERT INTO ability VALUES ( 62, 'College of Lore - Peerless Skill', 'Starting at 14th level, when you make an ability check, you can expend one use of Bardic Inspiration. Roll a Bardic Inspiration die and add the number rolled to your ability check. You can choose to do so after you roll the die for the ability check, but before the GM tells you whether you succeed or fail.', 0, 'Ability' );
INSERT INTO ability VALUES ( 63, 'Spellcasting - Cleric', 'As a conduit for divine power, you can cast cleric-spells.<br/><h1>Cantrips</h1>At 1st level, you know three cantrips of your choice from the cleric-spells list. You learn additional cleric cantrips of your choice at higher levels, as shown in the Cantrips Known column of the Cleric table.<br/><h1>Preparing and Casting Spells</h1>The Cleric table shows how many spell slots you have to cast your spells of 1st level and higher. To cast one of these spells, you must expend a slot of the spell''s level or higher. You regain all expended spell slots when you finish a long rest.<br/><br/>You prepare the list of cleric spells that are available for you to cast, choosing from the cleric spell list. When you do so, choose a number of cleric spells equal to your Wisdom modifier + your cleric level (minimum of one spell). The spells must be of a level for which you have spell slots.<br/><br/>For example, if you are a 3rd-level cleric, you have four 1st-level and two 2nd-level spell slots. With a Wisdom of 16, your list of prepared spells can include six spells of 1st or 2nd level, in any combination. If you prepare the 1st-level spell cure-wounds, you can cast it using a 1st-level or 2nd-level slot. Casting the spell doesn''t remove it from your list of prepared spells.<br/><br/>You can change your list of prepared spells when you finish a long rest. Preparing a new list of cleric spells requires time spent in prayer and meditation: at least 1 minute per spell level for each spell on your list.<br/><h1>Spellcasting Ability</h1>Wisdom is your spellcasting ability for your cleric spells. The power of your spells comes from your devotion to your deity. You use your Wisdom whenever a cleric spell refers to your spellcasting ability. In addition, you use your Wisdom modifier when setting the saving throw DC for a cleric spell you cast and when making an attack roll with one.<br/><br/>- <b>Spell save DC</b> = 8 + your proficiency bonus + your Wisdom modifier<br/><br/>- <b>Spell attack modifier</b> = your proficiency bonus + your Wisdom modifier<br/><br/><h1>Ritual Casting</h1>You can cast a cleric spell as a ritual if that spell has the ritual tag and you have the spell prepared.<br/><h1>Spellcasting Focus</h1>You can use a holy symbol (see adventuring-gear) as a spellcasting focus for your cleric spells.', 0, 'Ability' );
INSERT INTO ability VALUES ( 64, 'Divine Domain', 'Choose one domain related to your deity, such as Life. Each domain is detailed at the end of the class description, and each one provides examples of gods associated with it. Your choice grants you domain spells and other features when you choose it at 1st level. It also grants you additional ways to use Channel Divinity when you gain that feature at 2nd level, and additional benefits at 6th, 8th, and 17th levels.<br/><h1>Domain Spells</h1>Each domain has a list of spells---its domain spells---that you gain at the cleric levels noted in the domain description. Once you gain a domain spell, you always have it prepared, and it doesn''t count against the number of spells you can prepare each day.<br/><br/>If you have a domain spell that doesn''t appear on the cleric spell list, the spell is nonetheless a cleric spell for you.', 0, 'Ability' );
INSERT INTO ability VALUES ( 65, 'Channel Divinity', 'At 2nd level, you gain the ability to channel divine energy directly from your deity, using that energy to fuel magical effects. You start with two such effects: Turn Undead and an effect determined by your domain. Some domains grant you additional effects as you advance in levels, as noted in the domain description.<br/><br/>When you use your Channel Divinity, you choose which effect to create. You must then finish a short or long rest to use your Channel Divinity again.<br/><br/>Some Channel Divinity effects require saving throws. When you use such an effect from this class, the DC equals your cleric spell save DC.<br/><br/>Beginning at 6th level, you can use your Channel Divinity twice between rests, and beginning at 18th level, you can use it three times between rests. When you finish a short or long rest, you regain your expended uses.', 0, 'Ability' );
INSERT INTO ability VALUES ( 66, 'Turn Undead', 'As an action, you present your holy symbol and speak a prayer censuring the undead. Each undead that can see or hear you within 30 feet of you must make a Wisdom saving throw. If the creature fails its saving throw, it is turned for 1 minute or until it takes any damage.<br/><br/>A turned creature must spend its turns trying to move as far away from you as it can, and it can''t willingly move to a space within 30 feet of you. It also can''t take reactions. For its action, it can use only the Dash action or try to escape from an effect that prevents it from moving. If there''s nowhere to move, the creature can use the Dodge action.', 0, 'Ability' );
INSERT INTO ability VALUES ( 67, 'Destroy Undead', 'Starting at 5th level, when an undead fails its saving throw against your Turn Undead feature, the creature is instantly destroyed if its challenge rating is at or below a certain threshold, as shown in the table below.<table><tr><th>Cleric Level</th><th>Destroys Undead of CR</th></tr><tr><td>5th</td><td>1/2 or lower</td></tr><tr><td>8th</td><td>1 or lower</td></tr><tr><td>11th</td><td>2 or lower</td></tr><tr><td>14th</td><td>3 or lower</td></tr><tr><td>17th</td><td>4 or lower</td></tr></table>', 0, 'Ability' );
INSERT INTO ability VALUES ( 68, 'Divine Intervention', 'Beginning at 10th level, you can call on your deity to intervene on your behalf when your need is great.<br/><br/>Imploring your deity''s aid requires you to use your action. Describe the assistance you seek, and roll percentile dice. If you roll a number equal to or lower than your cleric level, your deity intervenes. The GM chooses the nature of the intervention, the effect of any cleric spell or cleric domain spell would be appropriate.<br/><br/>If your deity intervenes, you can''t use this feature again for 7 days. Otherwise, you can use it again after you finish a long rest.<br/><br/>At 20th level, your call for intervention succeeds automatically, no roll required.', 0, 'Ability' );
INSERT INTO ability VALUES ( 69, 'Life Domain', 'The Life domain focuses on the vibrant positive energy---one of the fundamental forces of the universe---that sustains all life. The gods of life promote vitality and health through healing the sick and wounded, caring for those in need, and driving away the forces of death and undeath. Almost any non-evil deity can claim influence over this domain, particularly agricultural deities (such as Chauntea, Arawai, and Demeter), sun gods (such as Lathander, Pelor, and Re-Horakhty), gods of healing or endurance (such as Ilmater, Mishakal, Apollo, and Diancecht), and gods of home and community (such as Hestia, Hathor, and Boldrei).<h1>Life Domain Spells</h1><table><tr><th>Cleric Level</th><th>Spells</th></tr><tr><td>1st</td><td>bless, cure-wounds</td></tr><tr><td>3rd</td><td>lesser-restoration, spiritual-weapon</td></tr><tr><td>5th</td><td>beacon-of-hope, revivify</td></tr><tr><td>7th</td><td>death-ward, guardian-of-faith</td></tr><tr><td>9th</td><td>mass-cure-wounds, raise-dead</td></tr></table>', 0, 'Ability' );
INSERT INTO ability VALUES ( 70, 'Life Domain - Bonus Proficiency', 'When you choose this domain at 1st level, you gain proficiency with heavy armor.', 0, 'Ability' );
INSERT INTO ability VALUES ( 71, 'Life Domain - Disciple of Life', 'Also starting at 1st level, your healing spells are more effective. Whenever you use a spell of 1st level or higher to restore hit points to a creature, the creature regains additional hit points equal to 2 + the spell''s level.', 0, 'Ability' );
INSERT INTO ability VALUES ( 72, 'Life Domain - Preserve Life', 'Starting at 2nd level, you can use your Channel Divinity to heal the badly injured.<br/><br/>As an action, you present your holy symbol and evoke healing energy that can restore a number of hit points equal to five times your cleric level. Choose any creatures within 30 feet of you, and divide those hit points among them. This feature can restore a creature to no more than half of its hit point maximum. You can''t use this feature on an undead or a construct.', 0, 'Ability' );
INSERT INTO ability VALUES ( 73, 'Life Domain - Blessed Healer', 'Beginning at 6th level, the healing spells you cast on others heal you as well. When you cast a spell of 1st level or higher that restores hit points to a creature other than you, you regain hit points equal to 2 + the spell''s level.', 0, 'Ability' );
INSERT INTO ability VALUES ( 74, 'Life Domain - Divine Strike', 'At 8th level, you gain the ability to infuse your weapon strikes with divine energy. Once on each of your turns when you hit a creature with a weapon attack, you can cause the attack to deal an extra 1d8 radiant damage to the target. When you reach 14th level, the extra damage increases to 2d8.', 0, 'Ability' );
INSERT INTO ability VALUES ( 75, 'Life Domain - Supreme Healing', 'Starting at 17th level, when you would normally roll one or more dice to restore hit points with a spell, you instead use the highest number possible for each die. For example, instead of restoring 2d6 hit points to a creature, you restore 12.', 0, 'Ability' );
INSERT INTO ability VALUES ( 76, 'Druidic', 'You know Druidic, the secret language of druids. You can speak the language and use it to leave hidden messages. You and others who know this language automatically spot such a message. Others spot the message''s presence with a successful DC 15 Wisdom<br/><br/>(Perception) check but can''t decipher it without magic.', 0, 'Ability' );
INSERT INTO ability VALUES ( 77, 'Spellcasting - Druid', 'Drawing on the divine essence of nature itself, you can cast spells to shape that essence to your will.<br/><h1>Cantrips</h1>At 1st level, you know two cantrips of your choice from the druid-spells list. You learn additional druid cantrips of your choice at higher levels, as shown in the Cantrips Known column of the Druid table.<br/><h1>Preparing and Casting Spells</h1>The Druid table shows how many spell slots you have to cast your spells of 1st level and higher. To cast one of these druid spells, you must expend a slot of the spell''s level or higher. You regain all expended spell slots when you finish a long rest.<br/><br/>You prepare the list of druid spells that are available for you to cast, choosing from the druid-spells list. When you do so, choose a number of druid spells equal to your Wisdom modifier + your druid level (minimum of one spell). The spells must be of a level for which you have spell slots.<br/><br/>For example, if you are a 3rd-level druid, you have four 1st-level and two 2nd-level spell slots. With a Wisdom of 16, your list of prepared spells can a include six spells of 1st or 2nd level, in any combination. If you prepare the 1st-level spell cure wounds, you can cast it using a 1st-level or 2nd-level slot. Casting the spell doesn''t remove it from your list of prepared spells.<br/><br/>You can also change your list of prepared spells when you finish a long rest. Preparing a new list of druid spells requires time spent in prayer and meditation: at least 1 minute per spell level for each spell on your list.<br/><h1>Spellcasting Focus</h1>You can use a druidic focus (see adventuring-gear) as a spellcasting focus for your druid spells.<br/><h1>Spellcasting Ability</h1>Wisdom is your spellcasting ability for your druid spells, since your magic draws upon your devotion and attunement to nature. You use your Wisdom whenever a spell refers to your spellcasting ability. In addition, you use your Wisdom modifier when setting the saving throw DC for a druid spell you cast and when making an attack roll with one.<br/><br/>- <b>Spell save DC</b> = 8 + your proficiency bonus + your Wisdom modifier<br/><br/>- <b>Spell attack modifier</b> = your proficiency bonus + your Wisdom modifier<br/><br/><h1>Ritual Casting</h1>You can cast a druid spell as a ritual if that spell has the ritual tag and you have the spell prepared.', 0, 'Ability' );
INSERT INTO ability VALUES ( 78, 'Wild Shape', 'Starting at 2nd level, you can use your action to magically assume the shape of a beast that you have seen before. You can use this feature twice. You regain expended uses when you finish a short or long rest.<br/><br/>Your druid level determines the beasts you can transform into, as shown in the Beast Shapes table. At 2nd level, for example, you can transform into any beast that has a challenge rating of 1/4 or lower that doesn''t have a flying or swimming speed.<br/><h1>Beast Shapes</h1><table><tr><th>Level</th><th>CR</th><th>Limitations</th><th>Example</th></tr><tr><td>2nd</td><td>1/4</td><td>No flying or swimming speed</td><td>Wolf</td></tr><tr><td>4th</td><td>1/2</td><td>No flying speed</td><td>Crocodile</td></tr><tr><td>8th</td><td>1</td><td>---</td><td>Giant eagle</td></tr></table><br/><br/>You can stay in a beast shape for a number of hours equal to half your druid level (rounded down). You then revert to your normal form unless you expend another use of this feature. You can revert to your normal form earlier by using a bonus action on your turn. You automatically revert if you fall unconscious, drop to 0 hit points, or die.<br/><br/>While you are transformed, the following rules apply:<br/><br/>- Your game statistics are replaced by the statistics of the beast, but you retain your alignment, personality, and Intelligence, Wisdom, and Charisma scores. You also retain all of your skill and saving throw proficiencies, in addition to gaining those of the creature. If the creature has the same proficiency as you and the bonus in its stat block is higher than yours, use the creature''s bonus instead of yours. If the creature has any legendary or lair actions, you can''t use them.<br/>- When you transform, you assume the beast''s hit points and Hit Dice. When you revert to your normal form, you return to the number of hit points you had before you transformed. However, if you revert as a result of dropping to 0 hit points, any excess damage carries over to your normal form. For example, if you take 10 damage in animal form and have only 1 hit point left, you revert and take 9 damage. As long as the excess damage doesn''t reduce your normal form to 0 hit points, you aren''t knocked unconscious.<br/>- You can''t cast spells, and your ability to speak or take any action that requires hands is limited to the capabilities of your beast form. Transforming doesn''t break your concentration on a spell you''ve already cast, however, or prevent you from taking actions that are part of a spell, such as call lightning, that you''ve already cast.<br/>- You retain the benefit of any features from your class, race, or other source and can use them if the new form is physically capable of doing so. However, you can''t use any of your special senses, such as darkvision, unless your new form also has that sense.<br/>- You choose whether your equipment falls to the ground in your space, merges into your new form, or is worn by it. Worn equipment functions as normal, but the GM decides whether it is practical for the new form to wear a piece of equipment, based on the creature''s shape and size. Your equipment doesn''t change size or shape to match the new form, and any equipment that the new form can''t wear must either fall to the ground or merge with it. Equipment that merges with the form has no effect until you leave the form.', 0, 'Ability' );
INSERT INTO ability VALUES ( 79, 'Druid Circle', 'At 2nd level, you choose to identify with a circle of druids, such as the Circle of the Land. Your choice grants you features at 2nd level and again at 6th, 10th, and 14th level.', 0, 'Ability' );
INSERT INTO ability VALUES ( 80, 'Timeless Body', 'Starting at 18th level, the primal magic that you wield causes you to age more slowly. For every 10 years that pass, your body ages only 1 year.', 0, 'Ability' );
INSERT INTO ability VALUES ( 81, 'Beast Spells', 'Beginning at 18th level, you can cast many of your druid spells in any shape you assume using Wild Shape. You can perform the somatic and verbal<br/><br/>components of a druid spell while in a beast shape, but you aren''t able to provide material components.', 0, 'Ability' );
INSERT INTO ability VALUES ( 82, 'Archdruid', 'At 20th level, you can use your Wild Shape an unlimited number of times.<br/><br/>Additionally, you can ignore the verbal and somatic components of your druid spells, as well as any material components that lack a cost and aren''t consumed by a spell. You gain this benefit in both your normal shape and your beast shape from Wild Shape.', 0, 'Ability' );
INSERT INTO ability VALUES ( 83, 'Circle of Land', 'The Circle of the Land is made up of mystics and sages who safeguard ancient knowledge and rites through a vast oral tradition. These druids meet within sacred circles of trees or standing stones to whisper primal secrets in Druidic. The circle''s wisest members preside as the chief priests of communities that hold to the Old Faith and serve as advisors to the rulers of those folk. As a member of this circle, your magic is influenced by the land where you were initiated into the circle''s mysterious rites.', 0, 'Ability' );
INSERT INTO ability VALUES ( 84, 'Circle of Land - Bonus Cantrip', 'When you choose this circle at 2nd level, you learn one additional druid cantrip of your choice.', 0, 'Ability' );
INSERT INTO ability VALUES ( 85, 'Circle of Land - Natural Recovery', 'Starting at 2nd level, you can regain some of your magical energy by sitting in meditation and communing with nature. During a short rest, you choose expended spell slots to recover. The spell slots can have a combined level that is equal to or less than half your druid level (rounded up), and none of the slots can be 6th level or higher. You can''t use this feature again until you finish a long rest.<br/><br/>For example, when you are a 4th-level druid, you can recover up to two levels worth of spell slots. You can recover either a 2nd-level slot or two 1st-level slots.', 0, 'Ability' );
INSERT INTO ability VALUES ( 86, 'Circle of Land - Land''s Stride', 'Starting at 6th level, moving through nonmagical difficult terrain costs you no extra movement. You can also pass through nonmagical plants without being slowed by them and without taking damage from them if they have thorns, spines, or a similar hazard.<br/><br/>In addition, you have advantage on saving throws against plants that are magically created or manipulated to impede movement, such those created by the entangle spell.', 0, 'Ability' );
INSERT INTO ability VALUES ( 87, 'Circle of Land - Nature''s Ward', 'When you reach 10th level, you can''t be charmed or frightened by elementals or fey, and you are immune to poison and disease.', 0, 'Ability' );
INSERT INTO ability VALUES ( 88, 'Circle of Land - Nature''s Sanctuary', 'When you reach 14th level, creatures of the natural world sense your connection to nature and become hesitant to attack you. When a beast or plant creature attacks you, that creature must make a Wisdom saving throw against your druid spell save DC. On a failed save, the creature must choose a different target, or the attack automatically misses. On a successful save, the creature is immune to this effect for 24 hours. The creature is aware of this effect before it makes its attack against you.', 0, 'Ability' );
INSERT INTO ability VALUES ( 89, 'Circle of Land - Spells Arctic', 'Your mystical connection to the land infuses you with the ability to cast certain spells. At 3rd, 5th, 7th, and 9th level you gain access to circle spells connected to the land where you became a druid.<br/><br/>Once you gain access to a circle spell, you always have it prepared, and it doesn''t count against the number of spells you can prepare each day. If you gain access to a spell that doesn''t appear on the druid spell list, the spell is nonetheless a druid spell for you.<br/><table><tr><th>Druid Level</th><th>Circle Spells<th></tr><tr><td>3rd</td><td>hold-person, spike-growth</td></tr><tr><td>5th</td><td>sleet-storm, slow</td></tr><tr><td>7th</td><td>freedom-of-movement, ice-storm</td></tr><tr><td>9th</td><td>commune-with-nature, cone-of-cold</td></tr></table>', 0, 'Ability' );
INSERT INTO ability VALUES ( 90, 'Circle of Land - Spells Coast', 'Your mystical connection to the land infuses you with the ability to cast certain spells. At 3rd, 5th, 7th, and 9th level you gain access to circle spells connected to the land where you became a druid.<br/><br/>Once you gain access to a circle spell, you always have it prepared, and it doesn''t count against the number of spells you can prepare each day. If you gain access to a spell that doesn''t appear on the druid spell list, the spell is nonetheless a druid spell for you.<br/><table><tr><th>Druid Level</th><th>Circle Spells<th></tr><tr><td>3rd</td><td>mirror-image, misty-step</td></tr><tr><td>5th</td><td>water-breathing, water-walk</td></tr><tr><td>7th</td><td>control-water, freedom-of-movement</td></tr><tr><td>9th</td><td>conjure-elemental, scrying</td></tr></table>', 0, 'Ability' );
INSERT INTO ability VALUES ( 91, 'Circle of Land - Spells Desert', 'Your mystical connection to the land infuses you with the ability to cast certain spells. At 3rd, 5th, 7th, and 9th level you gain access to circle spells connected to the land where you became a druid.<br/><br/>Once you gain access to a circle spell, you always have it prepared, and it doesn''t count against the number of spells you can prepare each day. If you gain access to a spell that doesn''t appear on the druid spell list, the spell is nonetheless a druid spell for you.<br/><table><tr><th>Druid Level</th><th>Circle Spells<th></tr><tr><td>3rd</td><td>blur, silence</td></tr><tr><td>5th</td><td>create-food-and-water, protection-from-energy</td></tr><tr><td>7th</td><td>blight, hallucinatory-terrain</td></tr><tr><td>9th</td><td>insect-plague, wall-of-stone</td></tr></table>', 0, 'Ability' );
INSERT INTO ability VALUES ( 92, 'Circle of Land - Spells Forest', 'Your mystical connection to the land infuses you with the ability to cast certain spells. At 3rd, 5th, 7th, and 9th level you gain access to circle spells connected to the land where you became a druid.<br/><br/>Once you gain access to a circle spell, you always have it prepared, and it doesn''t count against the number of spells you can prepare each day. If you gain access to a spell that doesn''t appear on the druid spell list, the spell is nonetheless a druid spell for you.<br/><table><tr><th>Druid Level</th><th>Circle Spells<th></tr><tr><td>3rd</td><td>barkskin, spider-climb</td></tr><tr><td>5th</td><td>call-lightning, plant-growth</td></tr><tr><td>7th</td><td>divination, freedom-of-movement</td></tr><tr><td>9th</td><td>commune-with-nature, tree-stride</td></tr></table>', 0, 'Ability' );
INSERT INTO ability VALUES ( 93, 'Circle of Land - Spells Grassland', 'Your mystical connection to the land infuses you with the ability to cast certain spells. At 3rd, 5th, 7th, and 9th level you gain access to circle spells connected to the land where you became a druid.<br/><br/>Once you gain access to a circle spell, you always have it prepared, and it doesn''t count against the number of spells you can prepare each day. If you gain access to a spell that doesn''t appear on the druid spell list, the spell is nonetheless a druid spell for you.<br/><table><tr><th>Druid Level</th><th>Circle Spells<th></tr><tr><td>3rd</td><td>invisibility, pass-without-trace</td></tr><tr><td>5th</td><td>daylight, haste</td></tr><tr><td>7th</td><td>divination, freedom-of-movement</td></tr><tr><td>9th</td><td>dream, insect-plague</td></tr></table>', 0, 'Ability' );
INSERT INTO ability VALUES ( 94, 'Circle of Land - Spells Mountain', 'Your mystical connection to the land infuses you with the ability to cast certain spells. At 3rd, 5th, 7th, and 9th level you gain access to circle spells connected to the land where you became a druid.<br/><br/>Once you gain access to a circle spell, you always have it prepared, and it doesn''t count against the number of spells you can prepare each day. If you gain access to a spell that doesn''t appear on the druid spell list, the spell is nonetheless a druid spell for you.<br/><table><tr><th>Druid Level</th><th>Circle Spells<th></tr><tr><td>3rd</td><td>spider-climb, spike-growth</td></tr><tr><td>5th</td><td>lightning-bolt, meld-into-stone</td></tr><tr><td>7th</td><td>stone-shape, stoneskin</td></tr><tr><td>9th</td><td>passwall, wall-of-stone</td></tr></table>', 0, 'Ability' );
INSERT INTO ability VALUES ( 95, 'Circle of Land - Spells Swamp', 'Your mystical connection to the land infuses you with the ability to cast certain spells. At 3rd, 5th, 7th, and 9th level you gain access to circle spells connected to the land where you became a druid.<br/><br/>Once you gain access to a circle spell, you always have it prepared, and it doesn''t count against the number of spells you can prepare each day. If you gain access to a spell that doesn''t appear on the druid spell list, the spell is nonetheless a druid spell for you.<br/><table><tr><th>Druid Level</th><th>Circle Spells<th></tr><tr><td>3rd</td><td>acid-arrow, darkness</td></tr><tr><td>5th</td><td>water-walk, stinking-cloud</td></tr><tr><td>7th</td><td>freedom-of-movement, locate-creature</td></tr><tr><td>9th</td><td>insect-plague, scrying</td></tr></table>', 0, 'Ability' );
INSERT INTO ability VALUES ( 96, 'Fighting Style - Archery', 'You gain a +2 bonus to attack rolls you make with ranged weapons.', 0, 'Ability' );
INSERT INTO ability VALUES ( 97, 'Fighting Style - Defense', 'While you are wearing armor, you gain a +1 bonus to AC.', 0, 'Ability' );
INSERT INTO ability VALUES ( 98, 'Fighting Style - Dueling', 'When you are wielding a melee weapon in one hand and no other weapons, you gain a +2 bonus to damage rolls with that weapon.', 0, 'Ability' );
INSERT INTO ability VALUES ( 99, 'Fighting Style - Great Weapon Fighting', 'When you roll a 1 or 2 on a damage die for an attack you make with a melee weapon that you are wielding with two hands, you can reroll the die and must use the new roll, even if the new roll is a 1 or a 2. The weapon must have the two-handed or versatile property for you to gain this benefit.', 0, 'Ability' );
INSERT INTO ability VALUES ( 100, 'Fighting Style - Protection', 'When a creature you can see attacks a target other than you that is within 5 feet of you, you can use your reaction to impose disadvantage on the attack roll. You must be wielding a shield.', 0, 'Ability' );
INSERT INTO ability VALUES ( 101, 'Fighting Style - Two-Weapon Fighting', 'When you engage in two-weapon fighting, you can add your ability modifier to the damage of the second attack.', 0, 'Ability' );
INSERT INTO ability VALUES ( 102, 'Second Wind', 'You have a limited well of stamina that you can draw on to protect yourself from harm. On your turn, you can use a bonus action to regain hit points equal to 1d10 + your fighter level. Once you use this feature, you must finish a short or long rest before you can use it again.', 0, 'Ability' );
INSERT INTO ability VALUES ( 103, 'Action Surge', 'Starting at 2nd level, you can push yourself beyond your normal limits for a moment. On your turn, you can take one additional action on top of your regular action and a possible bonus action.<br/><br/>Once you use this feature, you must finish a short or long rest before you can use it again. Starting at 17th level, you can use it twice before a rest, but only once on the same turn.', 0, 'Ability' );
INSERT INTO ability VALUES ( 104, 'Martial Archetype', 'At 3rd level, you choose an archetype that you strive to emulate in your combat styles and techniques, such as Champion. The archetype you choose grants you features at 3rd level and again at 7th, 10th, 15th, and 18th level.', 0, 'Ability' );
INSERT INTO ability VALUES ( 105, 'Extra Attack', 'Beginning at 5th level, you can attack twice, instead of once, whenever you take the Attack action on your turn.<br/><br/>The number of attacks increases to three when you reach 11th level in this class and to four when you reach 20th level in this class.', 0, 'Ability' );
INSERT INTO ability VALUES ( 106, 'Indomitable', 'Beginning at 9th level, you can reroll a saving throw that you fail. If you do so, you must use the new roll, and you can''t use this feature again until you finish a long rest.<br/><br/>You can use this feature twice between long rests starting at 13th level and three times between long rests starting at 17th level.', 0, 'Ability' );
INSERT INTO ability VALUES ( 107, 'Champion', 'The archetypal Champion focuses on the development of raw physical power honed to deadly perfection. Those who model themselves on this archetype combine rigorous training with physical excellence to deal devastating blows.', 0, 'Ability' );
INSERT INTO ability VALUES ( 108, 'Champion - Improved Critical', 'Beginning when you choose this archetype at 3rd level, your weapon attacks score a critical hit on a roll of 19 or 20.', 0, 'Ability' );
INSERT INTO ability VALUES ( 109, 'Champion - Remarkable Athlete', 'Starting at 7th level, you can add half your proficiency bonus (round up) to any Strength, Dexterity, or Constitution check you make that doesn''t already use your proficiency bonus.<br/><br/>In addition, when you make a running long jump, the distance you can cover increases by a number of feet equal to your Strength modifier.', 0, 'Ability' );
INSERT INTO ability VALUES ( 110, 'Champion - Additional Fighting Style', 'At 10th level, you can choose a second option from the Fighting Style class feature.', 0, 'Ability' );
INSERT INTO ability VALUES ( 111, 'Champion - Superior Critical', 'Starting at 15th level, your weapon attacks score a critical hit on a roll of 18-20.', 0, 'Ability' );
INSERT INTO ability VALUES ( 112, 'Champion - Survivor', 'At 18th level, you attain the pinnacle of resilience in battle. At the start of each of your turns, you regain hit points equal to 5 + your Constitution modifier if you have no more than half of your hit points left. You don''t gain this benefit if you have 0 hit points.', 0, 'Ability' );
INSERT INTO ability VALUES ( 113, 'Unarmored Defense', 'Beginning at 1st level, while you are wearing no armor and not wielding a shield, your AC equals 10 + your Dexterity modifier + your Wisdom modifier.', 0, 'Ability' );
INSERT INTO ability VALUES ( 114, 'Martial Arts', 'At 1st level, your practice of martial arts gives you mastery of combat styles that use unarmed strikes and monk weapons, which are shortswords and any simple melee weapons that don''t have the two-handed or heavy property.<br/><br/>You gain the following benefits while you are unarmed or wielding only monk weapons and you aren''t wearing armor or wielding a shield:<br/><br/>- You can use Dexterity instead of Strength for the attack and damage rolls of your unarmed strikes and monk weapons.<br/>- You can roll a d4 in place of the normal damage of your unarmed strike or monk weapon. This die changes as you gain monk levels, ashown in the Martial Arts column of the Monk table.<br/>- When you use the Attack action with an unarmed strike or a monk weapon on your turn, you can make one unarmed strike as a bonus action. For example, if you take the Attack action and attack with a quarterstaff, you can also make an unarmed strike as a bonus action, assuming you haven''t already taken a bonus action this turn.<br/><br/>Certain monasteries use specialized forms of the monk weapons. For example, you might use a club that is two lengths of wood connected by a short chain (called a nunchaku) or a sickle with a shorter, straighter blade (called a kama). Whatever name you use for a monk weapon, you can use the game statistics provided for the weapon.', 0, 'Ability' );
INSERT INTO ability VALUES ( 115, 'Ki', 'Starting at 2nd level, your training allows you to harness the mystic energy of ki. Your access to this energy is represented by a number of ki points. Your monk level determines the number of points you have, as shown in the Ki Points column of the Monk table.<br/><br/>You can spend these points to fuel various ki features. You start knowing three such features: Flurry of Blows, Patient Defense, and Step of the Wind. You learn more ki features as you gain levels in this class.<br/><br/>When you spend a ki point, it is unavailable until you finish a short or long rest, at the end of which you draw all of your expended ki back into yourself. You must spend at least 30 minutes of the rest meditating to regain your ki points.<br/><br/>Some of your ki features require your target to make a saving throw to resist the feature''s effects. The saving throw DC is calculated as follows:<br/><br/><b>Ki save DC</b> = 8 + your proficiency bonus + your Wisdom modifier', 0, 'Ability' );
INSERT INTO ability VALUES ( 116, 'Unarmored Movement', 'Starting at 2nd level, your speed increases by 10 feet while you are not wearing armor or wielding a shield. This bonus increases when you reach certain monk levels, as shown in the Monk table.<br/><br/>At 9th level, you gain the ability to move along vertical surfaces and across liquids on your turn without falling during the move.', 0, 'Ability' );
INSERT INTO ability VALUES ( 117, 'Monastic Tradition', 'When you reach 3rd level, you commit yourself to a monastic tradition, such as the Way of the Open Hand. Your tradition grants you features at 3rd level and again at 6th, 11th, and 17th level.<br/><br/>Three traditions of monastic pursuit are common in the monasteries scattered across the multiverse. Most monasteries practice one tradition exclusively, but a few honor the three traditions and instruct each monk according to his or her aptitude and interest. All three traditions rely on the same basic techniques, diverging as the student grows more adept. Thus, a monk need choose a tradition only upon reaching 3rd level.', 0, 'Ability' );
INSERT INTO ability VALUES ( 118, 'Ki - Flurry of Blows', 'Immediately after you take the Attack action on your turn, you can spend 1 ki point to make two unarmed strikes as a bonus action.', 0, 'Ability' );
INSERT INTO ability VALUES ( 119, 'Ki - Patient Defense', 'You can spend 1 ki point to take the Dodge action as a bonus action on your turn.', 0, 'Ability' );
INSERT INTO ability VALUES ( 120, 'Ki - Step of the Wind', 'You can spend 1 ki point to take the Disengage or Dash action as a bonus action on your turn, and your jump distance is doubled for the turn.', 0, 'Ability' );
INSERT INTO ability VALUES ( 121, 'Ki - Deflect Missiles', 'Starting at 3rd level, you can use your reaction to deflect or catch the missile when you are hit by a ranged weapon attack. When you do so, the damage you take from the attack is reduced by 1d10 + your Dexterity modifier + your monk level.<br/><br/>If you reduce the damage to 0, you can catch the missile if it is small enough for you to hold in one hand and you have at least one hand free. If you catch a missile in this way, you can spend 1 ki point to make a ranged attack with the weapon or piece of ammunition you just caught, as part of the same reaction. You make this attack with proficiency, regardless of your weapon proficiencies, and the missile counts as a monk weapon for the attack, which has a normal range of 20 feet and a long range of 60 feet.', 0, 'Ability' );
INSERT INTO ability VALUES ( 122, 'Slow Fall', 'Beginning at 4th level, you can use your reaction when you fall to reduce any falling damage you take by an amount equal to five times your monk level.', 0, 'Ability' );
INSERT INTO ability VALUES ( 123, 'Extra Attack', 'Beginning at 5th level, you can attack twice, instead of once, whenever you take the Attack action on your turn.', 0, 'Ability' );
INSERT INTO ability VALUES ( 124, 'Stunning Strike', 'Starting at 5th level, you can interfere with the flow of ki in an opponent''s body. When you hit another creature with a melee weapon attack, you can spend 1 ki point to attempt a stunning strike. The target must succeed on a Constitution saving throw or be stunned until the end of your next turn.', 0, 'Ability' );
INSERT INTO ability VALUES ( 125, 'Ki-Empowered Strikes', 'Starting at 6th level, your unarmed strikes count as magical for the purpose of overcoming resistance and immunity to nonmagical attacks and damage.', 0, 'Ability' );
INSERT INTO ability VALUES ( 126, 'Evasion', 'At 7th level, your instinctive agility lets you dodge out of the way of certain area effects, such as a blue dragon''s lightning breath or a fireball spell. When you are subjected to an effect that allows you to make a Dexterity saving throw to take only half damage, you instead take no damage if you succeed on the saving throw, and only half damage if you fail.', 0, 'Ability' );
INSERT INTO ability VALUES ( 127, 'Stillness of Mind', 'Starting at 7th level, you can use your action to end one effect on yourself that is causing you to be charmed or frightened.', 0, 'Ability' );
INSERT INTO ability VALUES ( 128, 'Purity of Body', 'At 10th level, your mastery of the ki flowing through you makes you immune to disease and poison.', 0, 'Ability' );
INSERT INTO ability VALUES ( 129, 'Tongue of the Sun and Moon', 'Starting at 13th level, you learn to touch the ki of other minds so that you understand all spoken languages. Moreover, any creature that can understand a language can understand what you say.', 0, 'Ability' );
INSERT INTO ability VALUES ( 130, 'Diamond Soul', 'Beginning at 14th level, your mastery of ki grants you proficiency in all saving throws.<br/><br/>Additionally, whenever you make a saving throw and fail, you can spend 1 ki point to reroll it and take the second result.', 0, 'Ability' );
INSERT INTO ability VALUES ( 131, 'Timeless Body', 'At 15th level, your ki sustains you so that you suffer none of the frailty of old age, and you can''t be aged magically. You can still die of old age, however. In addition, you no longer need food or water.', 0, 'Ability' );
INSERT INTO ability VALUES ( 132, 'Empty Body', 'Beginning at 18th level, you can use your action to spend 4 ki points to become invisible for 1 minute. During that time, you also have resistance to all damage but force damage.<br/><br/>Additionally, you can spend 8 ki points to cast the astral projection spell, without needing material components. When you do so, you can''t take any other creatures with you', 0, 'Ability' );
INSERT INTO ability VALUES ( 133, 'Perfect Self', 'At 20th level, when you roll for initiative and have no ki points remaining, you regain 4 ki points.', 0, 'Ability' );
INSERT INTO ability VALUES ( 134, 'Way of the Open Hand', 'Monks of the Way of the Open Hand are the ultimate masters of martial arts combat, whether armed or unarmed. They learn techniques to push and trip their opponents, manipulate ki to heal damage to their bodies, and practice advanced meditation that can protect them from harm.', 0, 'Ability' );
INSERT INTO ability VALUES ( 135, 'Way of the Open Hand - Open Hand Technique', 'Starting when you choose this tradition at 3rd level, you can manipulate your enemy''s ki when you harness your own. Whenever you hit a creature with one of the attacks granted by your Flurry of Blows, you can impose one of the following effects on that target:<br/><br/>- It must succeed on a Dexterity saving throw or be knocked prone.<br/>- It must make a Strength saving throw. If it fails, you can push it up to 15 feet away from you.<br/>- It can''t take reactions until the end of your next turn.', 0, 'Ability' );
INSERT INTO ability VALUES ( 136, 'Way of the Open Hand - Wholeness of Body', 'At 6th level, you gain the ability to heal yourself. As an action, you can regain hit points equal to three times your monk level. You must finish a long rest before you can use this feature again.', 0, 'Ability' );
INSERT INTO ability VALUES ( 137, 'Way of the Open Hand - Tranquility', 'Beginning at 11th level, you can enter a special meditation that surrounds you with an aura of peace. At the end of a long rest, you gain the effect of a sanctuary spell that lasts until the start of your next long rest (the spell can end early as normal). The saving throw DC for the spell equals 8 + your Wisdom modifier + your proficiency bonus.', 0, 'Ability' );
INSERT INTO ability VALUES ( 138, 'Way of the Open Hand - Quivering Palm', 'At 17th level, you gain the ability to set up lethal vibrations in someone''s body. When you hit a creature with an unarmed strike, you can spend 3 ki points to start these imperceptible vibrations, which last for a number of days equal to your monk level. The vibrations are harmless unless you use your action to end them. To do so, you and the target must be on the same plane of existence. When you use this action, the creature must make a Constitution saving throw. If it fails, it is reduced to 0 hit points. If it succeeds, it takes 10d10 necrotic damage.<br/><br/>You can have only one creature under the effect of this feature at a time. You can choose to end the vibrations harmlessly without using an action.', 0, 'Ability' );
INSERT INTO ability VALUES ( 139, 'Divine Sense', 'The presence of strong evil registers on your senses like a noxious odor, and powerful good rings like heavenly music in your ears. As an action, you can open your awareness to detect such forces. Until the end of your next turn, you know the location of any celestial, fiend, or undead within 60 feet of you that is not behind total cover. You know the type (celestial, fiend, or undead) of any being whose presence you sense, but not its identity (the vampire Count Strahd von Zarovich, for instance). Within the same radius, you also detect the presence of any place or object that has been consecrated or desecrated, as with the hallow spell.<br/><br/>You can use this feature a number of times equal to 1 + your Charisma modifier. When you finish a long rest, you regain all expended uses.', 0, 'Ability' );
INSERT INTO ability VALUES ( 140, 'Lay on Hands', 'Your blessed touch can heal wounds. You have a pool of healing power that replenishes when you take a long rest. With that pool, you can restore a total number of hit points equal to your paladin level Ã— 5.<br/><br/>As an action, you can touch a creature and draw power from the pool to restore a number of hit points to that creature, up to the maximum amount remaining in your pool.<br/><br/>Alternatively, you can expend 5 hit points from your pool of healing to cure the target of one disease or neutralize one poison affecting it. You can cure multiple diseases and neutralize multiple poisons with a single use of Lay on Hands, expending hit points separately for each one.<br/><br/>This feature has no effect on undead and constructs.', 0, 'Ability' );
INSERT INTO ability VALUES ( 141, 'Spellcasting - Paladin', 'By 2nd level, you have learned to draw on divine magic through meditation and prayer to cast spells as a cleric does.<br/><h1>Preparing and Casting Spells</h1>The Paladin table shows how many spell slots you have to cast your spells. To cast one of your paladin spells of 1st level or higher, you must expend a slot of the spell''s level or higher. You regain all expended spell slots when you finish a long rest.<br/><br/>You prepare the list of paladin spells that are available for you to cast, choosing from the paladin-spells list. When you do so, choose a number of paladin spells equal to your Charisma modifier + half your paladin level, rounded down (minimum of one spell). The spells must be of a level for which you have spell slots.<br/><br/>For example, if you are a 5th-level paladin, you have four 1st-level and two 2nd-level spell slots. With a Charisma of 14, your list of prepared spells can include four spells of 1st or 2nd level, in any combination. If you prepare the 1st-level spell cure wounds, you can cast it using a 1st-level or a 2nd-level slot. Casting the spell doesn''t remove it from your list of prepared spells.<br/><br/>You can change your list of prepared spells when you finish a long rest. Preparing a new list of paladin spells requires time spent in prayer and meditation: at least 1 minute per spell level for each spell on your list.<br/><h1>Spellcasting Ability</h1>Charisma is your spellcasting ability for your paladin spells, since their power derives from the strength of your convictions. You use your Charisma whenever a spell refers to your spellcasting ability. In addition, you use your Charisma modifier when setting the saving throw DC for a paladin spell you cast and when making an attack roll with one.<br/><br/>- <b>Spell save DC</b> = 8 + your proficiency bonus + your Charisma modifier<br/><br/>- <b>Spell attack modifier</b> = your proficiency bonus + your Charisma modifier<br/><br/><h1>Spellcasting Focus</h1>You can use a holy symbol as a spellcasting focus for your paladin spells.', 0, 'Ability' );
INSERT INTO ability VALUES ( 142, 'Divine Smite', 'Starting at 2nd level, when you hit a creature with a melee weapon attack, you can expend one spell slot to deal radiant damage to the target, in addition to the weapon''s damage. The extra damage is 2d8 for a 1st-level spell slot, plus 1d8 for each spell level higher than 1st, to a maximum of 5d8. The damage increases by 1d8 if the target is an undead or a fiend.', 0, 'Ability' );
INSERT INTO ability VALUES ( 143, 'Divine Health', 'By 3rd level, the divine magic flowing through you makes you immune to disease.', 0, 'Ability' );
INSERT INTO ability VALUES ( 144, 'Sacred Oath', 'When you reach 3rd level, you swear the oath that binds you as a paladin forever. Up to this time you have been in a preparatory stage, committed to the path but not yet sworn to it. Now you choose an oath, such as the Oath of Devotion.<br/><br/>Your choice grants you features at 3rd level and again at 7th, 15th, and 20th level. Those features include oath spells and the Channel Divinity feature.<br/><h1>Oath Spells</h1>Each oath has a list of associated spells. You gain access to these spells at the levels specified in the oath description. Once you gain access to an oath spell, you always have it prepared. Oath spells don''t count against the number of spells you can prepare each day.<br/><br/>If you gain an oath spell that doesn''t appear on the paladin spell list, the spell is nonetheless a paladin spell for you.<br/><h1>Channel Divinity</h1>Your oath allows you to channel divine energy to fuel magical effects. Each Channel Divinity option provided by your oath explains how to use it.<br/><br/>When you use your Channel Divinity, you choose which option to use. You must then finish a short or long rest to use your Channel Divinity again.<br/><br/>Some Channel Divinity effects require saving throws. When you use such an effect from this class, the DC equals your paladin spell save DC.', 0, 'Ability' );
INSERT INTO ability VALUES ( 145, 'Aura of Protection', 'Starting at 6th level, whenever you or a friendly creature within 10 feet of you must make a saving throw, the creature gains a bonus to the saving throw equal to your Charisma modifier (with a minimum bonus of +1). You must be conscious to grant this bonus.<br/><br/>At 18th level, the range of this aura increases to 30 feet.', 0, 'Ability' );
INSERT INTO ability VALUES ( 146, 'Aura of Courage', 'Starting at 10th level, you and friendly creatures within 10 feet of you can''t be frightened while you are conscious.<br/><br/>At 18th level, the range of this aura increases to 30 feet.', 0, 'Ability' );
INSERT INTO ability VALUES ( 147, 'Improved Divine Smite', 'By 11th level, you are so suffused with righteous might that all your melee weapon strikes carry divine power with them. Whenever you hit a creature with a melee weapon, the creature takes an extra 1d8 radiant damage. If you also use your Divine Smite with an attack, you add this damage to the extra damage of your Divine Smite.', 0, 'Ability' );
INSERT INTO ability VALUES ( 148, 'Cleansing Touch', 'Beginning at 14th level, you can use your action to end one spell on yourself or on one willing creature that you touch.<br/><br/>You can use this feature a number of times equal to your Charisma modifier (a minimum of once). You regain expended uses when you finish a long rest.', 0, 'Ability' );
INSERT INTO ability VALUES ( 149, 'Oath of Devotion', 'The Oath of Devotion binds a paladin to the loftiest ideals of justice, virtue, and order. Sometimes called cavaliers, white knights, or holy warriors, these paladins meet the ideal of the knight in shining armor, acting with honor in pursuit of justice and the greater good. They hold themselves to the highest standards of conduct, and some, for better or worse, hold the rest of the world to the same standards. Many who swear this oath are devoted to gods of law and good and use their gods'' tenets as the measure of their devotion. They hold angels---the perfect servants of good---as their ideals, and incorporate images of angelic wings into their helmets or coats of arms.<br/><h1>Tenets of Devotion</h1>Though the exact words and strictures of the Oath of Devotion vary, paladins of this oath share these tenets.<br/><br/><b>Honesty.</b> Don''t lie or cheat. Let your word be your promise.<br/><br/><b>Courage.</b> Never fear to act, though caution is wise.<br/><br/><b>Compassion.</b> Aid others, protect the weak, and punish those who threaten them. Show mercy to your foes, but temper it with wisdom.<br/><br/><b>Honor.</b> Treat others with fairness, and let your honorable deeds be an example to them. Do as much good as possible while causing the least amount of harm.<br/><br/><b>Duty.</b> Be responsible for your actions and their consequences, protect those entrusted to your care, and obey those who have just authority over you.<br/><h1>Oath Spells</h1>You gain oath spells at the paladin levels listed.<br/><h1>Oath of Devotion Spells</h1>Paladin Level Spells', 0, 'Ability' );
INSERT INTO ability VALUES ( 150, 'Oath of Devotion - Sacred Weapon', 'As an action, you can imbue one weapon that you are holding with positive energy, using your Channel Divinity. For 1 minute, you add your Charisma modifier to attack rolls made with<br/><br/>that weapon (with a minimum bonus of +1). The weapon also emits bright light in a 20-foot radius and dim light 20 feet beyond that. If the weapon is not already magical, it becomes magical for the duration.<br/><br/>You can end this effect on your turn as part of any other action. If you are no longer holding or carrying this weapon, or if you fall unconscious, this effect ends.', 0, 'Ability' );
INSERT INTO ability VALUES ( 151, 'Oath of Devotion - Turn the Unholy', 'As an action, you present your holy symbol and speak a prayer censuring fiends and undead, using your Channel Divinity. Each fiend or undead that can see or hear you within 30 feet of you must make a Wisdom saving throw. If the creature fails its saving throw, it is turned for 1 minute or until it takes damage.<br/><br/>A turned creature must spend its turns trying to move as far away from you as it can, and it can''t willingly move to a space within 30 feet of you. It also can''t take reactions. For its action, it can use only the Dash action or try to escape from an effect that prevents it from moving. If there''s nowhere to move, the creature can use the Dodge action.', 0, 'Ability' );
INSERT INTO ability VALUES ( 152, 'Oath of Devotion - Aura of Devotion', 'Starting at 7th level, you and friendly creatures within 10 feet of you can''t be charmed while you are conscious.<br/><br/>At 18th level, the range of this aura increases to 30 feet.', 0, 'Ability' );
INSERT INTO ability VALUES ( 153, 'Oath of Devotion - Purity of Spirit', 'Beginning at 15th level, you are always under the effects of a protection-from-evil-and-good spell.', 0, 'Ability' );
INSERT INTO ability VALUES ( 154, 'Oath of Devotion - Holy Nimbus', 'At 20th level, as an action, you can emanate an aura of sunlight. For 1 minute, bright light shines from you in a 30-foot radius, and dim light shines 30 feet beyond that.<br/><br/>Whenever an enemy creature starts its turn in the bright light, the creature takes 10 radiant damage.<br/><br/>In addition, for the duration, you have advantage on saving throws against spells cast by fiends or undead.<br/><br/>Once you use this feature, you can''t use it again until you finish a long rest.', 0, 'Ability' );
INSERT INTO ability VALUES ( 155, 'Favored Enemy', 'Beginning at 1st level, you have significant experience studying, tracking, hunting, and even talking to a certain type of enemy.<br/><br/>Choose a type of favored enemy: aberrations, beasts, celestials, constructs, dragons, elementals, fey, fiends, giants, monstrosities, oozes, plants, or undead. Alternatively, you can select two races of humanoid (such as gnolls and orcs) as favored enemies.<br/><br/>You have advantage on Wisdom (Survival) checks to track your favored enemies, as well as on Intelligence checks to recall information about them.<br/><br/>When you gain this feature, you also learn one language of your choice that is spoken by your favored enemies, if they speak one at all.<br/><br/>You choose one additional favored enemy, as well as an associated language, at 6th and 14th level. As you gain levels, your choices should reflect the types of monsters you have encountered on your adventures.', 0, 'Ability' );
INSERT INTO ability VALUES ( 156, 'Natural Explorer', 'You are particularly familiar with one type of natural environment and are adept at traveling and surviving in such regions. Choose one type of favored terrain: arctic, coast, desert, forest, grassland, mountain, or swamp. When you make an Intelligence or Wisdom check related to your favored terrain, your proficiency bonus is doubled if you are using a skill that you''re proficient in.<br/><br/>While traveling for an hour or more in your favored terrain, you gain the following benefits:<br/><br/>- Difficult terrain doesn''t slow your group''s travel.<br/>- Your group can''t become lost except by magical means.<br/>- Even when you are engaged in another activity while traveling (such as foraging, navigating, or tracking), you remain alert to danger.<br/>- If you are traveling alone, you can move stealthily at a normal pace.<br/>- When you forage, you find twice as much food as you normally would.<br/>- While tracking other creatures, you also learn their exact number, their sizes, and how long ago they passed through the area.<br/><br/>You choose additional favored terrain types at 6th and 10th level.', 0, 'Ability' );
INSERT INTO ability VALUES ( 157, 'Spellcasting - Ranger', 'By the time you reach 2nd level, you have learned to use the magical essence of nature to cast spells, much as a druid does. See spellcasting for the general rules of spellcasting and the ranger-spells list.<br/><h1>Spell Slots</h1>The Ranger table shows how many spell slots you have to cast your spells of 1st level and higher. To cast one of these spells, you must expend a slot of the spell''s level or higher. You regain all expended spell slots when you finish a long rest.<br/><br/>For example, if you know the 1st-level spell animal-friendship and have a 1st-level and a 2nd-level spell slot available, you can cast animal-friendship using either slot.<br/><h1>Spells Known of 1st Level and Higher</h1>You know two 1st-level spells of your choice from the ranger spell list.<br/><br/>The Spells Known column of the Ranger table shows when you learn more ranger spells of your choice. Each of these spells must be of a level for which you have spell slots. For instance, when you reach 5th level in this class, you can learn one new spell of 1st or 2nd level.<br/><br/>Additionally, when you gain a level in this class, you can choose one of the ranger spells you know and replace it with another spell from the ranger spell list, which also must be of a level for which you have spell slots.<br/><h1>Spellcasting Ability</h1>Wisdom is your spellcasting ability for your ranger spells, since your magic draws on your attunement to nature. You use your Wisdom whenever a spell refers to your spellcasting ability. In addition, you use your Wisdom modifier when setting the saving throw DC for a ranger spell you cast and when making an attack roll with one.<br/><br/>- <b>Spell save DC</b> = 8 + your proficiency bonus + your Wisdom modifier<br/><br/>- <b>Spell attack modifier</b> = your proficiency bonus + your Wisdom modifier', 0, 'Ability' );
INSERT INTO ability VALUES ( 158, 'Ranger Archetype', 'At 3rd level, you choose an archetype that you strive to emulate, such as the Hunter. Your choice grants you features at 3rd level and again at 7th, 11th, and 15th level.', 0, 'Ability' );
INSERT INTO ability VALUES ( 159, 'Primeval Awareness', 'Beginning at 3rd level, you can use your action and expend one ranger spell slot to focus your awareness on the region around you. For 1 minute per level of the spell slot you expend, you can sense whether the following types of creatures are present within 1 mile of you (or within up to 6 miles if you are in your favored terrain): aberrations, celestials, dragons, elementals, fey, fiends, and undead. This feature doesn''t reveal the creatures'' location or number.', 0, 'Ability' );
INSERT INTO ability VALUES ( 160, 'Land''s Stride', 'Starting at 8th level, moving through nonmagical difficult terrain costs you no extra movement. You can also pass through nonmagical plants without being slowed by them and without taking damage from them if they have thorns, spines, or a similar hazard.<br/><br/>In addition, you have advantage on saving throws against plants that are magically created or manipulated to impede movement, such those created by the entangle spell.', 0, 'Ability' );
INSERT INTO ability VALUES ( 161, 'Hide in Plain Sight', 'Starting at 10th level, you can spend 1 minute creating camouflage for yourself. You must have access to fresh mud, dirt, plants, soot, and other naturally occurring materials with which to create your camouflage.<br/><br/>Once you are camouflaged in this way, you can try to hide by pressing yourself up against a solid surface, such as a tree or wall, that is at least as tall and wide as you are. You gain a +10 bonus to Dexterity (Stealth) checks as long as you remain there without moving or taking actions. Once you move or take an action or a reaction, you must camouflage yourself again to gain this benefit.', 0, 'Ability' );
INSERT INTO ability VALUES ( 162, 'Vanish', 'Starting at 14th level, you can use the Hide action as a bonus action on your turn. Also, you can''t be tracked by nonmagical means, unless you choose to leave a trail.', 0, 'Ability' );
INSERT INTO ability VALUES ( 163, 'Feral Senses', 'At 18th level, you gain preternatural senses that help you fight creatures you can''t see. When you attack a creature you can''t see, your inability to see it doesn''t impose disadvantage on your attack rolls against it.<br/><br/>You are also aware of the location of any invisible creature within 30 feet of you, provided that the creature isn''t hidden from you and you aren''t blinded or deafened.', 0, 'Ability' );
INSERT INTO ability VALUES ( 164, 'Foe Slayer', 'At 20th level, you become an unparalleled hunter of your enemies. Once on each of your turns, you can add your Wisdom modifier to the attack roll or the damage roll of an attack you make against one of your favored enemies. You can choose to use this feature before or after the roll, but before any effects of the roll are applied.', 0, 'Ability' );
INSERT INTO ability VALUES ( 165, 'Hunter', 'Emulating the Hunter archetype means accepting your place as a bulwark between civilization and the terrors of the wilderness. As you walk the Hunter''s path, you learn specialized techniques for fighting the threats you face, from rampaging ogres and hordes of orcs to towering giants and terrifying dragons.', 0, 'Ability' );
INSERT INTO ability VALUES ( 166, 'Hunter - Colossus Slayer', 'Your tenacity can wear down the most potent foes. When you hit a creature with a weapon attack, the creature takes an extra 1d8 damage if it''s below its hit point maximum. You can deal this extra damage only once per turn.', 0, 'Ability' );
INSERT INTO ability VALUES ( 167, 'Hunter - Giant Killer', 'When a Large or larger creature within 5 feet of you hits or misses you with an attack, you can use your reaction to attack that creature immediately after its attack, provided that you can see the creature.', 0, 'Ability' );
INSERT INTO ability VALUES ( 168, 'Hunter - Horde Breaker', 'Once on each of your turns when you make a weapon attack, you can make another attack with the same weapon against a different creature that is within 5 feet of the original target and within range of your weapon.', 0, 'Ability' );
INSERT INTO ability VALUES ( 169, 'Hunter - Escape the Horde', 'Opportunity attacks against you are made with disadvantage.', 0, 'Ability' );
INSERT INTO ability VALUES ( 170, 'Hunter - Multiattack Defense', 'When a creature hits you with an attack, you gain a +4 bonus to AC against all subsequent attacks made by that creature for the rest of the turn.', 0, 'Ability' );
INSERT INTO ability VALUES ( 171, 'Hunter - Steel Will', 'You have advantage on saving throws against being frightened.', 0, 'Ability' );
INSERT INTO ability VALUES ( 172, 'Hunter - Volley', 'You can use your action to make a ranged attack against any number of creatures within 10 feet of a point you can see within your weapon''s range. You must have ammunition for each target, as normal, and you make a separate attack roll for each target.', 0, 'Ability' );
INSERT INTO ability VALUES ( 173, 'Hunter - Whirlwind Attack', 'You can use your action to make a melee attack against any number of creatures within 5 feet of you, with a separate attack roll for each target.', 0, 'Ability' );
INSERT INTO ability VALUES ( 174, 'Hunter - Evasion', 'When you are subjected to an effect, such as a red dragon''s fiery breath or a lightning-bolt spell, that allows you to make a Dexterity saving throw to take only half damage, you instead take no damage if you succeed on the saving throw, and only half damage if you fail.', 0, 'Ability' );
INSERT INTO ability VALUES ( 175, 'Hunter - Stand Against the Tide', 'When a hostile creature misses you with a melee attack, you can use your reaction to force that creature to repeat the same attack against another creature (other than itself) of your choice.', 0, 'Ability' );
INSERT INTO ability VALUES ( 176, 'Hunter - Uncanny Dodge', 'When an attacker that you can see hits you with an attack, you can use your reaction to halve the attack''s damage against you.', 0, 'Ability' );
INSERT INTO ability VALUES ( 177, 'Expertise', 'At 1st level, choose two of your skill proficiencies, or one of your skill proficiencies and your proficiency with thieves'' tools. Your proficiency bonus is doubled for any ability check you make that uses either of the chosen proficiencies.<br/><br/>At 6th level, you can choose two more of your proficiencies (in skills or with thieves'' tools) to gain this benefit.', 0, 'Ability' );
INSERT INTO ability VALUES ( 178, 'Sneak Attack', 'Beginning at 1st level, you know how to strike subtly and exploit a foe''s distraction. Once per turn, you can deal an extra 1d6 damage to one creature you hit with an attack if you have advantage on the attack roll. The attack must use a finesse or a ranged weapon.<br/><br/>You don''t need advantage on the attack roll if another enemy of the target is within 5 feet of it, that enemy isn''t incapacitated, and you don''t have disadvantage on the attack roll.<br/><br/>The amount of the extra damage increases as you gain levels in this class, as shown in the Sneak Attack column of the Rogue table.', 0, 'Ability' );
INSERT INTO ability VALUES ( 179, 'Thieves'' Cant', 'During your rogue training you learned thieves'' cant, a secret mix of dialect, jargon, and code that allows you to hide messages in seemingly normal conversation. Only another creature that knows thieves'' cant understands such messages. It takes four times longer to convey such a message than it does to speak the same idea plainly.<br/><br/>In addition, you understand a set of secret signs and symbols used to convey short, simple messages, such as whether an area is dangerous or the territory of a thieves'' guild, whether loot is nearby, or whether the people in an area are easy marks or will provide a safe house for thieves on the run.', 0, 'Ability' );
INSERT INTO ability VALUES ( 180, 'Cunning Action', 'Starting at 2nd level, your quick thinking and agility allow you to move and act quickly. You can take a bonus action on each of your turns in combat. This action can be used only to take the Dash, Disengage, or Hide action.', 0, 'Ability' );
INSERT INTO ability VALUES ( 181, 'Roguish Archetype', 'Rogues have many features in common, including their emphasis on perfecting their skills, their precise and deadly approach to combat, and their increasingly quick reflexes. But different rogues steer those talents in varying directions, embodied by the rogue archetypes. Your choice of archetype is a reflection of your focus---not necessarily an indication of your chosen profession, but a description of your preferred techniques.<br/><br/>At 3rd level, you choose an archetype that you emulate in the exercise of your rogue abilities, such as Thief. Your archetype choice grants you features at 3rd level and then again at 9th, 13th, and 17th level.', 0, 'Ability' );
INSERT INTO ability VALUES ( 182, 'Uncanny Dodge', 'Starting at 5th level, when an attacker that you can see hits you with an attack, you can use your reaction to halve the attack''s damage against you.', 0, 'Ability' );
INSERT INTO ability VALUES ( 183, 'Evasion', 'Beginning at 7th level, you can nimbly dodge out of the way of certain area effects, such as a red dragon''s fiery breath or an ice storm spell. When you are subjected to an effect that allows you to make a Dexterity saving throw to take only half damage, you instead take no damage if you succeed on the saving throw, and only half damage if you fail.', 0, 'Ability' );
INSERT INTO ability VALUES ( 184, 'Reliable Talent', 'By 11th level, you have refined your chosen skills until they approach perfection. Whenever you make an ability check that lets you add your proficiency bonus, you can treat a d20 roll of 9 or lower as a 10.', 0, 'Ability' );
INSERT INTO ability VALUES ( 185, 'Blindsense', 'Starting at 14th level, if you are able to hear, you are aware of the location of any hidden or invisible creature within 10 feet of you.', 0, 'Ability' );
INSERT INTO ability VALUES ( 186, 'Slippery Mind', 'By 15th level, you have acquired greater mental strength. You gain proficiency in Wisdom saving throws.', 0, 'Ability' );
INSERT INTO ability VALUES ( 187, 'Elusive', 'Beginning at 18th level, you are so evasive that attackers rarely gain the upper hand against you. No attack roll has advantage against you while you aren''t incapacitated.', 0, 'Ability' );
INSERT INTO ability VALUES ( 188, 'Stroke of Luck', 'At 20th level, you have an uncanny knack for succeeding when you need to. If your attack misses a target within range, you can turn the miss into a hit. Alternatively, if you fail an ability check, you can treat the d20 roll as a 20.<br/><br/>Once you use this feature, you can''t use it again until you finish a short or long rest.', 0, 'Ability' );
INSERT INTO ability VALUES ( 189, 'Thief', 'You hone your skills in the larcenous arts. Burglars, bandits, cutpurses, and other criminals typically follow this archetype, but so do rogues who prefer to think of themselves as professional treasure seekers, explorers, delvers, and investigators. In addition to improving your agility and stealth, you learn skills useful for delving into ancient ruins, reading unfamiliar languages, and using magic items you normally couldn''t employ.', 0, 'Ability' );
INSERT INTO ability VALUES ( 190, 'Thief - Fast Hands', 'Starting at 3rd level, you can use the bonus action granted by your Cunning Action to make a Dexterity (Sleight of Hand) check, use your thieves'' tools to disarm a trap or open a lock, or take the Use an Object action.', 0, 'Ability' );
INSERT INTO ability VALUES ( 191, 'Thief - Second-Story Work', 'When you choose this archetype at 3rd level, you gain the ability to climb faster than normal, climbing no longer costs you extra movement.<br/><br/>In addition, when you make a running jump, the distance you cover increases by a number of feet equal to your Dexterity modifier.', 0, 'Ability' );
INSERT INTO ability VALUES ( 192, 'Thief - Supreme Sneak', 'Starting at 9th level, you have advantage on a Dexterity (Stealth) check if you move no more than half your speed on the same turn.', 0, 'Ability' );
INSERT INTO ability VALUES ( 193, 'Thief - Use Magic Device', 'By 13th level, you have learned enough about the workings of magic that you can improvise the use of items even when they are not intended for you. You ignore all class, race, and level requirements on the use of magic items.', 0, 'Ability' );
INSERT INTO ability VALUES ( 194, 'Thief - Thief''s Reflexes', 'When you reach 17th level, you have become adept at laying ambushes and quickly escaping danger. You can take two turns during the first round of any combat. You take your first turn at your normal initiative and your second turn at your initiative minus 10. You can''t use this feature when you are surprised.', 0, 'Ability' );
INSERT INTO ability VALUES ( 195, 'Spellcasting - Sorcerer', 'An event in your past, or in the life of a parent or ancestor, left an indelible mark on you, infusing you with arcane magic. This font of magic, whatever its origin, fuels your spells.<br/><h1>Cantrips</h1>At 1st level, you know four cantrips of your choice from the sorcerer-spells list. You learn additional sorcerer cantrips of your choice at higher levels, as shown in the Cantrips Known column of the Sorcerer table.<br/><h1>Spell Slots</h1>The Sorcerer table shows how many spell slots you have to cast your spells of 1st level and higher. To cast one of these sorcerer spells, you must expend a slot of the spell''s level or higher. You regain all expended spell slots when you finish a long rest.<br/><br/>For example, if you know the 1st-level spell burning hands and have a 1st-level and a 2nd-level spell slot available, you can cast burning hands using either slot.<br/><h1>Spells Known of 1st Level and Higher</h1>You know two 1st-level spells of your choice from the sorcerer-spells list.<br/><br/>The Spells Known column of the Sorcerer table shows when you learn more sorcerer spells of your choice. Each of these spells must be of a level for which you have spell slots. For instance, when you reach 3rd level in this class, you can learn one new spell of 1st or 2nd level.<br/><br/>Additionally, when you gain a level in this class, you can choose one of the sorcerer spells you know and replace it with another spell from the sorcerer spell list, which also must be of a level for which you have spell slots.<br/><h1>Spellcasting Ability</h1>Charisma is your spellcasting ability for your sorcerer spells, since the power of your magic relies on your ability to project your will into the world. You use your Charisma whenever a spell refers to your spellcasting ability. In addition, you use your Charisma modifier when setting the saving throw DC for a sorcerer spell you cast and when making an attack roll with one.<br/><br/>- <b>Spell save DC</b> = 8 + your proficiency bonus + your Charisma modifier<br/><br/>- <b>Spell attack modifier</b> = your proficiency bonus + your Charisma modifier<br/><br/><h1>Spellcasting Focus</h1>You can use an arcane focus as a spellcasting focus for your sorcerer spells.', 0, 'Ability' );
INSERT INTO ability VALUES ( 196, 'Sorcerous Origin', 'Different sorcerers claim different origins for their innate magic, such as a draconic bloodline.<br/><br/>Choose a sorcerous origin, which describes the source of your innate magical power, such as Draconic Bloodline.<br/><br/>Your choice grants you features when you choose it at 1st level and again at 6th, 14th, and 18th level.', 0, 'Ability' );
INSERT INTO ability VALUES ( 197, 'Font of Magic', 'At 2nd level, you tap into a deep wellspring of magic within yourself. This wellspring is represented by sorcery points, which allow you to create a variety of magical effects.<br/><h1>Sorcery Points</h1>You have 2 sorcery points, and you gain more as you reach higher levels, as shown in the Sorcery Points column of the Sorcerer table. You can never have more sorcery points than shown on the table for your level. You regain all spent sorcery points when you finish a long rest.<br/><h1>Flexible Casting</h1>You can use your sorcery points to gain additional spell slots, or sacrifice spell slots to gain additional sorcery points. You learn other ways to use your sorcery points as you reach higher levels.<br/><br/><b>Creating Spell Slots</b>. You can transform unexpended sorcery points into one spell slot as a bonus action on your turn. The Creating Spell Slots table shows the cost of creating a spell slot of a given level. You can create spell slots no higher in level than 5th.<br/><br/>Any spell slot you create with this feature vanishes when you finish a long rest.<br/><b>Creating Spell Slots</b><table><tr><th>Spell Slot Level</th><th>Sorcery Point Cost</th></tr><tr><td>1st</td><td>2</td></tr><tr><td>2nd</td><td>3</td></tr><tr><td>3rd</td><td>5</td></tr><tr><td>4th</td><td>6</td></tr><tr><td>5th</td><td>7</td></tr></table><br/><b>Converting a Spell Slot to Sorcery Points.</b> As a bonus action on your turn, you can expend one spell slot and gain a number of sorcery points equal to the slot''s level.^^', 0, 'Ability' );
INSERT INTO ability VALUES ( 198, 'Metamagic', 'At 3rd level, you gain the ability to twist your spells to suit your needs. You gain two of the following Metamagic options of your choice. You gain another one at 10th and 17th level.<br/><br/>You can use only one Metamagic option on a spell when you cast it, unless otherwise noted.', 0, 'Ability' );
INSERT INTO ability VALUES ( 199, 'Metamagic - Careful Spell', 'When you cast a spell that forces other creatures to make a saving throw, you can protect some of those creatures from the spell''s full force. To do so, you spend 1 sorcery point and choose a number of those creatures up to your Charisma modifier (minimum of one creature). A chosen creature automatically succeeds on its saving throw against the spell.', 0, 'Ability' );
INSERT INTO ability VALUES ( 200, 'Metamagic - Distant Spell', 'When you cast a spell that has a range of 5 feet or greater, you can spend 1 sorcery point to double the range of the spell.<br/><br/>When you cast a spell that has a range of touch, you can spend 1 sorcery point to make the range of the spell 30 feet.', 0, 'Ability' );
INSERT INTO ability VALUES ( 201, 'Metamagic - Empowered Spell', 'When you roll damage for a spell, you can spend 1 sorcery point to reroll a number of the damage dice up to your Charisma modifier (minimum of one). You must use the new rolls.<br/><br/>You can use Empowered Spell even if you have already used a different Metamagic option during the casting of the spell.', 0, 'Ability' );
INSERT INTO ability VALUES ( 202, 'Metamagic - Extended Spell', 'When you cast a spell that has a duration of 1 minute or longer, you can spend 1 sorcery point to double its duration, to a maximum duration of 24 hours.', 0, 'Ability' );
INSERT INTO ability VALUES ( 203, 'Metamagic - Heightened Spell', 'When you cast a spell that forces a creature to make a saving throw to resist its effects, you can spend 3 sorcery points to give one target of the spell disadvantage on its first saving throw made against the spell.', 0, 'Ability' );
INSERT INTO ability VALUES ( 204, 'Metamagic - Quickened Spell', 'When you cast a spell that has a casting time of 1 action, you can spend 2 sorcery points to change the casting time to 1 bonus action for this casting.', 0, 'Ability' );
INSERT INTO ability VALUES ( 205, 'Metamagic - Subtle Spell', 'When you cast a spell, you can spend 1 sorcery point to cast it without any somatic or verbal components.', 0, 'Ability' );
INSERT INTO ability VALUES ( 206, 'Metamagic - Twinned Spell', 'When you cast a spell that targets only one creature and doesn''t have a range of self, you can spend a number of sorcery points equal to the spell''s level to target a second creature in range with the same spell (1 sorcery point if the spell is a cantrip).<br/><br/>To be eligible, a spell must be incapable of targeting more than one creature at the spell''s current level. For example, magic-missile and scorching-ray aren''t eligible, but ray-of-frost is.', 0, 'Ability' );
INSERT INTO ability VALUES ( 207, 'Sorcerous Restoration', 'At 20th level, you regain 4 expended sorcery points whenever you finish a short rest.', 0, 'Ability' );
INSERT INTO ability VALUES ( 208, 'Draconic Bloodline', 'Your innate magic comes from draconic magic that was mingled with your blood or that of your ancestors. Most often, sorcerers with this origin trace their descent back to a mighty sorcerer of ancient times who made a bargain with a dragon or who might even have claimed a dragon parent. Some of these bloodlines are well established in the world, but most are obscure. Any given sorcerer could be the first of a new bloodline, as a result of a pact or some other exceptional circumstance.', 0, 'Ability' );
INSERT INTO ability VALUES ( 209, 'Draconic - Dragon Ancestor', 'At 1st level, you choose one type of dragon as your ancestor. The damage type associated with each dragon is used by features you gain later.<br/><br/>Draconic Ancestry<br/><table><tr><th>Dragon</th><th>Damage Type</th></tr><tr><td>Black</td><td>Acid</th></tr><tr><td>Blue</td><td>Lightning</th></tr><tr><td>Brass</td><td>Fire</th></tr><tr><td>Bronze</td><td>Lightning</th></tr><tr><td>Copper</td><td>Acid</th></tr><tr><td>Gold</td><td>Fire</th></tr><tr><td>Green</td><td>Poison</th></tr><tr><td>Red</td><td>Fire</th></tr><tr><td>Silver</td><td>Cold</th></tr><tr><td>White</td><td>Cold</th></tr></table><br/>You can speak, read, and write Draconic. Additionally, whenever you make a Charisma check when interacting with dragons, your proficiency bonus is doubled if it applies to the check.', 0, 'Ability' );
INSERT INTO ability VALUES ( 210, 'Draconic - Draconic Resilience', 'As magic flows through your body, it causes physical traits of your dragon ancestors to emerge. At 1st level, your hit point maximum increases by 1 and increases by 1 again whenever you gain a level in this class.<br/><br/>Additionally, parts of your skin are covered by a thin sheen of dragon-like scales. When you aren''t wearing armor, your AC equals 13 + your Dexterity modifier.', 0, 'Ability' );
INSERT INTO ability VALUES ( 211, 'Draconic - Elemental Affinity', 'Starting at 6th level, when you cast a spell that deals damage of the type associated with your draconic ancestry, you can add your Charisma modifier to one damage roll of that spell. At the same time, you can spend 1 sorcery point to gain resistance to that damage type for 1 hour.', 0, 'Ability' );
INSERT INTO ability VALUES ( 212, 'Draconic - Dragon Wings', 'At 14th level, you gain the ability to sprout a pair of dragon wings from your back, gaining a flying speed equal to your current speed. You can create these wings as a bonus action on your turn. They last until you dismiss them as a bonus action on your turn.<br/><br/>You can''t manifest your wings while wearing armor unless the armor is made to accommodate them, and clothing not made to accommodate your wings might be destroyed when you manifest them.', 0, 'Ability' );
INSERT INTO ability VALUES ( 213, 'Draconic - Draconic Presence', 'Beginning at 18th level, you can channel the dread presence of your dragon ancestor, causing those around you to become awestruck or frightened. As an action, you can spend 5 sorcery points to draw on this power and exude an aura of awe or fear (your choice) to a distance of 60 feet. For 1 minute or until you lose your concentration (as if you were casting a concentration spell), each hostile creature that starts its turn in this aura must succeed on a Wisdom saving throw or be charmed (if you chose awe) or frightened (if you chose fear) until the aura ends. A creature that succeeds on this saving throw is immune to your aura for 24 hours.', 0, 'Ability' );
INSERT INTO ability VALUES ( 214, 'Otherworldly Patron', 'At 1st level, you have struck a bargain with an otherworldly being of your choice: the Archfey, the Fiend, or the Great Old One, each of which is detailed at the end of the class description. Your choice grants you features at 1st level and again at 6th, 10th, and 14th level.<br/><br/>The beings that serve as patrons for warlocks are mighty inhabitants of other planes of existence---not gods, but almost godlike in their power. Various patrons give their warlocks access to different powers and invocations, and expect significant favors in return.<br/><br/>Some patrons collect warlocks, doling out mystic knowledge relatively freely or boasting of their ability to bind mortals to their will. Other patrons bestow their power only grudgingly, and might make a pact with only one warlock. Warlocks who serve the same patron might view each other as allies, siblings, or rivals.', 0, 'Ability' );
INSERT INTO ability VALUES ( 215, 'Pact Magic', 'Your arcane research and the magic bestowed on you by your patron have given you facility with spells.<h1>Cantrips</h1>You know two cantrips of your choice from the warlock-spells list. You learn additional warlock cantrips of your choice at higher levels, as shown in the Cantrips Known column of the Warlock table.<h1>Spell Slots</h1>The Warlock table shows how many spell slots you have. The table also shows what the level of those slots is, all of your spell slots are the same level. To cast one of your warlock spells of 1st level or higher, you must expend a spell slot. You regain all expended spell slots when you finish a short or long rest.For example, when you are 5th level, you have two 3rd-level spell slots. To cast the 1st-level spell thunderwave, you must spend one of those slots, and you cast it as a 3rd-level spell.<h1>Spells Known of 1st Level and Higher</h1>At 1st level, you know two 1st-level spells of your choice from the warlock spell list.The Spells Known column of the Warlock table shows when you learn more warlock spells of your choice of 1st level and higher. A spell you choose must be of a level no higher than what''''s shown in the table''''s Slot Level column for your level. When you reach 6th level, for example, you learn a new warlock spell, which can be 1st, 2nd, or 3rd level.Additionally, when you gain a level in this class, you can choose one of the warlock spells you know and replace it with another spell from the warlock spell list, which also must be of a level for which you have spell slots.<h1>Spellcasting Ability</h1>Charisma is your spellcasting ability for your warlock spells, so you use your Charisma whenever a spell refers to your spellcasting ability. In addition, you use your Charisma modifier when setting the saving throw DC for a warlock spell you cast and when making an attack roll with one.<br/><br/>- <b>Spell save DC</b> = 8 + your proficiency bonus + your Charisma modifier<br/><br/>- <b>Spell attack modifier</b> = your proficiency bonus + your Charisma modifier<br/><br/><h1>Spellcasting Focus</h1>You can use an arcane focus as a spellcasting focus for your warlock spells.', 0, 'Ability' );
INSERT INTO ability VALUES ( 216, 'Eldritch Invocations', 'In your study of occult lore, you have unearthed eldritch invocations, fragments of forbidden knowledge that imbue you with an abiding magical ability.<br/><br/>At 2nd level, you gain two eldritch invocations of your choice. Your invocation options are detailed at the end of the class description. When you gain certain warlock levels, you gain additional invocations of your choice, as shown in the Invocations Known column of the Warlock table.<br/><br/>Additionally, when you gain a level in this class, you can choose one of the invocations you know and replace it with another invocation that you could learn at that level.', 0, 'Ability' );
INSERT INTO ability VALUES ( 217, 'Pact of the Chain', 'You learn the find-familiar spell and can cast it as a ritual. The spell doesn''t count against your number of spells known.<br/><br/>When you cast the spell, you can choose one of the normal forms for your familiar or one of the following special forms: imp, pseudodragon, quasit, or sprite.<br/><br/>Additionally, when you take the Attack action, you can forgo one of your own attacks to allow your familiar to make one attack of its own with its reaction.<br/><br/>If you serve the Fiend, your weapon could be an axe made of black metal and adorned with decorative flames.', 0, 'Ability' );
INSERT INTO ability VALUES ( 218, 'Pact of the Blade', 'You can use your action to create a pact weapon in your empty hand. You can choose the form that this melee weapon takes each time you create it. You are proficient with it while you wield it. This weapon counts as magical for the purpose of overcoming resistance and immunity to nonmagical attacks and damage.<br/><br/>Your pact weapon disappears if it is more than 5 feet away from you for 1 minute or more. It also disappears if you use this feature again, if you dismiss the weapon (no action required), or if you die.<br/><br/>You can transform one magic weapon into your pact weapon by performing a special ritual while you hold the weapon. You perform the ritual over the course of 1 hour, which can be done during a short rest. You can then dismiss the weapon, shunting it into an extradimensional space, and it appears whenever you create your pact weapon thereafter. You can''t affect an artifact or a sentient weapon in this way. The weapon ceases being your pact weapon if you die, if you perform the 1-hour ritual on a different weapon, or if you use a 1-hour ritual to break your bond to it. The weapon appears at your feet if it is in the extradimensional space when the bond breaks.<br/><br/>If you serve the Fiend, your weapon could be an axe made of black metal and adorned with decorative flames.', 0, 'Ability' );
INSERT INTO ability VALUES ( 219, 'Pact of the Tome', 'Your patron gives you a grimoire called a Book of Shadows. When you gain this feature, choose three cantrips from any class''s spell list (the three needn''t be from the same list). While the book is on your person, you can cast those cantrips at will. They don''t count against your number of cantrips known. If they don''t appear on the warlock spell list, they are nonetheless warlock spells for you.<br/><br/>If you lose your Book of Shadows, you can perform a 1-hour ceremony to receive a replacement from your patron. This ceremony can be performed during a short or long rest, and it destroys the previous book. The book turns to ash when you die.<br/><br/>Your Book of Shadows could be a weighty tome bound in demon hide studded with iron, holding spells of conjuration and a wealth of forbidden lore about the sinister regions of the cosmos, a gift of the Fiend.', 0, 'Ability' );
INSERT INTO ability VALUES ( 220, 'Mystic Arcanum', 'At 11th level, your patron bestows upon you a magical secret called an arcanum. Choose one 6th-level spell from the warlock-spells list as this arcanum.<br/><br/>You can cast your arcanum spell once without expending a spell slot. You must finish a long rest before you can do so again.<br/><br/>At higher levels, you gain more warlock spells of your choice that can be cast in this way: one 7th-level spell at 13th level, one 8th-level spell at 15th level, and one 9th-level spell at 17th level. You regain all uses of your Mystic Arcanum when you finish a long rest.', 0, 'Ability' );
INSERT INTO ability VALUES ( 221, 'Eldritch Master', 'At 20th level, you can draw on your inner reserve of mystical power while entreating your patron to regain expended spell slots. You can spend 1 minute entreating your patron for aid to regain all your expended spell slots from your Pact Magic feature. Once you regain spell slots with this feature, you must finish a long rest before you can do so again.', 0, 'Ability' );
INSERT INTO ability VALUES ( 222, 'Eldritch Invocation - Agonizing Blast', 'Prerequisite: eldritch-blast cantrip<br/><br/>When you cast eldritch-blast, add your Charisma modifier to the damage it deals on a hit.', 0, 'Ability' );
INSERT INTO ability VALUES ( 223, 'Eldritch Invocation - Armor of Shadows', 'You can cast mage-armor on yourself at will, without expending a spell slot or material components.', 0, 'Ability' );
INSERT INTO ability VALUES ( 224, 'Eldritch Invocation - Ascendant Step', 'Prerequisite: 9th level<br/><br/>You can cast levitate on yourself at will, without expending a spell slot or material components.', 0, 'Ability' );
INSERT INTO ability VALUES ( 225, 'Eldritch Invocation - Beast Speech', 'You can cast speak-with-animals at will, without expending a spell slot.', 0, 'Ability' );
INSERT INTO ability VALUES ( 226, 'Eldritch Invocation - Beguiling Influence', 'You gain proficiency in the Deception and Persuasion skills.', 0, 'Ability' );
INSERT INTO ability VALUES ( 227, 'Eldritch Invocation - Bewitching Whispers', 'Prerequisite: 7th level<br/><br/>You can cast compulsion once using a warlock spell slot. You can''t do so again until you finish a long rest.', 0, 'Ability' );
INSERT INTO ability VALUES ( 228, 'Eldritch Invocation - Book of Ancient Secrets', 'Prerequisite: Pact of the Tome feature<br/><br/>You can now inscribe magical rituals in your Book of Shadows. Choose two 1st-level spells that have the ritual tag from any class''s spell list (the two needn''t be from the same list). The spells appear in the book and don''t count against the number of spells you know. With your Book of Shadows in hand, you can cast the chosen spells as rituals. You can''t cast the spells except as rituals, unless you''ve learned them by some other means. You can also cast a warlock spell you know as a ritual if it has the ritual tag.<br/><br/>On your adventures, you can add other ritual spells to your Book of Shadows. When you find such a spell, you can add it to the book if the spell''s level is equal to or less than half your warlock level (rounded up) and if you can spare the time to<br/><br/>transcribe the spell. For each level of the spell, the transcription process takes 2 hours and costs 50 gp for the rare inks needed to inscribe it.', 0, 'Ability' );
INSERT INTO ability VALUES ( 229, 'Eldritch Invocation - Chains of Carceri', 'Prerequisite: 15th level, Pact of the Chain feature<br/><br/>You can cast hold-monster at will---targeting a celestial, fiend, or elemental---without expending a spell slot or material components. You must finish a long rest before you can use this invocation on the same creature again.', 0, 'Ability' );
INSERT INTO ability VALUES ( 230, 'Eldritch Invocation - Devil''s Sight', 'You can see normally in darkness, both magical and nonmagical, to a distance of 120 feet.', 0, 'Ability' );
INSERT INTO ability VALUES ( 231, 'Eldritch Invocation - Dreadful Word', 'Prerequisite: 7th level<br/><br/>You can cast confusion once using a warlock spell slot. You can''t do so again until you finish a long rest.', 0, 'Ability' );
INSERT INTO ability VALUES ( 232, 'Eldritch Invocation - Eldritch Sight', 'You can cast detect-magic at will, without expending a spell slot.', 0, 'Ability' );
INSERT INTO ability VALUES ( 233, 'Eldritch Invocation - Eldritch Spear', 'Prerequisite: eldritch-blast cantrip<br/><br/>When you cast eldritch-blast, its range is 300 feet.', 0, 'Ability' );
INSERT INTO ability VALUES ( 234, 'Eldritch Invocation - Eyes of the Rune Keeper', 'You can read all writing.', 0, 'Ability' );
INSERT INTO ability VALUES ( 235, 'Eldritch Invocation - Fiendish Vigor', 'You can cast false-life on yourself at will as a 1st-level spell, without expending a spell slot or material components.', 0, 'Ability' );
INSERT INTO ability VALUES ( 236, 'Eldritch Invocation - Gaze of Two Minds', 'You can use your action to touch a willing humanoid and perceive through its senses until the end of your next turn. As long as the creature is on the same plane of existence as you, you can use your action on subsequent turns to maintain this connection, extending the duration until the end of your next turn. While perceiving through the other creature''s senses, you benefit from any special senses possessed by that creature, and you are blinded and deafened to your own surroundings.', 0, 'Ability' );
INSERT INTO ability VALUES ( 237, 'Eldritch Invocation - Lifedrinker', 'Prerequisite: 12th level, Pact of the Blade feature<br/><br/>When you hit a creature with your pact weapon, the creature takes extra necrotic damage equal to your Charisma modifier (minimum 1).', 0, 'Ability' );
INSERT INTO ability VALUES ( 238, 'Eldritch Invocation - Mask of Many Faces', 'You can cast disguise-self at will, without expending a spell slot.', 0, 'Ability' );
INSERT INTO ability VALUES ( 239, 'Eldritch Invocation - Master of Myriad Forms', 'Prerequisite: 15th level<br/><br/>You can cast alter-self at will, without expending a spell slot.', 0, 'Ability' );
INSERT INTO ability VALUES ( 240, 'Eldritch Invocation - Minions of Chaos', 'Prerequisite: 9th level<br/><br/>You can cast conjure-elemental once using a warlock spell slot. You can''t do so again until you finish a long rest.', 0, 'Ability' );
INSERT INTO ability VALUES ( 241, 'Eldritch Invocation - Mire the Mind', 'Prerequisite: 5th level<br/><br/>You can cast slow once using a warlock spell slot. You can''t do so again until you finish a long rest.', 0, 'Ability' );
INSERT INTO ability VALUES ( 242, 'Eldritch Invocation - Misty Visions', 'You can cast silent-image at will, without expending a spell slot or material components.', 0, 'Ability' );
INSERT INTO ability VALUES ( 243, 'Eldritch Invocation - One with Shadows', 'Prerequisite: 5th level<br/><br/>When you are in an area of dim light or darkness, you can use your action to become invisible until you move or take an action or a reaction.', 0, 'Ability' );
INSERT INTO ability VALUES ( 244, 'Eldritch Invocation - Otherworldly Leap', 'Prerequisite: 9th level<br/><br/>You can cast jump on yourself at will, without expending a spell slot or material components.', 0, 'Ability' );
INSERT INTO ability VALUES ( 245, 'Eldritch Invocation - Repelling Blast', 'Prerequisite: eldritch-blast cantrip*<br/><br/>When you hit a creature with eldritch-blast, you can push the creature up to 10 feet away from you in a straight line.', 0, 'Ability' );
INSERT INTO ability VALUES ( 246, 'Eldritch Invocation - Sculptor of Flesh', 'Prerequisite: 7th level<br/><br/>You can cast polymorph once using a warlock spell slot. You can''t do so again until you finish a long rest.', 0, 'Ability' );
INSERT INTO ability VALUES ( 247, 'Eldritch Invocation - Sign of Ill Omen', 'Prerequisite: 5th level<br/><br/>You can cast bestow-curse once using a warlock spell slot. You can''t do so again until you finish a long rest.', 0, 'Ability' );
INSERT INTO ability VALUES ( 248, 'Eldritch Invocation - Thief of Five Fates', 'You can cast bane once using a warlock spell slot. You can''t do so again until you finish a long rest.', 0, 'Ability' );
INSERT INTO ability VALUES ( 249, 'Eldritch Invocation - Thirsting Blade', 'Prerequisite: 5th level, Pact of the Blade feature<br/><br/>You can attack with your pact weapon twice, instead of once, whenever you take the Attack action on your turn.', 0, 'Ability' );
INSERT INTO ability VALUES ( 250, 'Eldritch Invocation - Visions of Distant Realms', 'Prerequisite: 15th level<br/><br/>You can cast arcane-eye at will, without expending a spell slot.', 0, 'Ability' );
INSERT INTO ability VALUES ( 251, 'Eldritch Invocation - Voice of the Chain Master', 'Prerequisite: Pact of the Chain feature<br/><br/>You can communicate telepathically with your familiar and perceive through your familiar''s senses as long as you are on the same plane of existence. Additionally, while perceiving through your familiar''s senses, you can also speak through your familiar in your own voice, even if your familiar is normally incapable of speech.', 0, 'Ability' );
INSERT INTO ability VALUES ( 252, 'Eldritch Invocation - Whispers of the Grave', 'Prerequisite: 9th level<br/><br/>You can cast speak-with-dead at will, without expending a spell slot.', 0, 'Ability' );
INSERT INTO ability VALUES ( 253, 'Eldritch Invocation - Witch Sight', 'Prerequisite: 15th level<br/><br/>You can see the true form of any shapechanger or creature concealed by illusion or transmutation magic while the creature is within 30 feet of you and within line of sight.', 0, 'Ability' );
INSERT INTO ability VALUES ( 254, 'The Fiend', 'You have made a pact with a fiend from the lower planes of existence, a being whose aims are evil, even if you strive against those aims. Such beings desire the corruption or destruction of all things, ultimately including you. Fiends powerful enough to forge a pact include demon lords such as Demogorgon, Orcus, Fraz''Urb-luu, and Baphomet, archdevils such as Asmodeus, Dispater, Mephistopheles, and Belial, pit fiends and balors that are especially mighty, and ultroloths and other lords of the yugoloths.', 0, 'Ability' );
INSERT INTO ability VALUES ( 255, 'The Fiend - Expanded Spell List', 'The Fiend lets you choose from an expanded list of spells when you learn a warlock spell. The following spells are added to the warlock spell list for you.<br/><h1>Fiend Expanded Spells</h1><table><tr><th>Spell Level</th><th>Spells</th></tr><tr><td>1st</td><td>burning-hands, command</td></tr><tr><th>2nd</td><td>blindness-deafness, scorching-ray</td></tr><tr><th>3rd</td><td>fireball, stinking-cloud</td></tr><tr><th>4th</td><td>fire-shield, wall-of-fire</td></tr><tr><th>5th</td><td>flame-strike, hallow</td></tr></table>', 0, 'Ability' );
INSERT INTO ability VALUES ( 256, 'The Fiend - Dark One''s Blessing', 'Starting at 1st level, when you reduce a hostile creature to 0 hit points, you gain temporary hit points equal to your Charisma modifier + your warlock level (minimum of 1).', 0, 'Ability' );
INSERT INTO ability VALUES ( 257, 'The Fiend - Dark One''s Own Luck', 'Starting at 6th level, you can call on your patron to alter fate in your favor. When you make an ability check or a saving throw, you can use this feature to add a d10 to your roll. You can do so after seeing the initial roll but before any of the roll''s effects occur.<br/><br/>Once you use this feature, you can''t use it again until you finish a short or long rest.', 0, 'Ability' );
INSERT INTO ability VALUES ( 258, 'The Fiend - Fiendish Resilience', 'Starting at 10th level, you can choose one damage type when you finish a short or long rest. You gain resistance to that damage type until you choose a different one with this feature. Damage from magical weapons or silver weapons ignores this resistance.', 0, 'Ability' );
INSERT INTO ability VALUES ( 259, 'The Fiend - Hurl Through Hell', 'Starting at 14th level, when you hit a creature with an attack, you can use this feature to instantly transport the target through the lower planes. The creature disappears and hurtles through a nightmare landscape.<br/><br/>At the end of your next turn, the target returns to the space it previously occupied, or the nearest unoccupied space. If the target is not a fiend, it takes 10d10 psychic damage as it reels from its horrific experience.<br/><br/>Once you use this feature, you can''t use it again until you finish a long rest.', 0, 'Ability' );
INSERT INTO ability VALUES ( 260, 'Spellcasting - Wizard', 'As a student of arcane magic, you have a spellbook containing spells that show the first glimmerings of your true power.<br/><h1>Cantrips</h1>At 1st level, you know three cantrips of your choice from the wizard-spells list. You learn additional wizard cantrips of your choice at higher levels, as shown in the Cantrips Known column of the Wizard table.<br/><h1>Spellbook</h1>At 1st level, you have a spellbook containing six 1st-level wizard spells of your choice. Your spellbook is the repository of the wizard spells you know, except your cantrips, which are fixed in your mind.<br/><h1>Preparing and Casting Spells</h1>The Wizard table shows how many spell slots you have to cast your spells of 1st level and higher. To cast one of these spells, you must expend a slot of the spell''s level or higher. You regain all expended spell slots when you finish a long rest.<br/><br/>You prepare the list of wizard spells that are available for you to cast. To do so, choose a number of wizard spells from your spellbook equal to your Intelligence modifier + your wizard level (minimum of one spell). The spells must be of a level for which you have spell slots.<br/><br/>For example, if you''re a 3rd-level wizard, you have four 1st-level and two 2nd-level spell slots. With an Intelligence of 16, your list of prepared spells can include six spells of 1st or 2nd level, in any combination, chosen from your spellbook. If you prepare the 1st-level spell magic-missile, you can cast it using a 1st-level or a 2nd-level slot. Casting the spell doesn''t remove it from your list of prepared spells.<br/><br/>You can change your list of prepared spells when you finish a long rest. Preparing a new list of wizard spells requires time spent studying your spellbook and memorizing the incantations and gestures you must make to cast the spell: at least 1 minute per spell level for each spell on your list.<br/><h1>Spellcasting Ability</h1>Intelligence is your spellcasting ability for your wizard spells, since you learn your spells through dedicated study and memorization. You use your Intelligence whenever a spell refers to your spellcasting ability. In addition, you use your Intelligence modifier when setting the saving throw DC for a wizard spell you cast and when making an attack roll with one.<br/><br/>- <b>Spell save DC</b> = 8 + your proficiency bonus + your Intelligence modifier<br/><br/>- <b>Spell attack modifier</b> = your proficiency bonus + your Intelligence modifier<br/><br/><h1>Ritual Casting</h1>You can cast a wizard spell as a ritual if that spell has the ritual tag and you have the spell in your spellbook. You don''t need to have the spell prepared.<br/><h1>Spellcasting Focus</h1>You can use an arcane focus as a spellcasting focus for your wizard spells.<br/><br/><h1>Learning Spells of 1st Level and Higher</h1>Each time you gain a wizard level, you can add two wizard spells of your choice to your spellbook for free. Each of these spells must be of a level for which you have spell slots, as shown on the Wizard table. On your adventures, you might find other spells that you can add to your spellbook (see the "Your Spellbook" sidebar).', 0, 'Ability' );
INSERT INTO ability VALUES ( 261, 'Arcane Recovery', 'You have learned to regain some of your magical energy by studying your spellbook. Once per day when you finish a short rest, you can choose expended spell slots to recover. The spell slots can have a combined level that is equal to or less than half your wizard level (rounded up), and none of the slots can be 6th level or higher.<br/><br/>For example, if you''re a 4th-level wizard, you can recover up to two levels worth of spell slots. You can recover either a 2nd-level spell slot or two 1st-level spell slots.', 0, 'Ability' );
INSERT INTO ability VALUES ( 262, 'Arcane Tradition', 'When you reach 2nd level, you choose an arcane tradition, shaping your practice of magic through one of eight schools, such as Evocation.<br/><br/>Your choice grants you features at 2nd level and again at 6th, 10th, and 14th level.<br/><br/>The study of wizardry is ancient, stretching back to the earliest mortal discoveries of magic. It is firmly established in fantasy gaming worlds, with various traditions dedicated to its complex study.<br/><br/>The most common arcane traditions in the multiverse revolve around the schools of magic. Wizards through the ages have cataloged thousands of spells, grouping them into eight categories called schools. In some places, these traditions are literally schools. In other institutions, the schools are more like academic departments, with rival faculties competing for students and funding. Even wizards who train apprentices in the solitude of their own towers use the division of magic into schools as a learning device, since the spells of each school require mastery of different techniques.', 0, 'Ability' );
INSERT INTO ability VALUES ( 263, 'Spell Mastery', 'At 18th level, you have achieved such mastery over certain spells that you can cast them at will. Choose a 1st-level wizard spell and a 2nd-level wizard spell that are in your spellbook. You can cast those spells at their lowest level without expending a spell slot when you have them prepared. If you want to cast either spell at a higher level, you must expend a spell slot as normal.<br/><br/>By spending 8 hours in study, you can exchange one or both of the spells you chose for different spells of the same levels.', 0, 'Ability' );
INSERT INTO ability VALUES ( 264, 'Signature Spells', 'When you reach 20th level, you gain mastery over two powerful spells and can cast them with little effort. Choose two 3rd-level wizard spells in your spellbook as your signature spells. You always have these spells prepared, they don''t count against the number of spells you have prepared, and you can cast each of them once at 3rd level without expending a spell slot. When you do so, you can''t do so again until you finish a short or long rest.<br/><br/>If you want to cast either spell at a higher level, you must expend a spell slot as normal.', 0, 'Ability' );
INSERT INTO ability VALUES ( 265, 'School of Evocation', 'You focus your study on magic that creates powerful elemental effects such as bitter cold, searing flame, rolling thunder, crackling lightning, and burning acid. Some evokers find employment in military forces, serving as artillery to blast enemy armies from afar. Others use their spectacular power to protect the weak, while some seek their own gain as bandits, adventurers, or aspiring tyrants.', 0, 'Ability' );
INSERT INTO ability VALUES ( 266, 'Evocation - Evocation Savant', 'Beginning when you select this school at 2nd level, the gold and time you must spend to copy an evocation spell into your spellbook is halved.', 0, 'Ability' );
INSERT INTO ability VALUES ( 267, 'Evocation - Sculpt Spells', 'Beginning at 2nd level, you can create pockets of relative safety within the effects of your evocation spells. When you cast an evocation spell that affects other creatures that you can see, you can choose a number of them equal to 1 + the spell''s level. The chosen creatures automatically succeed on their saving throws against the spell, and they take no damage if they would normally take half damage on a successful save.', 0, 'Ability' );
INSERT INTO ability VALUES ( 268, 'Evocation - Potent Cantrip', 'Starting at 6th level, your damaging cantrips affect even creatures that avoid the brunt of the effect. When a creature succeeds on a saving throw against your cantrip, the creature takes half the cantrip''s damage (if any) but suffers no additional effect from the cantrip.', 0, 'Ability' );
INSERT INTO ability VALUES ( 269, 'Evocation - Empowered Evocation', 'Beginning at 10th level, you can add your Intelligence modifier to one damage roll of any wizard evocation spell you cast.', 0, 'Ability' );
INSERT INTO ability VALUES ( 270, 'Evocation - Overchannel', 'Starting at 14th level, you can increase the power of your simpler spells. When you cast a wizard spell of 1st through 5th level that deals damage, you can deal maximum damage with that spell.<br/><br/>The first time you do so, you suffer no adverse effect. If you use this feature again before you finish a long rest, you take 2d12 necrotic damage for each level of the spell, immediately after you cast it. Each time you use this feature again before finishing a long rest, the necrotic damage per spell level increases by 1d12. This damage ignores resistance and immunity.', 0, 'Ability' );
