INSERT INTO good VALUES (33, 'Component pouch', 'A component pouch is a small, watertight leather belt pouch that has compartments to hold all the material components and other special items you need to cast your spells, except for those components that have a specific cost (as indicated in a spell''s description).', 25, 2, 0, 0 );
INSERT INTO good VALUES (34, 'Crowbar', 'Using a crowbar grants advantage to Strength checks where the crowbar''s leverage can be applied.', 2, 5, 0, 0 );
INSERT INTO good VALUES (35, 'Druidic Focus - Sprig of mistletoe', 'A druidic focus might be a sprig of mistletoe or holly, a wand or scepter made of yew or another special wood, a staff drawn whole out of a living tree, or a totem object incorporating feathers, fur, bones, and teeth from sacred animals. A druid can use such an object as a spellcasting focus.', 1, 0, 0, 0 );
INSERT INTO good VALUES (36, 'Druidic Focus - Totem', 'A druidic focus might be a sprig of mistletoe or holly, a wand or scepter made of yew or another special wood, a staff drawn whole out of a living tree, or a totem object incorporating feathers, fur, bones, and teeth from sacred animals. A druid can use such an object as a spellcasting focus.', 1, 0, 0, 0 );
