INSERT INTO good VALUES (68, 'Piton', '', 0.05, 0.25, 0, 0 );
INSERT INTO good VALUES (69, 'Poison, basic (vial)', 'You can use the poison in this vial to coat one slashing or piercing weapon or up to three pieces of ammunition. Applying the poison takes an action. A creature hit by the poisoned weapon or ammunition must make a DC 10 Constitution saving throw or take 1d4 poison damage. Once applied, the poison retains potency for 1 minute before drying.', 100, 0, 1, 0 );
INSERT INTO good VALUES (70, 'Pole (10-foot)', '', 0.05, 7, 0, 0 );
INSERT INTO good VALUES (71, 'Pot, iron', '', 2, 10, 0, 0 );
INSERT INTO good VALUES (72, 'Potion of healing', 'A character who drinks the magical red fluid in this vial regains 2d4 + 2 hit points. Drinking or administering a potion takes an action.', 50, 0.5, 1, 2 );
INSERT INTO good VALUES (73, 'Pouch', 'A cloth or leather pouch can hold up to 20 sling bullets or 50 blowgun needles, among other things. A compartmentalized pouch for holding spell components is called a component pouch (described earlier in this section).', 0.5, 1, 0, 0 );
INSERT INTO good VALUES (74, 'Quiver', 'A quiver can hold up to 20 arrows.', 1, 1, 0, 0 );
INSERT INTO good VALUES (75, 'Ram, portable', 'You can use a portable ram to break down doors. When doing so, you gain a +4 bonus on the Strength check. One other character can help you use the ram, giving you advantage on this check.', 4, 35, 0, 0 );
INSERT INTO good VALUES (76, 'Rations (1 day)', 'Rations consist of dry foods suitable for extended travel, including jerky, dried fruit, hardtack, and nuts.', 0.5, 2, 0, 0 );
