INSERT INTO ability VALUES ( 1228, 'Spell-Like Ability', 'Undines can cast hydraulic push once per day as a spell-like ability (with a caster level equal to the undine''s character level).', 2, 'Ability' );
INSERT INTO ability VALUES ( 1229, 'Energy Resistance', 'Undines have cold resistance 5.', 0, 'Ability' );
INSERT INTO ability VALUES ( 1230, 'Water Affinity', 'Undine sorcerers with the elemental (water) bloodline treat their Charisma scores as 2 points higher for the purposes of all sorcerer spells and class abilities. Undine clerics with the Water domain cast their Water domain powers and spells at +1 caster level.', 0, 'Ability' );
INSERT INTO ability VALUES ( 1231, '+2 Dexterity, +2 Wisdom, -2 Charisma:', 'Vanaras are agile and perceptive, but are also rather impish and mischievous.', 0, 'Ability' );
INSERT INTO ability VALUES ( 1232, 'Vanara', 'Vanaras are humanoids with the vanara subtype.', 0, 'Ability' );
INSERT INTO ability VALUES ( 1233, 'Nimble', 'Vanaras have a +2 racial bonus on Acrobatics and Stealth checks.', 0, 'Ability' );
INSERT INTO ability VALUES ( 1234, 'Climb speed', 'Vanaras have a base speed of 30 feet and a Climb speed of 20 feet.', 0, 'Ability' );
INSERT INTO ability VALUES ( 1235, 'Prehensile Tail', 'A vanara has a long, flexible tail that she can use to carry objects. She cannot wield weapons with her tail, but the tail allows her to retrieve a small, stowed object carried on her person as a swift action.', 0, 'Ability' );
INSERT INTO ability VALUES ( 1236, '+2 Dexterity, +2 Charisma, -2 Wisdom', 'Vishkanyas are graceful but often irrational.', 0, 'Ability' );
INSERT INTO ability VALUES ( 1237, 'Vishkanya', 'Vishkanyas are humanoids with the vishkanya subtype.', 0, 'Ability' );
INSERT INTO ability VALUES ( 1238, 'Poison Resistance', 'A vishkanya has a racial bonus on saving throws against poison equal to its Hit Dice.', 0, 'Ability' );
INSERT INTO ability VALUES ( 1239, 'Limber', 'Vishkanyas receive a +2 racial bonus on Escape Artist and Stealth checks.', 0, 'Ability' );
INSERT INTO ability VALUES ( 1240, 'Toxic', 'A number of times per day equal to his Constitution modifier (minimum 1/day), a vishkanya can envenom a weapon that he wields with his toxic saliva or blood (using blood requires the vishkanya to be injured when he uses this ability). Applying venom in this way is a swift action. Vishkanya Venom: Weapon-injury, save Fort DC 10 + 1/2 the vishkanya''s Hit Dice + the vishkanya''s Constitution modifier, frequency 1/round for 6 rounds, effect 1d2 Dex, cure 1 save.', 0, 'Ability' );
INSERT INTO ability VALUES ( 1241, 'Weapon Familiarity', 'Vishkanyas are always proficient with blowguns, kukri, and shuriken.', 0, 'Ability' );
INSERT INTO ability VALUES ( 1242, '+2 Dexterity, +2 Intelligence, -2 Wisdom', 'Wayangs are nimble and cagey, but their perception of the world is clouded by shadows.', 0, 'Ability' );
INSERT INTO ability VALUES ( 1243, 'Wayang', 'Wayangs are humanoids with the wayang subtype.', 0, 'Ability' );
INSERT INTO ability VALUES ( 1244, 'Light and Dark', 'Once per day as an immediate action, a wayang can treat positive and negative energy effects as if she were an undead creature, taking damage from positive energy and being healed of damage by negative energy. This ability lasts for 1 minute once activated.', 0, 'Ability' );
INSERT INTO ability VALUES ( 1245, 'Lurker', 'Wayangs gain a +2 racial bonus on Perception and Stealth checks.', 0, 'Ability' );
INSERT INTO ability VALUES ( 1246, 'Shadow Magic', 'Wayangs add 1 to the DC of any saving throws to resist spells of the shadow subschool that they cast. Wayangs with a Charisma score of 11 or higher also gain the following spell-like abilities: 1/day-ghost sound, pass without trace, and ventriloquism. The caster level for these effects is equal to the wayang''s character level. The DCs of these spells are equal to 10 + the spell''s level + the wayang''s Charisma modifier.', 2, 'Ability' );
INSERT INTO ability VALUES ( 1247, 'Shadow Resistance', 'Wayangs get a +2 racial bonus on saving throws against spells of the shadow subschool.', 0, 'Ability' );
