INSERT INTO ability VALUES ( 1, 'Ability Score Increase (Con +2, Wis +2, Cha -2)', 'Constitution +2, Wisdom +2, Charisma -2.', 0, 'Ability' );
INSERT INTO ability VALUES ( 2, 'Darkvision', 'Character can see in the dark up to 60 feet. If she already has darkvision, the range increases by 30 feet.', 1, 'Ability' );
INSERT INTO ability VALUES ( 3, 'Defensive Training Dwarves', 'Dwarves get a +4 dodge bonus to AC against monsters of the giant subtype.', 0, 'Ability' );
INSERT INTO ability VALUES ( 4, 'Greed', 'Dwarves receive a +2 racial bonus on Appraise skill checks made to determine the price of nonmagical goods that contain precious metals or gemstones.', 0, 'Ability' );
INSERT INTO ability VALUES ( 5, 'Hatred Dwarves', 'Dwarves receive a +1 bonus on attack rolls against humanoid creatures of the orc and goblinoid subtypes due to special training against these hated foes.', 0, 'Ability' );
INSERT INTO ability VALUES ( 6, 'Hardy', 'Dwarves receive a +2 racial bonus on saving throws against poison, spells, and spell-like abilities.', 0, 'Ability' );
INSERT INTO ability VALUES ( 7, 'Stability', 'Dwarves receive a +4 racial bonus to their Combat Maneuver Defense when resisting a bull rush or trip attempt while standing on the ground.', 0, 'Ability' );
INSERT INTO ability VALUES ( 8, 'Ability Score Increase (Dex +2, Int +2, Con -2)', 'Dexterity +2, Intelligence +2, Constitution -2.', 0, 'Ability' );
INSERT INTO ability VALUES ( 9, 'Low-Light Vision', 'Characters can see twice as far as humans in conditions of dim light. See Additional Rules.', 0, 'Ability' );
INSERT INTO ability VALUES ( 10, 'Elven Immunities', 'Characters are immune to magic sleep effects and get a +2 racial saving throw bonus against enchantment spells and effects.', 0, 'Ability' );
INSERT INTO ability VALUES ( 11, 'Elven Magic', 'Elves receive a +2 racial bonus on caster level checks made to overcome spell resistance. In addition, elves receive a +2 racial bonus on Spellcraft skill checks made to identify the properties of magic items.', 0, 'Ability' );
INSERT INTO ability VALUES ( 12, 'Keen Senses', 'Characters receive a +2 racial bonus on Perception skill checks.', 0, 'Ability' );
INSERT INTO ability VALUES ( 13, 'Ability Score Increase (Con +2, Cha +2, Str -2)', 'Constitution +2, Charisma +2, Strength -2.', 0, 'Ability' );
INSERT INTO ability VALUES ( 15, 'Defensive Training Gnomes', 'Gnomes get a +4 dodge bonus to AC against monsters of the giant type.', 0, 'Ability' );
INSERT INTO ability VALUES ( 16, 'Gnome Magic', 'Gnomes add +1 to the DC of any saving throws against illusion spells that they cast. Gnomes with a Charisma of 11 or higher also gain the following spell-like abilities: 1/day-dancing lights, ghost sound, prestidigitation, and speak with animals. The caster level for these effects is equal to the gnome''''s level. The DC for these spells is equal to 10 + the spell''''s level + the gnome''''s Charisma modifier.', 0, 'Ability' );
INSERT INTO ability VALUES ( 17, 'Hatred Gnomes', 'Gnomes receive a +1 bonus on attack rolls against humanoid creatures of the reptilian and goblinoid subtypes due to special training against these hated foes.', 0, 'Ability' );
INSERT INTO ability VALUES ( 18, 'Illusion Resistance', 'Gnomes get a +2 racial saving throw bonus against illusion spells or effects.', 0, 'Ability' );
INSERT INTO ability VALUES ( 20, 'Obsessive', 'Gnomes receive a +2 racial bonus on a Craft or Profession skill of their choice.', 0, 'Ability' );
INSERT INTO ability VALUES ( 21, 'Ability Score Increase (One Ability Score +2)', 'One Ability Score +2.', 0, 'Ability' );
INSERT INTO ability VALUES ( 23, 'Adaptability', 'Half-elves receive Skill Focus as a bonus feat at 1st level.', 0, 'Ability' );
INSERT INTO ability VALUES ( 24, 'Elf Blood', 'Half-elves count as both elves and humans for any effect related to race.', 0, 'Ability' );
INSERT INTO ability VALUES ( 27, 'Multitalented', 'Half-elves choose two favored classes at first level and gain +1 hit point or +1 skill point whenever they take a level in either one of those classes. See Classes for more information about favored classes.', 0, 'Ability' );
INSERT INTO ability VALUES ( 30, 'Intimidating', 'Half-orcs receive a +2 racial bonus on Intimidate skill checks due to their fearsome nature.', 0, 'Ability' );
INSERT INTO ability VALUES ( 31, 'Orc Blood', 'Half-orcs count as both humans and orcs for any effect related to race.', 0, 'Ability' );
INSERT INTO ability VALUES ( 32, 'Orc Ferocity', 'Once per day, when a half-orc is brought below 0 hit points but not killed, he can fight on for one more round as if disabled. At the end of his next turn, unless brought to above 0 hit points, he immediately falls unconscious and begins dying.', 0, 'Ability' );
INSERT INTO ability VALUES ( 33, 'Ability Score Increase (Dex +2, Cha +2, Str -2)', 'Dexterity +2, Charisma +2, Strength -2.', 0, 'Ability' );
INSERT INTO ability VALUES ( 34, 'Fearless', 'Halflings receive a +2 racial bonus on all saving throws against fear. This bonus stacks with the bonus granted by halfling luck.', 0, 'Ability' );
INSERT INTO ability VALUES ( 35, 'Halfling Luck', 'Halflings receive a +1 racial bonus on all saving throws.', 0, 'Ability' );
INSERT INTO ability VALUES ( 37, 'Sure-Footed', 'Halflings receive a +2 racial bonus on Acrobatics and Climb skill checks.', 0, 'Ability' );
INSERT INTO ability VALUES ( 39, 'Bonus Feat - Human', 'Humans select one extra feat at 1st level.', 0, 'ExtraFeatsAbility' );
INSERT INTO ability VALUES ( 40, 'Skilled', 'Humans gain an additional skill rank at first level and one additional rank whenever they gain a level.', 0, 'ExtraSkillPointsAbility' );
INSERT INTO ability VALUES ( 41, 'Stonecunning', 'Dwarves receive a +2 bonus on Perception checks to potentially notice unusual stonework, such as traps and hidden doors located in stone walls or floors. They receive a check to notice such features whenever they pass within 10 feet of them, whether or not they are actively looking.', 0, 'Ability' );
INSERT INTO ability VALUES ( 42, 'Fast Movement - Barbarian', 'A barbarian''s land speed is faster than the norm for her race by +10 feet. This benefit applies only when she is wearing no armor, light armor, or medium armor, and not carrying a heavy load. Apply this bonus before modifying the barbarian''s speed because of any load carried or armor worn. This bonus stacks with any other bonuses to the barbarian''s land speed.', 1, 'Ability' );
INSERT INTO ability VALUES ( 43, 'Rage', 'A barbarian can call upon inner reserves of strength and ferocity, granting her additional combat prowess. Starting at 1st level, a barbarian can rage for a number of rounds per day equal to 4 + her Constitution modifier. At each level after 1st, she can rage for 2 additional rounds. Temporary increases to Constitution, such as those gained from rage and spells like bear''s endurance, do not increase the total number of rounds that a barbarian can rage per day. A barbarian can enter rage as a free action. The total number of rounds of rage per day is renewed after resting for 8 hours, although these hours do not need to be consecutive.<br/>While in rage, a barbarian gains a +4 morale bonus to her Strength and Constitution, as well as a +2 morale bonus on Will saves. In addition, she takes a -2 penalty to Armor Class. The increase to Constitution grants the barbarian 2 hit points per Hit Dice, but these disappear when the rage ends and are not lost first like temporary hit points. While in rage, a barbarian cannot use any Charisma-, Dexterity-, or Intelligence-based skills (except Acrobatics, Fly, Intimidate, and Ride) or any ability that requires patience or concentration.<br/>A barbarian can end her rage as a free action and is fatigued after rage for a number of rounds equal to 2 times the number of rounds spent in the rage. A barbarian cannot enter a new rage while fatigued or exhausted but can otherwise enter rage multiple times during a single encounter or combat. If a barbarian falls unconscious, her rage immediately ends, placing her in peril of death.', 1, 'Ability' );
INSERT INTO ability VALUES ( 44, 'Rage Powers', 'As a barbarian gains levels, she learns to use her rage in new ways. Starting at 2nd level, a barbarian gains a rage power. She gains another rage power for every two levels of barbarian attained after 2nd level. A barbarian gains the benefits of rage powers only while raging, and some of these powers require the barbarian to take an action first. Unless otherwise noted, a barbarian cannot select an individual power more than once.', 1, 'Ability' );
INSERT INTO ability VALUES ( 45, 'Animal Fury', 'While raging, the barbarian gains a bite attack. If used as part of a full attack action, the bite attack is made at the barbarian''s full base attack bonus -5. If the bite hits, it deals 1d4 points of damage (assuming the barbarian is Medium, 1d3 points of damage if Small) plus half the barbarian''s Strength modifier. A barbarian can make a bite attack as part of the action to maintain or break free from a grapple. This attack is resolved before the grapple check is made. If the bite attack hits, any grapple checks made by the barbarian against the target this round are at a +2 bonus.', 1, 'Ability' );
INSERT INTO ability VALUES ( 46, 'Clear Mind', 'A barbarian may reroll a failed Will save. This power is used as an immediate action after the first save is attempted, but before the results are revealed by the GM. The barbarian must take the second result, even if it is worse. A barbarian must be at least 8th level before selecting this power. This power can only be used once per rage.', 1, 'Ability' );
INSERT INTO ability VALUES ( 47, 'Fearless Rage', 'While raging, the barbarian is immune to the shaken and frightened conditions. A barbarian must be at least 12th level before selecting this rage power.', 1, 'Ability' );
INSERT INTO ability VALUES ( 48, 'Guarded Stance', 'The barbarian gains a +1 dodge bonus to her Armor Class against melee attacks for a number of rounds equal to the barbarian''s current Constitution modifier (minimum 1). This bonus increases by +1 for every 6 levels the barbarian has attained. Activating this ability is a move action that does not provoke an attack of opportunity.', 1, 'Ability' );
INSERT INTO ability VALUES ( 49, 'Increased Damage Reduction', 'The barbarian''s damage reduction increases by 1/-. This increase is always active while the barbarian is raging. A barbarian can select this rage power up to three times. Its effects stack. A barbarian must be at least 8th level before selecting this rage power.', 1, 'Ability' );
INSERT INTO ability VALUES ( 50, 'Internal Fortitude', 'While raging, the barbarian is immune to the sickened and nauseated conditions. A barbarian must be at least 8th level before selecting this rage power.', 1, 'Ability' );
INSERT INTO ability VALUES ( 51, 'Intimidating Glare', 'The barbarian can make an Intimidate check against one adjacent foe as a move action. If the barbarian successfully demoralizes her opponent, the foe is shaken for 1d4 rounds + 1 round for every 5 points by which the barbarian''s check exceeds the DC.', 1, 'Ability' );
INSERT INTO ability VALUES ( 52, 'Knockback', 'Once per round, the barbarian can make a bull rush attempt against one target in place of a melee attack. If successful, the target takes damage equal to the barbarian''s Strength modifier and is moved back as normal. The barbarian does not need to move with the target if successful. This does not provoke an attack of opportunity.', 1, 'Ability' );
INSERT INTO ability VALUES ( 54, 'Mighty Swing', 'The barbarian automatically confirms a critical hit. This power is used as an immediate action once a critical threat has been determined. A barbarian must be at least 12th level before selecting this power. This power can only be used once per rage.', 1, 'Ability' );
INSERT INTO ability VALUES ( 55, 'Moment of Clarity', 'The barbarian does not gain any benefits or take any of the penalties from rage for 1 round. Activating this power is a swift action. This includes the penalty to Armor Class and the restriction on what actions can be performed. This round still counts against her total number of rounds of rage per day. This power can only be used once per rage.', 1, 'Ability' );
INSERT INTO ability VALUES ( 56, 'Night Vision', 'The barbarian''s senses grow incredibly sharp while raging and she gains darkvision 60 feet. A barbarian must have low-light vision as a rage power or a racial trait to select this rage power.', 1, 'Ability' );
INSERT INTO ability VALUES ( 57, 'No Escape', 'The barbarian can move up to double her normal speed as an immediate action but she can only use this ability when an adjacent foe uses a withdraw action to move away from her. She must end her movement adjacent to the enemy that used the withdraw action. The barbarian provokes attacks of opportunity as normal during this movement. This power can only be used once per rage.', 1, 'Ability' );
INSERT INTO ability VALUES ( 58, 'Powerful Blow', 'The barbarian gains a +1 bonus on a single damage roll. This bonus increases by +1 for every 4 levels the barbarian has attained. This power is used as a swift action before the roll to hit is made. This power can only be used once per rage.', 1, 'Ability' );
INSERT INTO ability VALUES ( 59, 'Quick Reflexes', 'While raging, the barbarian can make one additional attack of opportunity per round.', 1, 'Ability' );
INSERT INTO ability VALUES ( 60, 'Raging Climber', 'When raging, the barbarian adds her level as an enhancement bonus on all Climb skill checks.', 1, 'Ability' );
INSERT INTO ability VALUES ( 61, 'Raging Leaper', 'When raging, the barbarian adds her level as an enhancement bonus on all Acrobatics skill checks made to jump. When making a jump in this way, the barbarian is always considered to have a running start.', 1, 'Ability' );
INSERT INTO ability VALUES ( 62, 'Raging Swimmer', 'When raging, the barbarian adds her level as an enhancement bonus on all Swim skill checks.', 1, 'Ability' );
INSERT INTO ability VALUES ( 63, 'Renewed Vigor', 'As a standard action, the barbarian heals 1d8 points of damage + her Constitution modifier. For every four levels the barbarian has attained above 4th, this amount of damage healed increases by 1d8, to a maximum of 5d8 at 20th level. A barbarian must be at least 4th level before selecting this power. This power can be used only once per day and only while raging.', 1, 'Ability' );
INSERT INTO ability VALUES ( 64, 'Rolling Dodge', 'The barbarian gains a +1 dodge bonus to her Armor Class against ranged attacks for a number of rounds equal to the barbarian''s current Constitution modifier (minimum 1). This bonus increases by +1 for every 6 levels the barbarian has attained. Activating this ability is a move action that does not provoke an attack of opportunity.', 1, 'Ability' );
INSERT INTO ability VALUES ( 65, 'Roused Anger', 'The barbarian may enter a rage even if fatigued. While raging after using this ability, the barbarian is immune to the fatigued condition. Once this rage ends, the barbarian is exhausted for 10 minutes per round spent raging.', 1, 'Ability' );
INSERT INTO ability VALUES ( 66, 'Scent', 'The barbarian gains the scent ability while raging and can use this ability to locate unseen foes (see scent ability).', 1, 'Ability' );
INSERT INTO ability VALUES ( 67, 'Strength Surge', 'The barbarian adds her barbarian level on one Strength check or combat maneuver check, or to her Combat Maneuver Defense when an opponent attempts a maneuver against her. This power is used as an immediate action. This power can only be used once per rage.', 1, 'Ability' );
INSERT INTO ability VALUES ( 68, 'Superstition', 'The barbarian gains a +2 morale bonus on saving throws made to resist spells, supernatural abilities, and spell-like abilities. This bonus increases by +1 for every 4 levels the barbarian has attained. While raging, the barbarian cannot be a willing target of any spell and must make saving throws to resist all spells, even those cast by allies.', 1, 'Ability' );
INSERT INTO ability VALUES ( 69, 'Surprise Accuracy', 'The barbarian gains a +1 morale bonus on one attack roll. This bonus increases by +1 for every 4 levels the barbarian has attained. This power is used as a swift action before the roll to hit is made. This power can only be used once per rage.', 1, 'Ability' );
INSERT INTO ability VALUES ( 70, 'Swift Foot', 'The barbarian gains a 5-foot enhancement bonus to her speed. This increase is always active while the barbarian is raging. A barbarian can select this rage power up to three times. Its effects stack. ', 1, 'Ability' );
INSERT INTO ability VALUES ( 71, 'Terrifying Howl', 'The barbarian unleashes a terrifying howl as a standard action. All shaken enemies within 30 feet must make a Will save (DC equal to 10 + 1/2 the barbarian''s level + the barbarian''s Strength modifier) or be panicked for 1d4+1 rounds. Once an enemy has made a save versus terrifying howl (successful or not), it is immune to this power for 24 hours. A barbarian must have the intimidating glare rage power to select this rage power. A barbarian must be at least 8th level before selecting this power.', 1, 'Ability' );
INSERT INTO ability VALUES ( 72, 'Unexpected Strike', 'The barbarian can make an attack of opportunity against a foe that moves into any square threatened by the barbarian, regardless of whether or not that movement would normally provoke an attack of opportunity. This power can only be used once per rage. A barbarian must be at least 8th level before selecting this power.', 1, 'Ability' );
INSERT INTO ability VALUES ( 73, 'Uncanny Dodge', 'A character gains the ability to react to danger before her senses would normally allow her to do so. She cannot be caught flat-footed, even if the attacker is invisible. She still loses her Dexterity bonus to AC if immobilized. A characer with this ability can still lose her Dexterity bonus to AC if an opponent successfully uses the feint action against her.<br/>If a character already has uncanny dodge from a different class, she automatically gains improved uncanny dodge instead.', 1, 'Ability' );
INSERT INTO ability VALUES ( 74, 'Trap Sense', 'A character gains a +1 bonus on Reflex saves made to avoid traps and a +1 dodge bonus to AC against attacks made by traps. These bonuses increase by +1 every three class levels thereafter (6th, 9th, 12th, 15th, and 18th level). Trap sense bonuses gained from multiple classes stack.', 1, 'Ability' );
INSERT INTO ability VALUES ( 75, 'Improved Uncanny Dodge', 'The character can no longer be flanked. This defense denies a rogue the ability to sneak attack the character by flanking her, unless the attacker has at least four more rogue levels than the target has class levels.<br/>If a character already has uncanny dodge from another class, the levels from the classes that grant uncanny dodge stack to determine the minimum rogue level required to flank the character.', 1, 'Ability' );
INSERT INTO ability VALUES ( 76, 'Damage Reduction', 'At 7th level, a barbarian gains damage reduction. Subtract 1 from the damage the barbarian takes each time she is dealt damage from a weapon or a natural attack. At 10th level, and every three barbarian levels thereafter (13th, 16th, and 19th level), this damage reduction rises by 1 point. Damage reduction can reduce damage to 0 but not below 0.', 1, 'Ability' );
INSERT INTO ability VALUES ( 77, 'Greater Rage', 'At 11th level, when a barbarian enters rage, the morale bonus to her Strength and Constitution increases to +6 and the morale bonus on her Will saves increases to +3.', 1, 'Ability' );
INSERT INTO ability VALUES ( 78, 'Indomitable Will', 'While in rage, a barbarian of 14th level or higher gains a +4 bonus on Will saves to resist enchantment spells. This bonus stacks with all other modifiers, including the morale bonus on Will saves she also receives during her rage.', 1, 'Ability' );
INSERT INTO ability VALUES ( 79, 'Tireless Rage', 'Starting at 17th level, a barbarian no longer becomes fatigued at the end of her rage.', 1, 'Ability' );
INSERT INTO ability VALUES ( 80, 'Mighty Rage', 'At 20th level, when a barbarian enters rage, the morale bonus to her Strength and Constitution increases to +8 and the morale bonus on her Will saves increases to +4.', 1, 'Ability' );
INSERT INTO ability VALUES ( 81, 'Spells - Bard', 'A bard casts arcane spells drawn from the bard spell list presented in verbal component (song, recitation, or music). To learn or cast a spell, a bard must have a Charisma score equal to at least 10 + the spell level. The Difficulty Class for a saving throw against a bard''s spell is 10 + the spell level + the bard''s Charisma modifier.<br/>Like other spellcasters, a bard can cast only a certain number of spells of each spell level per day. His base daily spell allotment is given on Table: Bard. In addition, he receives bonus spells per day if he has a high Charisma score (see Table: Ability Modifiers and Bonus Spells).<br/>The bard''s selection of spells is extremely limited. A bard begins play knowing four 0-level spells and two 1st-level spells of the bard''s choice. At each new bard level, he gains one or more new spells, as indicated on Table: Bard Spells Known. (Unlike spells per day, the number of spells a bard knows is not affected by his Charisma score. The numbers on Table: Bard Spells Known are fixed.)<br/>Upon reaching 5th level, and at every third bard level after that (8th, 11th, and so on), a bard can choose to learn a new spell in place of one he already knows. In effect, the bard ''loses'' the old spell in exchange for the new one. The new spell''s level must be the same as that of the spell being exchanged, and it must be at least one level lower than the highest-level bard spell the bard can cast. A bard may swap only a single spell at any given level and must choose whether or not to swap the spell at the same time that he gains new spells known for the level.<br/>A bard need not prepare his spells in advance. He can cast any spell he knows at any time, assuming he has not yet used up his allotment of spells per day for the spell''s level. ', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 82, 'Bardic Knowledge', 'A character adds half his class level (minimum 1) to all Knowledge skill checks and may make all Knowledge skill checks untrained. Levels in this class stack with levels in any other class that grants a similar ability.', 1, 'Ability' );
INSERT INTO ability VALUES ( 83, 'Bardic Performance - Bard', 'A bard is trained to use the Perform skill to create magical effects on those around him, including himself if desired. He can use this ability for a number of rounds per day equal to 4 + his Charisma modifier. At each level after 1st a bard can use bardic performance for 2 additional rounds per day. Each round, the bard can produce any one of the types of bardic performance that he has mastered, as indicated by his level. <br/>Starting a bardic performance is a standard action, but it can be maintained each round as a free action. Changing a bardic performance from one effect to another requires the bard to stop the previous performance and start a new one as a standard action. A bardic performance cannot be disrupted, but it ends immediately if the bard is killed, paralyzed, stunned, knocked unconscious, or otherwise prevented from taking a free action to maintain it each round. A bard cannot have more than one bardic performance in effect at one time.<br/>At 7th level, a bard can start a bardic performance as a move action instead of a standard action. At 13th level, a bard can start a bardic performance as a swift action. <br/>Each bardic performance has audible components, visual components, or both.<br/>If a bardic performance has audible components, the targets must be able to hear the bard for the performance to have any effect, and such performances are language dependent. A deaf bard has a 20% chance to fail when attempting to use a bardic performance with an audible component. If he fails this check, the attempt still counts against his daily limit. Deaf creatures are immune to bardic performances with audible components.<br/>If a bardic performance has a visual component, the targets must have line of sight to the bard for the performance to have any effect. A blind bard has a 50% chance to fail when attempting to use a bardic performance with a visual component. If he fails this check, the attempt still counts against his daily limit. Blind creatures are immune to bardic performances with visual components.', 0, 'Ability' );
INSERT INTO ability VALUES ( 84, 'Countersong', 'At 1st level, a bard learns to counter magic effects that depend on sound (but not spells that have verbal components). Each round of the countersong he makes a Perform (keyboard, percussion, wind, string, or sing) skill check. Any creature within 30 feet of the bard (including the bard himself) that is affected by a sonic or language-dependent magical attack may use the bard''s Perform check result in place of its saving throw if, after the saving throw is rolled, the Perform check result proves to be higher. If a creature within range of the countersong is already under the effect of a noninstantaneous sonic or language-dependent magical attack, it gains another saving throw against the effect each round it hears the countersong, but it must use the bard''s Perform skill check result for the save. Countersong does not work on effects that don''t allow saves. Countersong relies on audible components.', 3, 'Ability' );
INSERT INTO ability VALUES ( 85, 'Distraction', 'At 1st level, a bard can use his performance to counter magic effects that depend on sight. Each round of the distraction, he makes a Perform (act, comedy, dance, or oratory) skill check. Any creature within 30 feet of the bard (including the bard himself) that is affected by an illusion (pattern) or illusion (figment) magical attack may use the bard''s Perform check result in place of its saving throw if, after the saving throw is rolled, the Perform skill check proves to be higher. If a creature within range of the distraction is already under the effect of a noninstantaneous illusion (pattern) or illusion (figment) magical attack, it gains another saving throw against the effect each round it sees the distraction, but it must use the bard''s Perform skill check result for the save. Distraction does not work on effects that don''t allow saves. Distraction relies on visual components.', 3, 'Ability' );
INSERT INTO ability VALUES ( 86, 'Fascinate', 'At 1st level, a bard can use his performance to cause one or more creatures to become fascinated with him. Each creature to be fascinated must be within 90 feet, able to see and hear the bard, and capable of paying attention to him. The bard must also be able to see the creatures affected. The distraction of a nearby combat or other dangers prevents this ability from working. For every three levels the bard has attained beyond 1st, he can target one additional creature with this ability.<br/>Each creature within range receives a Will save (DC 10 + 1/2 the bard''s level + the bard''s Cha modifier) to negate the effect. If a creature''s saving throw succeeds, the bard cannot attempt to fascinate that creature again for 24 hours. If its saving throw fails, the creature sits quietly and observes the performance for as long as the bard continues to maintain it. While fascinated, a target takes a -4 penalty on all skill checks made as reactions, such as Perception checks. Any potential threat to the target allows the target to make a new saving throw against the effect. Any obvious threat, such as someone drawing a weapon, casting a spell, or aiming a weapon at the target, automatically breaks the effect.<br/>Fascinate is an enchantment (compulsion), mind-affecting ability. Fascinate relies on audible and visual components in order to function. ', 3, 'Ability' );
INSERT INTO ability VALUES ( 87, 'Inspire Courage', 'A 1st-level bard can use his performance to inspire courage in his allies (including himself), bolstering them against fear and improving their combat abilities. To be affected, an ally must be able to perceive the bard''s performance. An affected ally receives a +1 morale bonus on saving throws against charm and fear effects and a +1 competence bonus on attack and weapon damage rolls. At 5th level, and every six bard levels thereafter, this bonus increases by +1, to a maximum of +4 at 17th level. Inspire courage is a mind-affecting ability. Inspire courage can use audible or visual components. The bard must choose which component to use when starting his performance.', 3, 'Ability' );
INSERT INTO ability VALUES ( 88, 'Inspire Competence', 'A bard of 3rd level or higher can use his performance to help an ally succeed at a task. That ally must be within 30 feet and be able to hear the bard. The ally gets a +2 competence bonus on skill checks with a particular skill as long as she continues to hear the bard''s performance. This bonus increases by +1 for every four levels the bard has attained beyond 3rd (+3 at 7th, +4 at 11th, +5 at 15th, and +6 at 19th). Certain uses of this ability are infeasible, such as Stealth, and may be disallowed at the GM''s discretion. A bard can''t inspire competence in himself. Inspire competence relies on audible components.', 3, 'Ability' );
INSERT INTO ability VALUES ( 89, 'Suggestion', 'A bard of 6th level or higher can use his performance to make a suggestion (as per the spell) to a creature he has already fascinated (see above). Using this ability does not disrupt the fascinate effect, but it does require a standard action to activate (in addition to the free action to continue the fascinate effect). A bard can use this ability more than once against an individual creature during an individual performance.<br/>Making a suggestion does not count against a bard''s daily use of bardic performance. A Will saving throw (DC 10 + 1/2 the bard''s level + the bard''s Cha modifier) negates the effect. This ability affects only a single creature. Suggestion is an enchantment (compulsion), mind affecting, language-dependent ability and relies on audible components.', 2, 'Ability' );
INSERT INTO ability VALUES ( 90, 'Dirge of Doom', 'A bard of 8th level or higher can use his performance to foster a sense of growing dread in his enemies, causing them to become shaken. To be affected, an enemy must be within 30 feet and able to see and hear the bard''s performance. The effect persists for as long as the enemy is within 30 feet and the bard continues his performance. This performance cannot cause a creature to become frightened or panicked, even if the targets are already shaken from another effect. Dirge of doom is a mind-affecting fear effect, and it relies on audible and visual components.', 3, 'Ability' );
INSERT INTO ability VALUES ( 91, 'Inspire Greatness', 'A bard of 9th level or higher can use his performance to inspire greatness in himself or a single willing ally within 30 feet, granting extra fighting capability. For every three levels the bard attains beyond 9th, he can target an additional ally while using this performance (up to a maximum of four targets at 18th level). To inspire greatness, all of the targets must be able to see and hear the bard. A creature inspired with greatness gains 2 bonus Hit Dice (d10s), the commensurate number of temporary hit points (apply the target''s Constitution modifier, if any, to these bonus Hit Dice), a +2 competence bonus on attack rolls, and a +1 competence bonus on Fortitude saves. The bonus Hit Dice count as regular Hit Dice for determining the effect of spells that are Hit Dice dependent. Inspire greatness is a mind-affecting ability and it relies on audible and visual components.', 3, 'Ability' );
INSERT INTO ability VALUES ( 92, 'Soothing Performance', 'A bard of 12th level or higher can use his performance to create an effect equivalent to a mass cure serious wounds, using the bard''s level as the caster level. In addition, this performance removes the fatigued, sickened, and shaken conditions from all those affected. Using this ability requires 4 rounds of continuous performance, and the targets must be able to see and hear the bard throughout the performance. Soothing performance affects all targets that remain within 30 feet throughout the performance. Soothing performance relies on audible and visual components.', 3, 'Ability' );
INSERT INTO ability VALUES ( 93, 'Frightening Tune', 'A bard of 14th level or higher can use his performance to cause fear in his enemies. To be affected, an enemy must be able to hear the bard perform and be within 30 feet. Each enemy within range receives a Will save (DC 10 + 1/2 the bard''s level + the bard''s Cha modifier) to negate the effect. If the save succeeds, the creature is immune to this ability for 24 hours. If the save fails, the target becomes frightened and flees for as long as the target can hear the bard''s performance. Frightening tune relies on audible components.', 2, 'Ability' );
INSERT INTO ability VALUES ( 94, 'Inspire Heroics', 'A bard of 15th level or higher can inspire tremendous heroism in himself or a single ally within 30 feet. For every three bard levels the character attains beyond 15th, he can inspire heroics in an additional creature. To inspire heroics, all of the targets must be able to see and hear the bard. Inspired creatures gain a +4 morale bonus on saving throws and a +4 dodge bonus to AC. This effect lasts for as long as the targets are able to witness the performance. Inspire heroics is a mind-affecting ability that relies on audible and visual components.', 3, 'Ability' );
INSERT INTO ability VALUES ( 95, 'Mass Suggestion', 'This ability functions just like suggestion, but allows a bard of 18th level or higher to make a suggestion simultaneously to any number of creatures that he has already fascinated. Mass suggestion is an  enchantment (compulsion), mind-affecting, language-dependent ability that relies on audible components.', 3, 'Ability' );
INSERT INTO ability VALUES ( 96, 'Deadly Performance', 'A bard of 20th level or higher can use his performance to cause one enemy to die from joy or sorrow. To be affected, the target must be able to see and hear the bard perform for 1 full round and be within 30 feet. The target receives a Will save (DC 10 + 1/2 the bard''s level + the bard''s Cha modifier) to negate the effect. If a creature''s saving throw succeeds, the target is staggered for 1d4 rounds, and the bard cannot use deadly performance on that creature again for 24 hours. If a creature''s saving throw fails, it dies. Deadly performance is a mind-affecting death effect that relies on audible and visual components.', 3, 'Ability' );
INSERT INTO ability VALUES ( 97, 'Cantrips - Bard', 'Bards learn a number of cantrips, or 0-level spells, as noted on Table: Bard Spells Known under ''Spells Known.'' These spells are cast like any other spell, but they do not consume any slots and may be used again.', 0, 'Ability' );
INSERT INTO ability VALUES ( 98, 'Versatile Performance', 'At 2nd level, a bard can choose one type of Perform skill. He can use his bonus in that skill in place of his bonus in associated skills. When substituting in this way, the bard uses his total Perform skill bonus, including class skill bonus, in place of its associated skill''s bonus, whether or not he has ranks in that skill or if it is a class skill. At 6th level, and every 4 levels thereafter, the bard can select an additional type of Perform to substitute.<br/>The types of Perform and their associated skills are: Act (Bluff, Disguise), Comedy (Bluff, Intimidate), Dance (Acrobatics, Fly), Keyboard Instruments (Diplomacy, Intimidate), Oratory (Diplomacy, Sense Motive), Percussion (Handle Animal, Intimidate), Sing (Bluff, Sense Motive), String (Bluff, Diplomacy), and Wind (Diplomacy, Handle Animal).', 1, 'Ability' );
INSERT INTO ability VALUES ( 99, 'Well-Versed', 'At 2nd level, the bard becomes resistant to the bardic performance of others, and to sonic effects in general. The bard gains a +4 bonus on saving throws made against bardic performance, sonic, and language-dependent effects. ', 1, 'Ability' );
INSERT INTO ability VALUES ( 100, 'Lore Master', 'At 5th level, the bard becomes a master of lore and can take 10 on any Knowledge skill check that he has ranks in. A bard can choose not to take 10 and can instead roll normally. In addition, once per day, the bard can take 20 on any Knowledge skill check as a standard action. He can use this ability one additional time per day for every six levels he possesses beyond 5th, to a maximum of three times per day at 17th level.', 1, 'Ability' );
INSERT INTO ability VALUES ( 101, 'Jack-of-All-Trades', 'At 10th level, the bard can use any skill, even if the skill normally requires him to be trained. At 16th level, the bard considers all skills to be class skills. At 19th level, the bard can take 10 on any skill check, even if it is not normally allowed.', 1, 'Ability' );
INSERT INTO ability VALUES ( 102, 'Aura', 'A cleric of a chaotic, evil, good, or lawful deity has a particularly powerful aura corresponding to the deity''s alignment (see the detect evil spell for details).', 1, 'Ability' );
INSERT INTO ability VALUES ( 103, 'Spells - Cleric', 'A cleric casts divine spells which are drawn from the cleric spell list presented in alignment, however, may restrict her from casting certain spells opposed to her moral or ethical beliefs, see chaotic, evil, good, and lawful spells. A cleric must choose and prepare her spells in advance.<br/>To prepare or cast a spell, a cleric must have a Wisdom score equal to at least 10 + the spell level. The Difficulty Class for a saving throw against a cleric''s spell is 10 + the spell level + the cleric''s Wisdom modifier.<br/>Like other spellcasters, a cleric can cast only a certain number of spells of each spell level per day. Her base daily spell allotment is given on Table: Cleric. In addition, she receives bonus spells per day if she has a high Wisdom score (see Table: Ability Modifiers and Bonus Spells). <br/>Clerics meditate or pray for their spells. Each cleric must choose a time when she must spend 1 hour each day in quiet contemplation or supplication to regain her daily allotment of spells. A cleric may prepare and cast any spell on the cleric spell list, provided that she can cast spells of that level, but she must choose which spells to prepare during her daily meditation.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 104, 'Channel Energy', 'Regardless of alignment, any cleric can release a wave of energy by channeling the power of her faith through her holy (or unholy) symbol. This energy can be used to cause or heal damage, depending on the type of energy channeled and the creatures targeted. <br/>A good cleric (or one who worships a good deity) channels positive energy and can choose to deal damage to undead creatures or to heal living creatures. An evil cleric (or one who worships an evil deity) channels negative energy and can choose to deal damage to living creatures or to heal undead creatures. A neutral cleric who worships a neutral deity (or one who is not devoted to a particular deity) must choose whether she channels positive or negative energy. Once this choice is made, it cannot be reversed. This decision also determines whether the cleric casts spontaneous cure or inflict spells (see spontaneous casting). <br/>Channeling energy causes a burst that affects all creatures of one type (either undead or living) in a 30-foot radius centered on the cleric. The amount of damage dealt or healed is equal to 1d6 points of damage plus 1d6 points of damage for every two cleric levels beyond 1st (2d6 at 3rd, 3d6 at 5th, and so on). Creatures that take damage from channeled energy receive a Will save to halve the damage. The DC of this save is equal to 10 + 1/2 the cleric''s level + the cleric''s Charisma modifier. Creatures healed by channeled energy cannot exceed their maximum hit point total-all excess healing is lost. A cleric may channel energy a number of times per day equal to 3 + her Charisma modifier. This is a standard action that does not provoke an attack of opportunity. A cleric can choose whether or not to include herself in this effect. A cleric must be able to present her holy symbol to use this ability. ', 3, 'Ability' );
INSERT INTO ability VALUES ( 105, 'Domains', 'A cleric''s deity influences her alignment, what magic she can perform, her values, and how others see her. A cleric chooses two domains from among those belonging to her deity. A cleric can select an alignment domain (Chaos, Evil, Good, or Law) only if her alignment matches that domain. If a cleric is not devoted to a particular deity, she still selects two domains to represent her spiritual inclinations and abilities (subject to GM approval). The restriction on alignment domains still applies.<br/>Each domain grants a number of domain powers, dependent upon the level of the cleric, as well as a number of bonus spells. A cleric gains one domain spell slot for each level of cleric spell she can cast, from 1st on up. Each day, a cleric can prepare one of the spells from her two domains in that slot. If a domain spell is not on the cleric spell list, a cleric can prepare it only in her domain spell slot. Domain spells cannot be used to cast spells spontaneously. <br/>In addition, a cleric gains the listed powers from both of her domains, if she is of a high enough level. Unless otherwise noted, using a domain power is a standard action. Cleric domains are listed at the end of this class entry.', 0, 'Ability' );
INSERT INTO ability VALUES ( 106, 'Orisons - Cleric', 'Clerics can prepare a number of orisons, or 0-level spells, each day, as noted on Table: Cleric under ''Spells per Day.'' These spells are cast like any other spell, but they are not expended when cast and may be used again.', 0, 'Ability' );
INSERT INTO ability VALUES ( 107, 'Spontaneous Casting - Cleric', 'A good cleric (or a neutral cleric of a good deity) can channel stored spell energy into healing spells that she did not prepare ahead of time. The cleric can ''lose'' any prepared spell that is not an orison or domain spell in order to cast any cure spell of the same spell level or lower (a cure spell is any spell with ''cure'' in its name). <br/>An evil cleric (or a neutral cleric who worships an evil deity) can''t convert prepared spells to cure spells but can convert them to inflict spells (an inflict spell is one with ''inflict'' in its name).<br/>A cleric who is neither good nor evil and whose deity is neither good nor evil can convert spells to either cure spells or inflict spells (player''s choice). Once the player makes this choice, it cannot be reversed. This choice also determines whether the cleric channels positive or negative energy (see Channel Energy).', 0, 'Ability' );
INSERT INTO ability VALUES ( 108, 'Chaotic, Evil, Good, and Lawful Spells', 'A character can''t cast spells of an alignment opposed to her own or her deity''s (if she has one). Spells associated with particular alignments are indicated by the chaotic, evil, good, and lawful descriptors in their spell descriptions.', 0, 'Ability' );
INSERT INTO ability VALUES ( 109, 'Bonus Languages - Cleric', 'A cleric''s bonus language options include Celestial, Abyssal, and Infernal (the languages of good, chaotic evil, and lawful evil outsiders, respectively). These choices are in addition to the bonus languages available to the character because of her race.', 0, 'Ability' );
INSERT INTO ability VALUES ( 110, 'Spells - Druid', 'A druid casts divine spells which are drawn from the druid spell list presented in alignment may restrict her from casting certain spells opposed to her moral or ethical beliefs, see Chaotic, Evil, Good, and Lawful Spells. A druid must choose and prepare her spells in advance.<br/>To prepare or cast a spell, the druid must have a Wisdom score equal to at least 10 + the spell level. The Difficulty Class for a saving throw against a druid''s spell is 10 + the spell level + the druid''s Wisdom modifier.<br/>Like other spellcasters, a druid can cast only a certain number of spells of each spell level per day. Her base daily spell allotment is given on Table: Druid. In addition, she receives bonus spells per day if she has a high Wisdom score (see Table: Ability Modifiers and Bonus Spells).<br/>A druid must spend 1 hour each day in a trance-like meditation on the mysteries of nature to regain her daily allotment of spells. A druid may prepare and cast any spell on the druid spell list, provided that she can cast spells of that level, but she must choose which spells to prepare during her daily meditation.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 111, 'Spontaneous Casting - Druid', 'A druid can channel stored spell energy into summoning spells that she hasn''t prepared ahead of time. She can ''lose'' a prepared spell in order to cast any summon nature''s ally spell of the same level or lower. ', 0, 'Ability' );
INSERT INTO ability VALUES ( 113, 'Orisons - Druid', 'Druids can prepare a number of orisons, or 0-level spells, each day, as noted on Table: Druid under ''Spells per Day.'' These spells are cast like any other spell, but they are not expended when cast and may be used again.', 0, 'Ability' );
INSERT INTO ability VALUES ( 114, 'Bonus Languages - Druid', 'A druid''s bonus language options include Sylvan, the language of woodland creatures. This choice is in addition to the bonus languages available to the character because of her race.<br/>A druid also knows Druidic, a secret language known only to druids, which she learns upon becoming a 1st-level druid. Druidic is a free language for a druid, that is, she knows it in addition to her regular allotment of languages and it doesn''t take up a language slot. Druids are forbidden to teach this language to nondruids.<br/>Druidic has its own alphabet.', 0, 'Ability' );
INSERT INTO ability VALUES ( 115, 'Nature Bond', 'At 1st level, a druid forms a bond with nature. This bond can take one of two forms. The first is a close tie to the natural world, granting the druid one of the following cleric domains: Air, Animal, Earth, Fire, Plant, Water, or Weather. When determining the powers and bonus spells granted by this domain, the druid''s effective cleric level is equal to her druid level. A druid that selects this option also receives additional domain spell slots, just like a cleric. She must prepare the spell from her domain in this slot and this spell cannot be used to cast a spell spontaneously.<br/>The second option is to form a close bond with an animal companion. A druid may begin play with any of the animals listed in Animal Choices. This animal is a loyal companion that accompanies the druid on her adventures.<br/>Unlike normal animals of its kind, an animal companion''s Hit Dice, abilities, skills, and feats advance as the druid advances in level. If a character receives an animal companion from more than one source, her effective druid levels stack for the purposes of determining the statistics and abilities of the companion. Most animal companions increase in size when their druid reaches 4th or 7th level, depending on the companion. If a druid releases her companion from service, she may gain a new one by performing a ceremony requiring 24 uninterrupted hours of prayer in the environment where the new companion typically lives. This ceremony can also replace an animal companion that has perished.', 1, 'Ability' );
INSERT INTO ability VALUES ( 116, 'Nature Sense', 'A druid gains a +2 bonus on Knowledge (nature) and Survival checks.', 1, 'Ability' );
INSERT INTO ability VALUES ( 117, 'Wild Empathy', 'A character can improve the attitude of an animal. This ability functions just like a Diplomacy check made to improve the attitude of a person (see Charisma modifier to determine the wild empathy check result. The typical domestic animal has a starting attitude of indifferent, while wild animals are usually unfriendly.<br/>To use wild empathy, the character and the animal must be within 30 feet of one another under normal conditions. Generally, influencing an animal in this way takes 1 minute but, as with influencing people, it might take more or less time.<br/>A character can also use this ability to influence a magical beast with an Intelligence score of 1 or 2, but she takes a -4 penalty on the check.', 1, 'Ability' );
INSERT INTO ability VALUES ( 118, 'Woodland Stride', 'A character may move through any sort of undergrowth (such as natural thorns, briars, overgrown areas, and similar terrain) at her normal speed and without taking damage or suffering any other impairment. Thorns, briars, and overgrown areas that have been magically manipulated to impede motion, however, still affect her.', 1, 'Ability' );
INSERT INTO ability VALUES ( 119, 'Trackless Step', 'Starting at 3rd level, a druid leaves no trail in natural surroundings and cannot be tracked. She may choose to leave a trail if so desired.', 1, 'Ability' );
INSERT INTO ability VALUES ( 120, 'Resist Nature''s Lure', 'Starting at 4th level, a druid gains a +4 bonus on saving throws against the spell-like and supernatural abilities of fey. This bonus also applies to spells and effects that utilize or target plants, such as blight, entangle, spike growth, and warp wood.', 1, 'Ability' );
INSERT INTO ability VALUES ( 121, 'Wild Shape', 'At 4th level, a druid gains the ability to turn herself into any Small or Medium animal and back again once per day. Her options for new forms include all creatures with the animal type. This ability functions like the beast shape I spell, except as noted here. The effect lasts for 1 hour per druid level, or until she changes back. Changing form (to animal or back) is a standard action and doesn''t provoke an attack of opportunity. The form chosen must be that of an animal with which the druid is familiar. <br/>A druid loses her ability to speak while in animal form because she is limited to the sounds that a normal, untrained animal can make, but she can communicate normally with other animals of the same general grouping as her new form. (The normal sound a wild parrot makes is a squawk, so changing to this form does not permit speech.)<br/>A druid can use this ability an additional time per day at 6th level and every two levels thereafter, for a total of eight times at 18th level. At 20th level, a druid can use wild shape at will. As a druid gains levels, this ability allows the druid to take on the form of larger and smaller animals, elementals, and plants. Each form expends one daily use of this ability, regardless of the form taken. <br/>At 6th level, a druid can also use wild shape to change into a Large or Tiny animal or a Small elemental. When taking the form of an animal, a druid''s wild shape now functions as beast shape II. When taking the form of an elemental, the druid''s wild shape functions as elemental body I.<br/>At 8th level, a druid can also use wild shape to change into a Huge or Diminutive animal, a Medium elemental, or a Small or Medium plant creature. When taking the form of animals, a druid''s wild shape now functions as beast shape III. When taking the form of an elemental, the druid''s wild shape now functions as elemental body II. When taking the form of a plant creature, the druid''s wild shape functions as plant shape I.<br/>At 10th level, a druid can also use wild shape to change into a Large elemental or a Large plant creature. When taking the form of an elemental, the druid''s wild shape now functions as elemental body III. When taking the form of a plant, the druid''s wild shape now functions as plant shape II.<br/>At 12th level, a druid can also use wild shape to change into a Huge elemental or a Huge plant creature. When taking the form of an elemental, the druid''s wild shape now functions as elemental body IV. When taking the form of a plant, the druid''s wild shape now functions as plant shape III.', 3, 'Ability' );
INSERT INTO ability VALUES ( 122, 'Venom Immunity', 'At 9th level, a druid gains immunity to all poisons. ', 1, 'Ability' );
INSERT INTO ability VALUES ( 123, 'A Thousand Faces', 'At 13th level, a druid gains the ability to change her appearance at will, as if using the alter self spell, but only while in her normal form.', 3, 'Ability' );
INSERT INTO ability VALUES ( 124, 'Timeless Body', 'A character no longer takes penalties to his ability scores for aging and cannot be magically aged. Any such penalties that he has already taken, however, remain in place. Age bonuses still accrue, and the character still dies of old age when his time is up.', 1, 'Ability' );
INSERT INTO ability VALUES ( 125, 'Bonus Feats - Fighter', 'At 1st level, and at every even level thereafter, a fighter gains a bonus feat in addition to those gained from normal advancement (meaning that the fighter gains a feat at every level). These bonus feats must be selected from those listed as combat feats, sometimes also called ''fighter bonus feats.''<br/>Upon reaching 4th level, and every four levels thereafter (8th, 12th, and so on), a fighter can choose to learn a new bonus feat in place of a bonus feat he has already learned. In effect, the fighter loses the bonus feat in exchange for the new one. The old feat cannot be one that was used as a prerequisite for another feat, prestige class, or other ability. A fighter can only change one feat at any given level and must choose whether or not to swap the feat at the time he gains a new bonus feat for the level.', 0, 'Ability' );
INSERT INTO ability VALUES ( 126, 'Bravery', 'Starting at 2nd level, a fighter gains a +1 bonus on Will saves against fear. This bonus increases by +1 for every four levels beyond 2nd.', 1, 'Ability' );
INSERT INTO ability VALUES ( 127, 'Armor Training', 'Starting at 3rd level, a fighter learns to be more maneuverable while wearing armor. Whenever he is wearing armor, he reduces the armor check penalty by 1 (to a minimum of 0) and increases the maximum Dexterity bonus allowed by his armor by 1. Every four levels thereafter (7th, 11th, and 15th), these bonuses increase by +1 each time, to a maximum -4 reduction of the armor check penalty and a +4 increase of the maximum Dexterity bonus allowed.<br/>In addition, a fighter can also move at his normal speed while wearing medium armor. At 7th level, a fighter can move at his normal speed while wearing heavy armor.', 1, 'Ability' );
INSERT INTO ability VALUES ( 128, 'Weapon Training - Fighter', 'Starting at 5th level, a fighter can select one group of weapons, as noted below. Whenever he attacks with a weapon from this group, he gains a +1 bonus on attack and damage rolls.<br/>Every four levels thereafter (9th, 13th, and 17th), a fighter becomes further trained in another group of weapons. He gains a +1 bonus on attack and damage rolls when using a weapon from this group. In addition, the bonuses granted by previous weapon groups increase by +1 each. For example, when a fighter reaches 9th level, he receives a +1 bonus on attack and damage rolls with one weapon group and a +2 bonus on attack and damage rolls with the weapon group selected at 5th level. Bonuses granted from overlapping groups do not stack. Take the highest bonus granted for a weapon if it resides in two or more groups.<br/>A fighter also adds this bonus to any combat maneuver checks made with weapons from this group. This bonus also applies to the fighter''s Combat Maneuver Defense when defending against disarm and sunder attempts made against weapons from this group.<br/>Weapon groups are defined as follows (GMs may add other weapons to these groups, or add entirely new groups):<br/>Axes: battleaxe, dwarven waraxe, greataxe, handaxe, heavy pick, light pick, orc double axe, and throwing axe. <br/>Blades, Heavy: bastard sword, elven curve blade, falchion, greatsword, longsword, scimitar, scythe, and two-bladed sword.<br/>Blades, Light: dagger, kama, kukri, rapier, sickle, starknife, and short sword.<br/>Bows: composite longbow, composite shortbow, longbow, and shortbow.<br/>Close: gauntlet, heavy shield, light shield, punching dagger, sap, spiked armor, spiked gauntlet, spiked shield, and unarmed strike.<br/>Crossbows: hand crossbow, heavy crossbow, light crossbow, heavy repeating crossbow, and light repeating crossbow.<br/>Double: dire flail, dwarven urgrosh, gnome hooked hammer, orc double axe, quarterstaff, and two-bladed sword.<br/>Flails: dire flail, flail, heavy flail, morningstar, nunchaku, spiked chain, and whip.<br/>Hammers: club, greatclub, heavy mace, light hammer, light mace, and warhammer.<br/>Monk: kama, nunchaku, quarterstaff, sai, shuriken, siangham, and unarmed strike.<br/>Natural: unarmed strike and all natural weapons, such as bite, claw, gore, tail, and wing.<br/>Pole Arms: glaive, guisarme, halberd, and ranseur.<br/>Spears: javelin, lance, longspear, shortspear, spear, and trident.<br/>Thrown: blowgun, bolas, club, dagger, dart, halfling sling staff, javelin, light hammer, net, shortspear, shuriken, sling, spear, starknife, throwing axe, and trident.', 1, 'Ability' );
INSERT INTO ability VALUES ( 129, 'Armor Mastery', 'At 19th level, a fighter gains DR 5/- whenever he is wearing armor or using a shield.', 1, 'Ability' );
INSERT INTO ability VALUES ( 130, 'Weapon Mastery', 'At 20th level, a fighter chooses one weapon, such as the longsword, greataxe, or longbow. Any attacks made with that weapon automatically confirm all critical threats and have their damage multiplier increased by 1 (&times,2 becomes &times,3, for example). In addition, he cannot be disarmed while wielding a weapon of this type.', 1, 'Ability' );
INSERT INTO ability VALUES ( 131, 'AC Bonus', 'When unarmored and unencumbered, the monk adds his Wisdom bonus (if any) to his AC and his CMD. In addition, a monk gains a +1 bonus to AC and CMD at 4th level. This bonus increases by 1 for every four monk levels thereafter, up to a maximum of +5 at 20th level.<br/>These bonuses to AC apply even against touch attacks or when the monk is flat-footed. He loses these bonuses when he is immobilized or helpless, when he wears any armor, when he carries a shield, or when he carries a medium or heavy load.', 1, 'Ability' );
INSERT INTO ability VALUES ( 132, 'Flurry of Blows', 'Starting at 1st level, a monk can make a flurry of blows as a full-attack action. When doing so he may make one additional attack using any combination of unarmed strikes or attacks with a special monk weapon (kama, nunchaku, quarterstaff, sai, shuriken, and siangham) as if using the Two-Weapon Fighting feat (even if the monk does not meet the prerequisites for the feat). For the purpose of these attacks, the monk''s base attack bonus from his monk class levels. For all other purposes, such as qualifying for a feat or a prestige class, the monk uses his normal base attack bonus. <br/>At 8th level, the monk can make two additional attacks when he uses flurry of blows, as if using Improved Two-Weapon Fighting (even if the monk does not meet the prerequisites for the feat).<br/>At 15th level, the monk can make three additional attacks using flurry of blows, as if using Greater Two-Weapon Fighting (even if the monk does not meet the prerequisites for the feat). <br/>A monk applies his full Strength bonus to his damage rolls for all successful attacks made with flurry of blows, whether the attacks are made with an off-hand or with a weapon wielded in both hands. A monk may substitute disarm, sunder, and trip combat maneuvers for unarmed attacks as part of a flurry of blows. A monk cannot use any weapon other than an unarmed strike or a special monk weapon as part of a flurry of blows. A monk with natural weapons cannot use such weapons as part of a flurry of blows, nor can he make natural attacks in addition to his flurry of blows attacks.', 1, 'Ability' );
INSERT INTO ability VALUES ( 133, 'Unarmed Strike', 'At 1st level, a monk gains Improved Unarmed Strike as a bonus feat. A monk''s attacks may be with fist, elbows, knees, and feet. This means that a monk may make unarmed strikes with his hands full. There is no such thing as an off-hand attack for a monk striking unarmed. A monk may thus apply his full Strength bonus on damage rolls for all his unarmed strikes.<br/>Usually a monk''s unarmed strikes deal lethal damage, but he can choose to deal nonlethal damage instead with no penalty on his attack roll. He has the same choice to deal lethal or nonlethal damage while grappling.<br/>A monk''s unarmed strike is treated as both a manufactured weapon and a natural weapon for the purpose of spells and effects that enhance or improve either manufactured weapons or natural weapons.<br/>A monk also deals more damage with his unarmed strikes than a normal person would.', 0, 'Ability' );
INSERT INTO ability VALUES ( 134, 'Bonus Feat - Monk', 'At 1st level, 2nd level, and every 4 levels thereafter, a monk may select a bonus feat. These feats must be taken from the following list: Catch Off-Guard, Combat Reflexes, Deflect Arrows, Dodge, Improved Grapple, Scorpion Style, and Throw Anything. At 6th level, the following feats are added to the list: Gorgon''s Fist, Improved Bull Rush, Improved Disarm, Improved Feint, Improved Trip, and Mobility. At 10th level, the following feats are added to the list: Improved Critical, Medusa''s Wrath, Snatch Arrows, and Spring Attack. A monk need not have any of the prerequisites normally required for these feats to select them.', 0, 'Ability' );
INSERT INTO ability VALUES ( 135, 'Stunning Fist', 'At 1st level, the monk gains Stunning Fist as a bonus feat, even if he does not meet the prerequisites. At 4th level, and every 4 levels thereafter, the monk gains the ability to apply a new condition to the target of his Stunning Fist. This condition replaces stunning the target for 1 round, and a successful saving throw still negates the effect. At 4th level, he can choose to make the target fatigued. At 8th level, he can make the target sickened for 1 minute. At 12th level, he can make the target staggered for 1d6+1 rounds. At 16th level, he can permanently blind or deafen the target. At 20th level, he can paralyze the target for 1d6+1 rounds. The monk must choose which condition will apply before the attack roll is made. These effects do not stack with themselves (a creature sickened by Stunning Fist cannot become nauseated if hit by Stunning Fist again), but additional hits do increase the duration.', 1, 'Ability' );
INSERT INTO ability VALUES ( 136, 'Evasion', 'If exposed to any effect that normally allows her to attempt a Reflex saving throw for half damage, she takes no damage with a successful saving throw. The evasion ability can only be used if the character is wearing light armor or no armor.', 1, 'Ability' );
INSERT INTO ability VALUES ( 137, 'Fast Movement - Monk', 'At 3rd level, a monk gains an enhancement bonus to his land speed, as shown on Table: Monk. A monk in armor or carrying a medium or heavy load loses this extra speed.', 1, 'Ability' );
INSERT INTO ability VALUES ( 138, 'Maneuver Training', 'At 3rd level, a monk uses his monk level in place of his base attack bonus when calculating his Combat Maneuver Bonus. Base attack bonuses granted from other classes are unaffected and are added normally.', 1, 'Ability' );
INSERT INTO ability VALUES ( 139, 'Still Mind', 'A monk of 3rd level or higher gains a +2 bonus on saving throws against enchantment spells and effects.', 1, 'Ability' );
INSERT INTO ability VALUES ( 140, 'Ki Pool', 'At 4th level, a monk gains a pool of ki points, supernatural energy he can use to accomplish amazing feats. The number of points in a monk''s ki pool is equal to 1/2 his monk level + his Wisdom modifier. As long as he has at least 1 point in his ki pool, he can make a ki strike. At 4th level, ki strike allows his unarmed attacks to be treated as magic weapons for the purpose of overcoming damage reduction. Ki strike improves with the character''s monk level. At 10th level, his unarmed attacks are also treated as lawful weapons for the purpose of overcoming damage reduction. At 16th level, his unarmed attacks are treated as adamantine weapons for the purpose of overcoming damage reduction and bypassing hardness.<br/>By spending 1 point from his ki pool, a monk can make one additional attack at his highest attack bonus when making a flurry of blows attack. In addition, he can spend 1 point to increase his speed by 20 feet for 1 round. Finally, a monk can spend 1 point from his ki pool to give himself a +4 dodge bonus to AC for 1 round. Each of these powers is activated as a swift action. A monk gains additional powers that consume points from his ki pool as he gains levels.<br/>The ki pool is replenished each morning after 8 hours of rest or meditation, these hours do not need to be consecutive.', 3, 'Ability' );
INSERT INTO ability VALUES ( 141, 'Slow Fall', 'At 4th level or higher, a monk within arm''s reach of a wall can use it to slow his descent. When first gaining this ability, he takes damage as if the fall were 20 feet shorter than it actually is. The monk''s ability to slow his fall (that is, to reduce the effective distance of the fall when next to a wall) improves with his monk level until at 20th level he can use a nearby wall to slow his descent and fall any distance without harm.', 1, 'Ability' );
INSERT INTO ability VALUES ( 142, 'High Jump', 'At 5th level, a monk adds his level to all Acrobatics checks made to jump, both for vertical jumps and horizontal jumps. In addition, he always counts as having a running start when making jump checks using Acrobatics. By spending 1 point from his ki pool as a swift action, a monk gains a +20 bonus on Acrobatics checks made to jump for 1 round.', 1, 'Ability' );
INSERT INTO ability VALUES ( 143, 'Purity of Body', 'At 5th level, a monk gains immunity to all diseases, including supernatural and magical diseases.', 1, 'Ability' );
INSERT INTO ability VALUES ( 144, 'Wholeness of Body', 'At 7th level or higher, a monk can heal his own wounds as a standard action. He can heal a number of hit points of damage equal to his monk level by using 2 points from his ki pool.', 3, 'Ability' );
INSERT INTO ability VALUES ( 145, 'Improved Evasion', 'This works like evasion, except that while the character still takes no damage on a successful Reflex saving throw against attacks, she henceforth takes only half damage on a failed save. A helpless character does not gain the benefit of improved evasion.', 1, 'Ability' );
INSERT INTO ability VALUES ( 146, 'Diamond Body', 'At 11th level, a monk gains immunity to poisons of all kinds.', 3, 'Ability' );
INSERT INTO ability VALUES ( 147, 'Abundant Step', 'At 12th level or higher, a monk can slip magically between spaces, as if using the spell dimension door. Using this ability is a move action that consumes 2 points from his ki pool. His caster level for this effect is equal to his monk level. He cannot take other creatures with him when he uses this ability.', 3, 'Ability' );
INSERT INTO ability VALUES ( 148, 'Diamond Soul', 'At 13th level, a monk gains spell resistance equal to his current monk level + 10. In order to affect the monk with a spell, a spellcaster must get a result on a caster level check (1d20 + caster level) that equals or exceeds the monk''s spell resistance.', 1, 'Ability' );
INSERT INTO ability VALUES ( 149, 'Quivering Palm', 'Starting at 15th level, a monk can set up vibrations within the body of another creature that can thereafter be fatal if the monk so desires. He can use this quivering palm attack once per day, and he must announce his intent before making his attack roll. Creatures immune to critical hits cannot be affected. Otherwise, if the monk strikes successfully and the target takes damage from the blow, the quivering palm attack succeeds. Thereafter, the monk can try to slay the victim at any later time, as long as the attempt is made within a number of days equal to his monk level. To make such an attempt, the monk merely wills the target to die (a free action), and unless the target makes a Fortitude saving throw (DC 10 + 1/2 the monk''s level + the monk''s Wis modifier), it dies. If the saving throw is successful, the target is no longer in danger from that particular quivering palm attack, but it may still be affected by another one at a later time. A monk can have no more than 1 quivering palm in effect at one time. If a monk uses quivering palm while another is still in effect, the previous effect is negated.', 3, 'Ability' );
INSERT INTO ability VALUES ( 151, 'Tongue of the Sun and Moon', 'A monk of 17th level or higher can speak with any living creature.', 1, 'Ability' );
INSERT INTO ability VALUES ( 152, 'Empty Body', 'At 19th level, a monk gains the ability to assume an ethereal state for 1 minute as though using the spell etherealness. Using this ability is a move action that consumes 3 points from his ki pool. This ability only affects the monk and cannot be used to make other creatures ethereal.', 3, 'Ability' );
INSERT INTO ability VALUES ( 153, 'Perfect Self', 'At 20th level, a monk becomes a magical creature. He is forevermore treated as an outsider rather than as a humanoid (or whatever the monk''s creature type was) for the purpose of spells and magical effects. Additionally, the monk gains damage reduction 10/chaotic, which allows him to ignore the first 10 points of damage from any attack made by a nonchaotic weapon or by any natural attack made by a creature that doesn''t have similar damage reduction. Unlike other outsiders, the monk can still be brought back from the dead as if he were a member of his previous creature type.', 0, 'Ability' );
INSERT INTO ability VALUES ( 154, 'Aura of Good', 'The power of a paladin''s aura of good (see the detect good spell) is equal to her paladin level.', 1, 'Ability' );
INSERT INTO ability VALUES ( 155, 'Detect Evil', 'At will, a paladin can use detect evil, as the spell. A paladin can, as a move action, concentrate on a single item or individual within 60 feet and determine if it is evil, learning the strength of its aura as if having studied it for 3 rounds. While focusing on one individual or object, the paladin does not detect evil in any other object or individual within range.', 2, 'Ability' );
INSERT INTO ability VALUES ( 156, 'Smite Evil', 'Once per day, a paladin can call out to the powers of good to aid her in her struggle against evil. As a swift action, the paladin chooses one target within sight to smite. If this target is evil, the paladin adds her Charisma bonus (if any) to her attack rolls and adds her paladin level to all damage rolls made against the target of her smite. If the target of smite evil is an outsider with the evil subtype, an evil-aligned dragon, or an undead creature, the bonus to damage on the first successful attack increases to 2 points of damage per level the paladin possesses. Regardless of the target, smite evil attacks automatically bypass any DR the creature might possess.<br/>In addition, while smite evil is in effect, the paladin gains a deflection bonus equal to her Charisma modifier (if any) to her AC against attacks made by the target of the smite. If the paladin targets a creature that is not evil, the smite is wasted with no effect.<br/>The smite evil effect remains until the target of the smite is dead or the next time the paladin rests and regains her uses of this ability. At 4th level, and at every three levels thereafter, the paladin may smite evil one additional time per day, as indicated on Table: Paladin, to a maximum of seven times per day at 19th level.', 3, 'Ability' );
INSERT INTO ability VALUES ( 157, 'Divine Grace', 'At 2nd level, a paladin gains a bonus equal to her Charisma bonus (if any) on all saving throws.', 3, 'Ability' );
INSERT INTO ability VALUES ( 158, 'Lay On Hands', 'Beginning at 2nd level, a paladin can heal wounds (her own or those of others) by touch. Each day she can use this ability a number of times equal to 1/2 her paladin level plus her Charisma modifier. With one use of this ability, a paladin can heal 1d6 hit points of damage for every two paladin levels she possesses. Using this ability is a standard action, unless the paladin targets herself, in which case it is a swift action. Despite the name of this ability, a paladin only needs one free hand to use this ability.<br/>Alternatively, a paladin can use this healing power to deal damage to undead creatures, dealing 1d6 points of damage for every two levels the paladin possesses. Using lay on hands in this way requires a successful melee touch attack and doesn''t provoke an attack of opportunity. Undead do not receive a saving throw against this damage.', 3, 'Ability' );
INSERT INTO ability VALUES ( 159, 'Aura of Courage', 'At 3rd level, a paladin is immune to fear (magical or otherwise). Each ally within 10 feet of her gains a +4 morale bonus on saving throws against fear effects. This ability functions only while the paladin is conscious, not if she is unconscious or dead.', 3, 'Ability' );
INSERT INTO ability VALUES ( 160, 'Divine Health', 'At 3rd level, a paladin is immune to all diseases, including supernatural and magical diseases, including mummy rot.', 1, 'Ability' );
INSERT INTO ability VALUES ( 161, 'Mercy', 'At 3rd level, and every three levels thereafter, a paladin can select one mercy. Each mercy adds an effect to the paladin''s lay on hands ability. Whenever the paladin uses lay on hands to heal damage to one target, the target also receives the additional effects from all of the mercies possessed by the paladin. A mercy can remove a condition caused by a curse, disease, or poison without curing the affliction. Such conditions return after 1 hour unless the mercy actually removes the affliction that causes the condition.<br/>At 3rd level, the paladin can select from the following initial mercies. <br/>- Fatigued: The target is no longer fatigued.<br/>- Shaken: The target is no longer shaken.<br/>- Sickened: The target is no longer sickened. <br/>At 6th level, a paladin adds the following mercies to the list of those that can be selected. <br/>- Dazed: The target is no longer dazed.<br/>- Diseased: The paladin''s lay on hands ability also acts as remove disease, using the paladin''s level as the caster level.<br/>- Staggered: The target is no longer staggered, unless the target is at exactly 0 hit points.<br/>At 9th level, a paladin adds the following mercies to the list of those that can be selected.<br/>- Cursed: The paladin''s lay on hands ability also acts as remove curse, using the paladin''s level as the caster level.<br/>- Exhausted: The target is no longer exhausted. The paladin must have the fatigue mercy before selecting this mercy.<br/>- Frightened: The target is no longer frightened. The paladin must have the shaken mercy before selecting this mercy.<br/>- Nauseated: The target is no longer nauseated. The paladin must have the sickened mercy before selecting this mercy.<br/>- Poisoned: The paladin''s lay on hands ability also acts as neutralize poison, using the paladin''s level as the caster level.<br/>At 12th level, a paladin adds the following mercies to the list of those that can be selected.<br/>- Blinded: The target is no longer blinded.<br/>- Deafened: The target is no longer deafened.<br/>- Paralyzed: The target is no longer paralyzed.<br/>- Stunned: The target is no longer stunned. <br/>These abilities are cumulative. For example, a 12th-level paladin''s lay on hands ability heals 6d6 points of damage and might also cure fatigued and exhausted conditions as well as removing diseases and neutralizing poisons. Once a condition or spell effect is chosen, it can''t be changed.', 3, 'Ability' );
INSERT INTO ability VALUES ( 162, 'Channel Positive Energy', 'When a paladin reaches 4th level, she gains the supernatural ability to channel positive energy like a cleric. Using this ability consumes two uses of her lay on hands ability. A paladin uses her level as her effective cleric level when channeling positive energy. This is a Charisma-based ability.', 3, 'Ability' );
INSERT INTO ability VALUES ( 163, 'Spells - Paladin', 'Beginning at 4th level, a paladin gains the ability to cast a small number of divine spells which are drawn from the paladin spell list presented in Spell Lists. A paladin must choose and prepare her spells in advance.<br/>To prepare or cast a spell, a paladin must have a Charisma score equal to at least 10 + the spell level. The Difficulty Class for a saving throw against a paladin''s spell is 10 + the spell level + the paladin''s Charisma modifier.<br/>Like other spellcasters, a paladin can cast only a certain number of spells of each spell level per day. Her base daily spell allotment is given on Table: Paladin. In addition, she receives bonus spells per day if she has a high Charisma score (see Table: Ability Modifiers and Bonus Spells). When Table: Paladin indicates that the paladin gets 0 spells per day of a given spell level, she gains only the bonus spells she would be entitled to based on her Charisma score for that spell level.<br/>A paladin must spend 1 hour each day in quiet prayer and meditation to regain her daily allotment of spells. A paladin may prepare and cast any spell on the paladin spell list, provided that she can cast spells of that level, but she must choose which spells to prepare during her daily meditation.<br/>Through 3rd level, a paladin has no caster level. At 4th level and higher, her caster level is equal to her paladin level - 3.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 164, 'Divine Bond', 'Upon reaching 5th level, a paladin forms a divine bond with her god. This bond can take one of two forms. Once the form is chosen, it cannot be changed.<br/>The first type of bond allows the paladin to enhance her weapon as a standard action by calling upon the aid of a celestial spirit for 1 minute per paladin level. When called, the spirit causes the weapon to shed light as a torch. At 5th level, this spirit grants the weapon a +1 enhancement bonus. For every three levels beyond 5th, the weapon gains another +1 enhancement bonus, to a maximum of +6 at 20th level. These bonuses can be added to the weapon, stacking with existing weapon bonuses to a maximum of +5, or they can be used to add any of the following weapon properties: axiomatic, brilliant energy, defending, disruption, flaming, flaming burst, holy, keen, merciful, and speed. Adding these properties consumes an amount of bonus equal to the property''s cost (see Table: Melee Weapon Special Abilities). These bonuses are added to any properties the weapon already has, but duplicate abilities do not stack. If the weapon is not magical, at least a +1 enhancement bonus must be added before any other properties can be added. The bonus and properties granted by the spirit are determined when the spirit is called and cannot be changed until the spirit is called again. The celestial spirit imparts no bonuses if the weapon is held by anyone other than the paladin but resumes giving bonuses if returned to the paladin. These bonuses apply to only one end of a double weapon. A paladin can use this ability once per day at 5th level, and one additional time per day for every four levels beyond 5th, to a total of four times per day at 17th level.<br/>If a weapon bonded with a celestial spirit is destroyed, the paladin loses the use of this ability for 30 days, or until she gains a level, whichever comes first. During this 30-day period, the paladin takes a -1 penalty on attack and weapon damage rolls.<br/>The second type of bond allows a paladin to gain the service of an unusually intelligent, strong, and loyal steed to serve her in her crusade against evil. This mount is usually a heavy horse (for a Medium paladin) or a pony (for a Small paladin), although more exotic mounts, such as a boar, camel, or dog are also suitable. This mount functions as a druid''s animal companion, using the paladin''s level as her effective druid level. Bonded mounts have an Intelligence of at least 6. <br/>Once per day, as a full-round action, a paladin may magically call her mount to her side. This ability is the equivalent of a spell of a level equal to one-third the paladin''s level. The mount immediately appears adjacent to the paladin. A paladin can use this ability once per day at 5th level, and one additional time per day for every 4 levels thereafter, for a total of four times per day at 17th level.<br/>At 11th level, the mount gains the celestial template and becomes a magical beast for the purposes of determining which spells affect it. At 15th level, a paladin''s mount gains spell resistance equal to the paladin''s level + 11.<br/>Should the paladin''s mount die, the paladin may not summon another mount for 30 days or until she gains a paladin level, whichever comes first. During this 30-day period, the paladin takes a -1 penalty on attack and weapon damage rolls. ', 2, 'Ability' );
INSERT INTO ability VALUES ( 165, 'Aura of Resolve', 'At 8th level, a paladin is immune to charm spells and spell-like abilities. Each ally within 10 feet of her gains a +4 morale bonus on saving throws against charm effects.<br/>This ability functions only while the paladin is conscious, not if she is unconscious or dead.', 3, 'Ability' );
INSERT INTO ability VALUES ( 166, 'Aura of Justice', 'At 11th level, a paladin can expend two uses of her smite evil ability to grant the ability to smite evil to all allies within 10 feet, using her bonuses. Allies must use this smite evil ability by the start of the paladin''s next turn and the bonuses last for 1 minute. Using this ability is a free action. Evil creatures gain no benefit from this ability. ', 3, 'Ability' );
INSERT INTO ability VALUES ( 167, 'Aura of Faith', 'At 14th level, a paladin''s weapons are treated as good-aligned for the purposes of overcoming damage reduction. Any attack made against an enemy within 10 feet of her is treated as good-aligned for the purposes of overcoming damage reduction.<br/>This ability functions only while the paladin is conscious, not if she is unconscious or dead.', 3, 'Ability' );
INSERT INTO ability VALUES ( 168, 'Aura of Righteousness', 'At 17th level, a paladin gains DR 5/evil and immunity to compulsion spells and spell-like abilities. Each ally within 10 feet of her gains a +4 morale bonus on saving throws against compulsion effects. <br/>This ability functions only while the paladin is conscious, not if she is unconscious or dead.', 3, 'Ability' );
INSERT INTO ability VALUES ( 169, 'Holy Champion', 'At 20th level, a paladin becomes a conduit for the power of her god. Her DR increases to 10/evil. Whenever she uses smite evil and successfully strikes an evil outsider, the outsider is also subject to a banishment, using her paladin level as the caster level (her weapon and holy symbol automatically count as objects that the subject hates). After the banishment effect and the damage from the attack is resolved, the smite immediately ends. In addition, whenever she channels positive energy or uses lay on hands to heal a creature, she heals the maximum possible amount.', 3, 'Ability' );
INSERT INTO ability VALUES ( 170, 'Code of Conduct', 'A paladin must be of lawful good alignment and loses all class features except proficiencies if she ever willingly commits an evil act.<br/>Additionally, a paladin''s code requires that she respect legitimate authority, act with honor (not lying, not cheating, not using poison, and so forth), help those in need (provided they do not use the help for evil or chaotic ends), and punish those who harm or threaten innocents.', 0, 'Ability' );
INSERT INTO ability VALUES ( 171, 'Associates', 'While she may adventure with good or neutral allies, a paladin avoids working with evil characters or with anyone who consistently offends her moral code. Under exceptional circumstances, a paladin can ally with evil associates, but only to defeat what she believes to be a greater evil. A paladin should seek an atonement spell periodically during such an unusual alliance, and should end the alliance immediately should she feel it is doing more harm than good. A paladin may accept only henchmen, followers, or cohorts who are lawful good.', 0, 'Ability' );
INSERT INTO ability VALUES ( 172, 'Favored Enemy', 'At 1st level, a ranger selects a creature type from the ranger favored enemies table. He gains a +2 bonus on Bluff, Knowledge, Perception, Sense Motive, and Survival checks against creatures of his selected type. Likewise, he gets a +2 bonus on weapon attack and damage rolls against them. A ranger may make Knowledge skill checks untrained when attempting to identify these creatures.<br/>At 5th level and every five levels thereafter (10th, 15th, and 20th level), the ranger may select an additional favored enemy. In addition, at each such interval, the bonus against any one favored enemy (including the one just selected, if so desired) increases by +2. <br/>If the ranger chooses humanoids or outsiders as a favored enemy, he must also choose an associated subtype, as indicated on the table below. (Note that there are other types of humanoid to choose from-those called out specifically on the table below are merely the most common.) If a specific creature falls into more than one category of favored enemy, the ranger''s bonuses do not stack, he simply uses whichever bonus is higher.', 1, 'Ability' );
INSERT INTO ability VALUES ( 173, 'Track', 'A ranger adds half his level (minimum 1) to Survival skill checks made to follow or identify tracks.', 1, 'Ability' );
INSERT INTO ability VALUES ( 175, 'Combat Style Feat', 'At 2nd level, a ranger must select one of two combat styles to pursue: archery or two-weapon combat. The ranger''s expertise manifests in the form of bonus feats at 2nd, 6th, 10th, 14th, and 18th level. He can choose feats from his selected combat style, even if he does not have the normal prerequisites.<br/>If the ranger selects archery, he can choose from the following list whenever he gains a combat style feat: Far Shot, Point Blank Shot, Precise Shot, and Rapid Shot. At 6th level, he adds Improved Precise Shot and Manyshot to the list. At 10th level, he adds Pinpoint Targeting and Shot on the Run to the list.<br/>If the ranger selects two-weapon combat, he can choose from the following list whenever he gains a combat style feat: Double Slice, Improved Shield Bash, Quick Draw, and Two-Weapon Fighting. At 6th level, he adds Improved Two-Weapon Fighting and Two-Weapon Defense to the list. At 10th level, he adds Greater Two-Weapon Fighting and Two-Weapon Rend to the list.<br/>The benefits of the ranger''s chosen style feats apply only when he wears light, medium, or no armor. He loses all benefits of his combat style feats when wearing heavy armor. Once a ranger selects a combat style, it cannot be changed.', 1, 'Ability' );
INSERT INTO ability VALUES ( 176, 'Endurance', 'A ranger gains Endurance as a bonus feat at 3rd level.', 1, 'Ability' );
INSERT INTO ability VALUES ( 177, 'Favored Terrain', 'At 3rd level, a ranger may select a type of terrain from the Favored Terrains table. The ranger gains a +2 bonus on initiative checks and Knowledge (geography), Perception, Stealth, and Survival skill checks when he is in this terrain. A ranger traveling through his favored terrain normally leaves no trail and cannot be tracked (though he may leave a trail if he so chooses). At 8th level and every five levels thereafter, the ranger may select an additional favored terrain. In addition, at each such interval, the skill bonus and initiative bonus in any one favored terrain (including the one just selected, if so desired), increases by +2. <br/>If a specific terrain falls into more than one category of favored terrain, the ranger''s bonuses do not stack, he simply uses whichever bonus is higher.', 1, 'Ability' );
INSERT INTO ability VALUES ( 178, 'Hunter''s Bond', 'At 4th level, a ranger forms a bond with his hunting companions. This bond can take one of two forms. Once the form is chosen, it cannot be changed. The first is a bond to his companions. This bond allows him to spend a move action to grant half his favored enemy bonus against a single target of the appropriate type to all allies within 30 feet who can see or hear him. This bonus lasts for a number of rounds equal to the ranger''s Wisdom modifier (minimum 1). This bonus does not stack with any favored enemy bonuses possessed by his allies, they use whichever bonus is higher.<br/>The second option is to form a close bond with an animal companion. A ranger who selects an animal companion can choose from the following list: badger, bird, camel, cat (small), dire rat, dog, horse, pony, snake (viper or constrictor), or wolf. If the campaign takes place wholly or partly in an aquatic environment, the ranger may choose a shark instead. This animal is a loyal companion that accompanies the ranger on his adventures as appropriate for its kind. A ranger''s animal companion shares his favored enemy and favored terrain bonuses.<br/>This ability functions like the druid animal companion ability (which is part of the Nature Bond class feature), except that the ranger''s effective druid level is equal to his ranger level - 3.', 1, 'Ability' );
INSERT INTO ability VALUES ( 179, 'Spells - Ranger', 'Beginning at 4th level, a ranger gains the ability to cast a small number of divine spells, which are drawn from the ranger spell list presented in Spell Lists. A ranger must choose and prepare his spells in advance.<br/>To prepare or cast a spell, a ranger must have a Wisdom score equal to at least 10 + the spell level. The Difficulty Class for a saving throw against a ranger''s spell is 10 + the spell level + the ranger''s Wisdom modifier.<br/>Like other spellcasters, a ranger can cast only a certain number of spells of each spell level per day. His base daily spell allotment is given on Table: Ranger. In addition, he receives bonus spells per day if he has a high Wisdom score (see Table: Ability Modifiers and Bonus Spells). When Table: Ranger indicates that the ranger gets 0 spells per day of a given spell level, he gains only the bonus spells he would be entitled to based on his Wisdom score for that spell level.<br/>A ranger must spend 1 hour per day in quiet meditation to regain his daily allotment of spells. A ranger may prepare and cast any spell on the ranger spell list, provided that he can cast spells of that level, but he must choose which spells to prepare during his daily meditation.<br/>Through 3rd level, a ranger has no caster level. At 4th level and higher, his caster level is equal to his ranger level - 3.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 180, 'Swift Tracker', 'Beginning at 8th level, a ranger can move at his normal speed while using Survival to follow tracks without taking the normal -5 penalty. He takes only a -10 penalty (instead of the normal -20) when moving at up to twice normal speed while tracking.', 1, 'Ability' );
INSERT INTO ability VALUES ( 182, 'Quarry', 'At 11th level, a ranger can, as a standard action, denote one target within his line of sight as his quarry. Whenever he is following the tracks of his quarry, a ranger can take 10 on his Survival skill checks while moving at normal speed, without penalty. In addition, he receives a +2 insight bonus on attack rolls made against his quarry, and all critical threats are automatically confirmed. A ranger can have no more than one quarry at a time and the creature''s type must correspond to one of his favored enemy types. He can dismiss this effect at any time as a free action, but he cannot select a new quarry for 24 hours. If the ranger sees proof that his quarry is dead, he can select a new quarry after waiting 1 hour.', 1, 'Ability' );
INSERT INTO ability VALUES ( 183, 'Camouflage', 'A ranger of 12th level or higher can use the Stealth skill to hide in any of his favored terrains, even if the terrain doesn''t grant cover or concealment.', 1, 'Ability' );
INSERT INTO ability VALUES ( 185, 'Hide in Plain Sight - Terrain', 'While in any of his favored terrains, a ranger of 17th level or higher can use the Stealth skill even while being observed.', 1, 'Ability' );
INSERT INTO ability VALUES ( 186, 'Improved Quarry', 'At 19th level, the ranger''s ability to hunt his quarry improves. He can now select a quarry as a free action, and can now take 20 while using Survival to track his quarry, while moving at normal speed without penalty. His insight bonus to attack his quarry increases to +4. If his quarry is killed or dismissed, he can select a new one after 10 minutes have passed.', 1, 'Ability' );
INSERT INTO ability VALUES ( 187, 'Master Hunter', 'A ranger of 20th level becomes a master hunter. He can always move at full speed while using Survival to follow tracks without penalty. He can, as a standard action, make a single attack against a favored enemy at his full attack bonus. If the attack hits, the target takes damage normally and must make a Fortitude save or die. The DC of this save is equal to 10 + 1/2 the ranger''s level + the ranger''s Wisdom modifier. A ranger can choose instead to deal an amount of nonlethal damage equal to the creature''s current hit points. A successful save negates this damage. A ranger can use this ability once per day against each favored enemy type he possesses, but not against the same creature more than once in a 24-hour period.', 1, 'Ability' );
INSERT INTO ability VALUES ( 188, 'Sneak Attack - Rogue', 'If a rogue can catch an opponent when he is unable to defend himself effectively from her attack, she can strike a vital spot for extra damage.<br/>The rogue''s attack deals extra damage anytime her target would be denied a Dexterity bonus to AC (whether the target actually has a Dexterity bonus or not), or when the rogue flanks her target. This extra damage is 1d6 at 1st level, and increases by 1d6 every two rogue levels thereafter. Should the rogue score a critical hit with a sneak attack, this extra damage is not multiplied. Ranged attacks can count as sneak attacks only if the target is within 30 feet.<br/>With a weapon that deals nonlethal damage (like a sap, whip, or an unarmed strike), a rogue can make a sneak attack that deals nonlethal damage instead of lethal damage. She cannot use a weapon that deals lethal damage to deal nonlethal damage in a sneak attack, not even with the usual -4 penalty.<br/>The rogue must be able to see the target well enough to pick out a vital spot and must be able to reach such a spot. A rogue cannot sneak attack while striking a creature with concealment.', 0, 'Ability' );
INSERT INTO ability VALUES ( 189, 'Trapfinding', 'A rogue adds 1/2 her level to Perception skill checks made to locate traps and to Disable Device skill checks (minimum +1). A rogue can use Disable Device to disarm magic traps.', 0, 'Ability' );
INSERT INTO ability VALUES ( 191, 'Rogue Talents', 'As a rogue gains experience, she learns a number of talents that aid her and confound her foes. Starting at 2nd level, a rogue gains one rogue talent. She gains an additional rogue talent for every 2 levels of rogue attained after 2nd level. A rogue cannot select an individual talent more than once.', 0, 'Ability' );
INSERT INTO ability VALUES ( 192, 'Bleeding Attack', 'A rogue with this ability can cause living opponents to bleed by hitting them with a sneak attack. This attack causes the target to take 1 additional point of damage each round for each die of the rogue''s sneak attack (e.g., 4d6 equals 4 points of bleed). Bleeding creatures take that amount of damage every round at the start of each of their turns. The bleeding can be stopped by a DC 15 Heal check or the application of any effect that heals hit point damage. Bleeding damage from this ability does not stack with itself. Bleeding damage bypasses any damage reduction the creature might possess.', 1, 'Ability' );
INSERT INTO ability VALUES ( 193, 'Combat Trick', 'A rogue that selects this talent gains a bonus combat feat (see Feats).', 0, 'Ability' );
INSERT INTO ability VALUES ( 194, 'Fast Stealth', 'This ability allows a rogue to move at full speed using the Stealth skill without penalty.', 1, 'Ability' );
INSERT INTO ability VALUES ( 195, 'Finesse Rogue', 'A rogue that selects this talent gains Weapon Finesse as a bonus feat.', 0, 'Ability' );
INSERT INTO ability VALUES ( 196, 'Ledge Walker', 'This ability allows a rogue to move along narrow surfaces at full speed using the Acrobatics skill without penalty. In addition, a rogue with this talent is not flat-footed when using Acrobatics to move along narrow surfaces.', 1, 'Ability' );
INSERT INTO ability VALUES ( 197, 'Major Magic', 'A rogue with this talent gains the ability to cast a 1st-level spell from the sorcerer/wizard spell list two times a day as a spell-like ability. The caster level for this ability is equal to the rogue''s level. The save DC for this spell is 11 + the rogue''s Intelligence modifier. The rogue must have an Intelligence of at least 11 to select this talent. A rogue must have the minor magic rogue talent before choosing this talent.', 2, 'Ability' );
INSERT INTO ability VALUES ( 198, 'Minor Magic', 'A rogue with this talent gains the ability to cast a 0-level spell from the sorcerer/wizard spell list. This spell can be cast three times a day as a spell-like ability. The caster level for this ability is equal to the rogue''s level. The save DC for this spell is 10 + the rogue''s Intelligence modifier. The rogue must have an Intelligence of at least 10 to select this talent.', 1, 'Ability' );
INSERT INTO ability VALUES ( 199, 'Quick Disable', 'It takes a rogue with this ability half the normal amount of time to disable a trap using the Disable Device skill (minimum 1 round).', 1, 'Ability' );
INSERT INTO ability VALUES ( 200, 'Resiliency', 'Once per day, a rogue with this ability can gain a number of temporary hit points equal to the rogue''s level. Activating this ability is an immediate action that can only be performed when she is brought to below 0 hit points. This ability can be used to prevent her from dying. These temporary hit points last for 1 minute. If the rogue''s hit points drop below 0 due to the loss of these temporary hit points, she falls unconscious and is dying as normal.', 1, 'Ability' );
INSERT INTO ability VALUES ( 201, 'Rogue Crawl', 'While prone, a rogue with this ability can move at half speed. This movement provokes attacks of opportunity as normal. A rogue with this talent can take a 5-foot step while crawling.', 1, 'Ability' );
INSERT INTO ability VALUES ( 202, 'Slow Reactions', 'Opponents damaged by the rogue''s sneak attack can''t make attacks of opportunity for 1 round.', 1, 'Ability' );
INSERT INTO ability VALUES ( 203, 'Stand Up', 'A rogue with this ability can stand up from a prone position as a free action. This still provokes attacks of opportunity for standing up while threatened by a foe.', 1, 'Ability' );
INSERT INTO ability VALUES ( 204, 'Surprise Attack', 'During the surprise round, opponents are always considered flat-footed to a rogue with this ability, even if they have already acted. ', 1, 'Ability' );
INSERT INTO ability VALUES ( 205, 'Trap Spotter', 'Whenever a rogue with this talent comes within 10 feet of a trap, she receives an immediate Perception skill check to notice the trap. This check should be made in secret by the GM.', 1, 'Ability' );
INSERT INTO ability VALUES ( 206, 'Weapon Training - Rogue', 'A rogue that selects this talent gains Weapon Focus as a bonus feat.', 1, 'Ability' );
INSERT INTO ability VALUES ( 210, 'Advanced Talents', 'At 10th level, and every two levels thereafter, a rogue can choose one of the following advanced talents in place of a rogue talent.', 0, 'Ability' );
INSERT INTO ability VALUES ( 211, 'Crippling Strike', 'A rogue with this ability can sneak attack opponents with such precision that her blows weaken and hamper them. An opponent damaged by one of her sneak attacks also takes 2 points of Strength damage.', 1, 'Ability' );
INSERT INTO ability VALUES ( 212, 'Defensive Roll', 'With this advanced talent, the character can roll with a potentially lethal blow to take less damage from it than she otherwise would. Once per day, when she would be reduced to 0 or fewer hit points by damage in combat (from a weapon or other blow, not a spell or special ability), the character can attempt to roll with the damage. To use this ability, the character must attempt a Reflex saving throw (DC = damage dealt). If the save succeeds, she takes only half damage from the blow, if it fails, she takes full damage. She must be aware of the attack and able to react to it in order to execute her defensive roll-if she is denied her Dexterity bonus to AC, she can''t use this ability. Since this effect would not normally allow a character to make a Reflex save for half damage, the character''s evasion ability does not apply to the defensive roll.', 1, 'Ability' );
INSERT INTO ability VALUES ( 213, 'Dispelling Attack', 'Opponents that are dealt sneak attack damage by a rogue with this ability are affected by a targeted dispel magic, targeting the lowest-level spell effect active on the target. The caster level for this ability is equal to the rogue''s level. A rogue must have the major magic rogue talent before choosing dispelling attack.', 3, 'Ability' );
INSERT INTO ability VALUES ( 215, 'Opportunist', 'Once per round, the rogue can make an attack of opportunity against an opponent who has just been struck for damage in melee by another character. This attack counts as an attack of opportunity for that round. Even a rogue with the Combat Reflexes feat can''t use the opportunist ability more than once per round.', 1, 'Ability' );
INSERT INTO ability VALUES ( 216, 'Skill Mastery', 'The rogue becomes so confident in the use of certain skills that she can use them reliably even under adverse conditions.<br/>Upon gaining this ability, she selects a number of skills equal to 3 + her Intelligence modifier. When making a skill check with one of these skills, she may take 10 even if stress and distractions would normally prevent her from doing so. A rogue may gain this special ability multiple times, selecting additional skills for skill mastery to apply to each time.', 1, 'Ability' );
INSERT INTO ability VALUES ( 217, 'Slippery Mind', 'This ability represents the character''s ability to wriggle free from magical effects that would otherwise control or compel her. If a character with slippery mind is affected by an enchantment spell or effect and fails her saving throw, she can attempt it again 1 round later at the same DC. She gets only this one extra chance to succeed on her saving throw.', 1, 'Ability' );
INSERT INTO ability VALUES ( 218, 'Feat', 'A rogue may gain any feat that she qualifies for in place of a rogue talent.', 0, 'Ability' );
INSERT INTO ability VALUES ( 219, 'Master Strike', 'Upon reaching 20th level, a rogue becomes incredibly deadly when dealing sneak attack damage. Each time the rogue deals sneak attack damage, she can choose one of the following three effects: the target can be put to sleep for 1d4 hours, paralyzed for 2d6 rounds, or slain. Regardless of the effect chosen, the target receives a Fortitude save to negate the additional effect. The DC of this save is equal to 10 + 1/2 the rogue''s level + the rogue''s Intelligence modifier. Once a creature has been the target of a master strike, regardless of whether or not the save is made, that creature is immune to that rogue''s master strike for 24 hours. Creatures that are immune to sneak attack damage are also immune to this ability.', 1, 'Ability' );
INSERT INTO ability VALUES ( 220, 'Spells - Sorcerer', 'A sorcerer casts arcane spells drawn primarily from the sorcerer/wizard spell list presented in Charisma score equal to at least 10 + the spell level. The Difficulty Class for a saving throw against a sorcerer''s spell is 10 + the spell level + the sorcerer''s Charisma modifier.<br/>Like other spellcasters, a sorcerer can cast only a certain number of spells of each spell level per day. Her base daily spell allotment is given on Table: Sorcerer. In addition, she receives bonus spells per day if she has a high Charisma score (see Table: Ability Modifiers and Bonus Spells).<br/>A sorcerer''s selection of spells is extremely limited. A sorcerer begins play knowing four 0-level spells and two 1st-level spells of her choice. At each new sorcerer level, she gains one or more new spells, as indicated on Table: Sorcerer Spells Known. (Unlike spells per day, the number of spells a sorcerer knows is not affected by her Charisma score, the numbers on Table: Sorcerer Spells Known are fixed.) These new spells can be common spells chosen from the sorcerer/wizard spell list, or they can be unusual spells that the sorcerer has gained some understanding of through study. <br/>Upon reaching 4th level, and at every even-numbered sorcerer level after that (6th, 8th, and so on), a sorcerer can choose to learn a new spell in place of one she already knows. In effect, the sorcerer loses the old spell in exchange for the new one. The new spell''s level must be the same as that of the spell being exchanged. A sorcerer may swap only a single spell at any given level, and must choose whether or not to swap the spell at the same time that she gains new spells known for the level.<br/>Unlike a wizard or a cleric, a sorcerer need not prepare her spells in advance. She can cast any spell she knows at any time, assuming she has not yet used up her spells per day for that spell level.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 221, 'Bloodline', 'Each sorcerer has a source of magic somewhere in her heritage that grants her spells, bonus feats, an additional class skill, and other special abilities. This source can represent a blood relation or an extreme event involving a creature somewhere in the family''s past. For example, a sorcerer might have a dragon as a distant relative or her grandfather might have signed a terrible contract with a devil. Regardless of the source, this influence manifests in a number of ways as the sorcerer gains levels. A sorcerer must pick one bloodline upon taking her first level of sorcerer. Once made, this choice cannot be changed.<br/>At 3rd level, and every two levels thereafter, a sorcerer learns an additional spell, derived from her bloodline. These spells are in addition to the number of spells given on Table: Sorcerer Spells Known. These spells cannot be exchanged for different spells at higher levels.<br/>At 7th level, and every six levels thereafter, a sorcerer receives one bonus feat, chosen from a list specific to each bloodline. The sorcerer must meet the prerequisites for these bonus feats. ', 0, 'Ability' );
INSERT INTO ability VALUES ( 222, 'Cantrips - Sorcerer', 'Sorcerers learn a number of cantrips, or 0-level spells, as noted on Table: Sorcerer Spells Known under ''Spells Known.'' These spells are cast like any other spell, but they do not consume any slots and may be used again.', 0, 'Ability' );
INSERT INTO ability VALUES ( 223, 'Eschew Materials', 'A sorcerer gains Eschew Materials as a bonus feat at 1st level.', 0, 'Ability' );
INSERT INTO ability VALUES ( 224, 'Aberrant Bloodline', 'There is a taint in your blood, one that is alien and bizarre. You tend to think in odd ways, approaching problems from an angle that most would not expect. Over time, this taint manifests itself in your physical form.', 0, 'Ability' );
INSERT INTO ability VALUES ( 225, 'Aberrant - Class Skill', 'Knowledge (dungeoneering).', 0, 'Ability' );
INSERT INTO ability VALUES ( 226, 'Aberrant - Bonus Spells', 'enlarge person (3rd), see invisibility (5th), tongues (7th), black tentacles (9th), feeblemind (11th), veil (13th), plane shift (15th), mind blank (17th), shapechange (19th).', 0, 'Ability' );
INSERT INTO ability VALUES ( 227, 'Aberrant - Bonus Feats', 'Combat Casting, Improved Disarm, Improved Grapple, Improved Initiative, Improved Unarmed Strike, Iron Will, Silent Spell, Skill Focus (Knowledge [dungeoneering]).', 0, 'Ability' );
INSERT INTO ability VALUES ( 228, 'Aberrant - Bloodline Arcana', 'Whenever you cast a spell of the polymorph subschool, increase the duration of the spell by 50% (minimum 1 round). This bonus does not stack with the increase granted by the Extend Spell feat.', 0, 'Ability' );
INSERT INTO ability VALUES ( 229, 'Aberrant - Bloodline Powers', 'Aberrant sorcerers show increasing signs of their tainted heritage as they increase in level, although they are only visible when used.', 0, 'Ability' );
INSERT INTO ability VALUES ( 230, 'Aberrant - Acidic Ray', 'Starting at 1st level, you can fire an acidic ray as a standard action, targeting any foe within 30 feet as a ranged touch attack. The acidic ray deals 1d6 points of acid damage + 1 for every two sorcerer levels you possess. You can use this ability a number of times per day equal to 3 + your Charisma modifier.', 2, 'Ability' );
INSERT INTO ability VALUES ( 231, 'Aberrant - Long Limbs', 'At 3rd level, your reach increases by 5 feet whenever you are making a melee touch attack. This ability does not otherwise increase your threatened area. At 11th level, this bonus to your reach increases to 10 feet. At 17th level, this bonus to your reach increases to 15 feet.', 1, 'Ability' );
INSERT INTO ability VALUES ( 232, 'Aberrant - Unusual Anatomy', 'At 9th level, your anatomy changes, giving you a 25% chance to ignore any critical hit or sneak attack scored against you. This chance increases to 50% at 13th level.', 1, 'Ability' );
INSERT INTO ability VALUES ( 233, 'Aberrant - Alien Resistance', 'At 15th level, you gain spell resistance equal to your sorcerer level + 10.', 3, 'Ability' );
INSERT INTO ability VALUES ( 234, 'Aberrant - Form', 'At 20th level, your body becomes truly unnatural. You are immune to critical hits and sneak attacks. In addition, you gain blindsight with a range of 60 feet and damage reduction 5/-.', 1, 'Ability' );
INSERT INTO ability VALUES ( 235, 'Abyssal Bloodline', 'Generations ago, a demon spread its filth into your heritage. While it does not manifest in all of your kin, for you it is particularly strong. You might sometimes have urges to chaos or evil, but your destiny (and alignment) is up to you.', 0, 'Ability' );
INSERT INTO ability VALUES ( 236, 'Abyssal - Class Skill', 'Knowledge (planes).', 0, 'Ability' );
INSERT INTO ability VALUES ( 237, 'Abyssal - Bonus Spells', 'cause fear (3rd), bull''s strength (5th), rage (7th), stoneskin (9th), dismissal (11th), transformation (13th), greater teleport (15th), unholy aura (17th), summon monster IX (19th).', 0, 'Ability' );
INSERT INTO ability VALUES ( 238, 'Abyssal - Bonus Feats', 'Augment Summoning, Cleave, Empower Spell, Great Fortitude, Improved Bull Rush, Improved Sunder, Power Attack, Skill Focus (Knowledge [planes]).', 0, 'Ability' );
INSERT INTO ability VALUES ( 239, 'Abyssal - Bloodline Arcana', 'Whenever you cast a spell of the summoning subschool, the creatures summoned gain DR/good equal to 1/2 your sorcerer level (minimum 1). This does not stack with any DR the creature might have.', 0, 'Ability' );
INSERT INTO ability VALUES ( 240, 'Abyssal - Bloodline Powers', 'While some would say that you are possessed, you know better. The demonic influence in your blood grows as you gain power.', 0, 'Ability' );
INSERT INTO ability VALUES ( 241, 'Abyssal - Claws', 'At 1st level, you can grow claws as a free action. These claws are treated as natural weapons, allowing you to make two claw attacks as a full attack action using your full base attack bonus. These attacks deal 1d4 points of damage each (1d3 if you are Small) plus your Strength modifier. At 5th level, these claws are considered magic weapons for the purpose of overcoming DR. At 7th level, the damage increases by one step to 1d6 points of damage (1d4 if you are Small). At 11th level, these claws become flaming weapons, each dealing an additional 1d6 points of fire damage on a successful hit. You can use your claws for a number of rounds per day equal to 3 + your Charisma modifier. These rounds do not need to be consecutive.', 3, 'Ability' );
INSERT INTO ability VALUES ( 242, 'Abyssal - Demon Resistances', 'At 3rd level, you gain resist electricity 5 and a +2 bonus on saving throws made against poison. At 9th level, your resistance to electricity increases to 10 and your bonus on poison saving throws increases to +4.', 1, 'Ability' );
INSERT INTO ability VALUES ( 243, 'Abyssal - Strength of the Abyss', 'At 9th level, you gain a +2 inherent bonus to your Strength. This bonus increases to +4 at 13th level, and to +6 at 17th level.', 1, 'Ability' );
INSERT INTO ability VALUES ( 244, 'Abyssal - Added Summonings', 'At 15th level, whenever you summon a creature with the demon subtype or the fiendish template using a summon monster spell, you summon one additional creature of the same kind.', 3, 'Ability' );
INSERT INTO ability VALUES ( 245, 'Abyssal - Demonic Might', 'At 20th level, the power of the Abyss flows through you. You gain immunity to electricity and poison. You also gain resistance to acid 10, cold 10, and fire 10, and gain telepathy with a range of 60 feet (allowing you to communicate with any creature that can speak a language).', 3, 'Ability' );
INSERT INTO ability VALUES ( 246, 'Arcane Bloodline', 'Your family has always been skilled in the eldritch art of magic. While many of your relatives were accomplished wizards, your powers developed without the need for study and practice.', 0, 'Ability' );
INSERT INTO ability VALUES ( 247, 'Arcane - Class Skill', 'Knowledge (any one).', 0, 'Ability' );
INSERT INTO ability VALUES ( 248, 'Arcane - Bonus Spells', 'identify (3rd), invisibility (5th), dispel magic (7th), dimension door (9th), overland flight (11th), true seeing (13th), greater teleport (15th), power word stun (17th), wish (19th).', 0, 'Ability' );
INSERT INTO ability VALUES ( 249, 'Arcane - Bonus Feats', 'Combat Casting, Improved Counterspell, Improved Initiative, Iron Will, Scribe Scroll, Skill Focus (Knowledge [arcana]), Spell Focus, Still Spell.', 0, 'Ability' );
INSERT INTO ability VALUES ( 250, 'Arcane - Bloodline Arcana', 'Whenever you apply a metamagic feat to a spell that increases the slot used by at least one level, increase the spell''s DC by +1. This bonus does not stack with itself and does not apply to spells modified by the Heighten Spell feat.', 0, 'Ability' );
INSERT INTO ability VALUES ( 251, 'Arcane - Bloodline Powers', 'Magic comes naturally to you, but as you gain levels you must take care to prevent the power from overwhelming you. ', 0, 'Ability' );
INSERT INTO ability VALUES ( 252, 'Arcane - Arcane Bond', 'At 1st level, you gain an arcane bond, as a wizard equal to your sorcerer level. Your sorcerer levels stack with any wizard levels you possess when determining the powers of your familiar or bonded object. This ability does not allow you to have both a familiar and a bonded item. Once per day, your bond item allows you to cast any one of our spells known (unlike a wizard''s bonded item, which allows him to cast any one spell in his spellbook).', 3, 'Ability' );
INSERT INTO ability VALUES ( 253, 'Arcane - Metamagic Adept', 'At 3rd level, you can apply any one metamagic feat you know to a spell you are about to cast without increasing the casting time. You must still expend a higher-level spell slot to cast this spell. You can use this ability once per day at 3rd level and one additional time per day for every four sorcerer levels you possess beyond 3rd, up to five times per day at 19th level. At 20th level, this ability is replaced by arcane apotheosis.', 1, 'Ability' );
INSERT INTO ability VALUES ( 254, 'Arcane - New Arcana', 'At 9th level, you can add any one spell from the sorcerer/wizard spell list to your list of spells known. This spell must be of a level that you are capable of casting. You can also add one additional spell at 13th level and 17th level.', 1, 'Ability' );
INSERT INTO ability VALUES ( 255, 'Arcane - School Power', 'At 15th level, pick one school of magic. The DC for any spells you cast from that school increases by +2. This bonus stacks with the bonus granted by Spell Focus.', 1, 'Ability' );
INSERT INTO ability VALUES ( 256, 'Arcane - Arcane Apotheosis', 'At 20th level, your body surges with arcane power. You can add any metamagic feats that you know to your spells without increasing their casting time, although you must still expend higher-level spell slots. Whenever you use magic items that require charges, you can instead expend spell slots to power the item. For every three levels of spell slots that you expend, you consume one less charge when using a magic item that expends charges.', 1, 'Ability' );
INSERT INTO ability VALUES ( 257, 'Celestial Bloodline', 'Your bloodline is blessed by a celestial power, either from a celestial ancestor or through divine intervention. Although this power drives you along the path of good, your fate (and alignment) is your own to determine.', 0, 'Ability' );
INSERT INTO ability VALUES ( 258, 'Celestial - Class Skill', 'Heal.', 0, 'Ability' );
INSERT INTO ability VALUES ( 259, 'Celestial - Bonus Spells', 'bless (3rd), resist energy (5th), magic circle against evil (7th), remove curse (9th), flame strike (11th), greater dispel magic (13th), banishment (15th), sunburst (17th), gate (19th).', 0, 'Ability' );
INSERT INTO ability VALUES ( 260, 'Celestial - Bonus Feats', 'Dodge, Extend Spell, Iron Will, Mobility, Mounted Combat, Ride-By Attack, Skill Focus (Knowledge [religion]), Weapon Finesse.', 0, 'Ability' );
INSERT INTO ability VALUES ( 261, 'Celestial - Bloodline Arcana', 'Whenever you cast a spell of the summoning subschool, the creatures summoned gain DR/evil equal to 1/2 your sorcerer level (minimum 1). This does not stack with any DR the creature might have.', 0, 'Ability' );
INSERT INTO ability VALUES ( 262, 'Celestial - Bloodline Powers', 'Your celestial heritage grants you a great many powers, but they come at a price. The lords of the higher planes are watching you and your actions closely.', 0, 'Ability' );
INSERT INTO ability VALUES ( 263, 'Celestial - Heavenly Fire', 'Starting at 1st level, you can unleash a ray of heavenly fire as a standard action, targeting any foe within 30 feet as a ranged touch attack. Against evil creatures, this ray deals 1d4 points of damage + 1 for every two sorcerer levels you possess. This damage is divine and not subject to energy resistance or immunity. This ray heals good creatures of 1d4 points of damage + 1 for every two sorcerer levels you possess. A good creature cannot benefit from your heavenly fire more than once per day. Neutral creatures are neither harmed nor healed by this effect. You can use this ability a number of times per day equal to 3 + your Charisma modifier.', 2, 'Ability' );
INSERT INTO ability VALUES ( 264, 'Celestial - Celestial Resistances', 'At 3rd level, you gain resist acid 5 and resist cold 5. At 9th level, your resistances increase to 10.', 1, 'Ability' );
INSERT INTO ability VALUES ( 265, 'Celestial - Wings of Heaven', 'At 9th level, you can sprout feathery wings and fly for a number of minutes per day equal to your sorcerer level, with a speed of 60 feet and good maneuverability. This duration does not need to be consecutive, but it must be used in 1 minute increments. ', 3, 'Ability' );
INSERT INTO ability VALUES ( 266, 'Celestial - Conviction', 'At 15th level, you can reroll any one ability check, attack roll, skill check, or saving throw you just made. You must decide to use this ability after the die is rolled, but before the results are revealed by the GM. You must take the second result, even if it is worse. You can use this ability once per day.', 3, 'Ability' );
INSERT INTO ability VALUES ( 267, 'Celestial - Ascension', 'At 20th level, you become infused with the power of the heavens. You gain immunity to acid, cold, and petrification. You also gain resist electricity 10, resist fire 10, and a +4 racial bonus on saves against poison. Finally, you gain unlimited use of the wings of heaven ability. Finally, you gain the ability to speak with any creature that has a language (as per the tongues spell).', 3, 'Ability' );
INSERT INTO ability VALUES ( 268, 'Destined Bloodline', 'Your family is destined for greatness in some way. Your birth could have been foretold in prophecy, or perhaps it occurred during an especially auspicious event, such as a solar eclipse. Regardless of your bloodline''s origin, you have a great future ahead.', 0, 'Ability' );
INSERT INTO ability VALUES ( 269, 'Destined - Class Skill', 'Knowledge (history).', 0, 'Ability' );
INSERT INTO ability VALUES ( 270, 'Destined - Bonus Spells', ' alarm (3rd), blur (5th), protection from energy (7th), freedom of movement (9th), break enchantment (11th), mislead (13th), spell turning (15th), moment of prescience (17th), foresight (19th).', 0, 'Ability' );
INSERT INTO ability VALUES ( 271, 'Destined - Bonus Feats', 'Arcane Strike, Diehard, Endurance, Leadership, Lightning Reflexes, Maximize Spell, Skill Focus (Knowledge [history]), Weapon Focus.', 0, 'Ability' );
INSERT INTO ability VALUES ( 272, 'Destined - Bloodline Arcana', 'Whenever you cast a spell with a range of ''personal,'' you gain a luck bonus equal to the spell''s level on all your saving throws for 1 round.', 0, 'Ability' );
INSERT INTO ability VALUES ( 273, 'Destined - Bloodline Powers', 'You are destined for great things, and the powers that you gain serve to protect you.', 0, 'Ability' );
INSERT INTO ability VALUES ( 274, 'Destined - Touch of Destiny', 'At 1st level, you can touch a creature as a standard action, giving it an insight bonus on attack rolls, skill checks, ability checks, and saving throws equal to 1/2 your sorcerer level (minimum 1) for 1 round. You can use this ability a number of times per day equal to 3 + your Charisma modifier.', 2, 'Ability' );
INSERT INTO ability VALUES ( 275, 'Destined - Fated', 'Starting at 3rd level, you gain a +1 luck bonus on all of your saving throws and to your AC during surprise rounds (see Combat) and when you are otherwise unaware of an attack. At 7th level and every four levels thereafter, this bonus increases by +1, to a maximum of +5 at 19th level.', 3, 'Ability' );
INSERT INTO ability VALUES ( 276, 'Destined - It Was Meant To Be', 'At 9th level, you may reroll any one attack roll, critical hit confirmation roll, or level check made to overcome spell resistance. You must decide to use this ability after the first roll is made but before the results are revealed by the GM. You must take the second result, even if it is worse. At 9th level, you can use this ability once per day. At 17th level, you can use this ability twice per day.', 3, 'Ability' );
INSERT INTO ability VALUES ( 277, 'Destined - Within Reach', 'At 15th level, your ultimate destiny is drawing near. Once per day, when an attack or spell that causes damage would result in your death, you may attempt a DC 20 Will save. If successful, you are instead reduced to -1 hit points and are automatically stabilized. The bonus from your fated ability applies to this save.', 3, 'Ability' );
INSERT INTO ability VALUES ( 278, 'Destined - Destiny Realized', 'At 20th level, your moment of destiny is at hand. Any critical threats made against you only confirm if the second roll results in a natural 20 on the die. Any critical threats you score with a spell are automatically confirmed. Once per day, you can automatically succeed at one caster level check made to overcome spell resistance. You must use this ability before making the roll.', 3, 'Ability' );
INSERT INTO ability VALUES ( 279, 'Draconic Bloodline', 'At some point in your family''s history, a dragon interbred with your bloodline, and now its ancient power flows through your veins. ', 0, 'Ability' );
INSERT INTO ability VALUES ( 280, 'Draconic - Class Skill', 'Perception.', 0, 'Ability' );
INSERT INTO ability VALUES ( 281, 'Draconic - Bonus Spells', 'mage armor (3rd), resist energy (5th), spell resistance (11th), form of the dragon I (13th), form of the dragon II (15th), form of the dragon III (17th), wish (19th).', 0, 'Ability' );
INSERT INTO ability VALUES ( 282, 'Draconic - Bonus Feats', 'Blind-Fight, Great Fortitude, Improved Initiative, Power Attack, Quicken Spell, Skill Focus (Fly), Skill Focus (Knowledge [arcana]), Toughness.', 0, 'Ability' );
INSERT INTO ability VALUES ( 283, 'Draconic - Bloodline Arcana', 'Whenever you cast a spell with an energy descriptor that matches your draconic bloodline''s energy type, that spell deals +1 point of damage per die rolled.', 0, 'Ability' );
INSERT INTO ability VALUES ( 284, 'Draconic - Bloodline Powers', 'The power of dragons flows through you and manifests in a number of ways. At 1st level, you must select one of the chromatic or metallic dragon types. This choice cannot be changed. A number of your abilities grant resistances and deal damage based on your dragon type.', 0, 'Ability' );
INSERT INTO ability VALUES ( 285, 'Draconic - Claws', 'Starting at 1st level, you can grow claws as a free action. These claws are treated as natural weapons, allowing you to make two claw attacks as a full attack action using your full base attack bonus. Each of these attacks deals 1d4 points of damage plus your Strength modifier (1d3 if you are Small). At 5th level, these claws are considered magic weapons for the purpose of overcoming DR. At 7th level, the damage increases by one step to 1d6 points of damage (1d4 if you are Small). At 11th level, these claws deal an additional 1d6 points of damage of your energy type on a successful hit. You can use your claws for a number of rounds per day equal to 3 + your Charisma modifier. These rounds do not need to be consecutive.', 3, 'Ability' );
INSERT INTO ability VALUES ( 286, 'Draconic - Dragon Resistances', 'At 3rd level, you gain resist 5 against your energy type and a +1 natural armor bonus. At 9th level, your energy resistance increases to 10 and natural armor bonus increases to +2. At 15th level, your natural armor bonus increases to +4.', 1, 'Ability' );
INSERT INTO ability VALUES ( 287, 'Draconic - Breath Weapon', 'At 9th level, you gain a breath weapon. This breath weapon deals 1d6 points of damage of your energy type per sorcerer level. Those caught in the area of the breath receive a Reflex save for half damage. The DC of this save is equal to 10 + 1/2 your sorcerer level + your Charisma modifier. The shape of the breath weapon depends on your dragon type (as indicated on the above chart). At 9th level, you can use this ability once per day. At 17th level, you can use this ability twice per day. At 20th level, you can use this ability three times per day.', 3, 'Ability' );
INSERT INTO ability VALUES ( 288, 'Draconic - Wings', 'At 15th level, leathery dragon wings grow from your back as a standard action, giving you a fly speed of 60 feet with average maneuverability. You can dismiss the wings as a free action.', 3, 'Ability' );
INSERT INTO ability VALUES ( 289, 'Draconic - Power of Wyrms', 'At 20th level, your draconic heritage becomes manifest. You gain immunity to paralysis, sleep, and damage of your energy type. You also gain blindsense 60 feet.', 3, 'Ability' );
INSERT INTO ability VALUES ( 290, 'Elemental Bloodline', 'The power of the elements resides in you, and at times you can hardly control its fury. This influence comes from an elemental outsider in your family history or a time when you or your relatives were exposed to a powerful elemental force.', 0, 'Ability' );
INSERT INTO ability VALUES ( 291, 'Elemental - Class Skill', 'Knowledge (planes).', 0, 'Ability' );
INSERT INTO ability VALUES ( 292, 'Elemental - Bonus Spells', 'burning hands* (3rd), scorching ray* (5th), protection from energy (7th), elemental body I (9th), elemental body II (11th), elemental body III (13th), elemental body IV (15th), summon monster VIII (elementals only) (17th), elemental swarm (19th).<br/>*These spells always deal a type of damage determined by your element. In addition, the subtype of these spells changes to match the energy type of your element.', 0, 'Ability' );
INSERT INTO ability VALUES ( 293, 'Elemental - Bonus Feats', 'Dodge, Empower Spell, Great Fortitude, Improved Initiative, Lightning Reflexes, Power Attack, Skill Focus (Knowledge [planes]), Weapon Finesse.', 0, 'Ability' );
INSERT INTO ability VALUES ( 294, 'Elemental - Bloodline Arcana', 'Whenever you cast a spell that deals energy damage, you can change the type of damage to match the type of your bloodline. This also changes the spell''s type to match the type of your bloodline.', 0, 'Ability' );
INSERT INTO ability VALUES ( 295, 'Elemental - Bloodline Powers', 'One of the four elements infuses your being, and you can draw upon its power in times of need. At first level, you must select one of the four elements: air, earth, fire, or water. This choice cannot be changed. A number of your abilities grant resistances and deal damage based on your element.', 0, 'Ability' );
INSERT INTO ability VALUES ( 296, 'Elemental - Elemental Ray', 'Starting at 1st level, you can unleash an elemental ray as a standard action, targeting any foe within 30 feet as a ranged touch attack. This ray deals 1d6 points of damage of your energy type + 1 for every two sorcerer levels you possess. You can use this ability a number of times per day equal to 3 + your Charisma modifier.', 2, 'Ability' );
INSERT INTO ability VALUES ( 297, 'Elemental - Elemental Resistance', 'At 3rd level, you gain energy resistance 10 against your energy type. At 9th level, your energy resistance increases to 20.', 1, 'Ability' );
INSERT INTO ability VALUES ( 298, 'Elemental - Elemental Blast', 'At 9th level, you can unleash a blast of elemental power once per day. This 20-foot-radius burst does 1d6 points of damage of your energy type per sorcerer level. Those caught in the area of your blast receive a Reflex save for half damage. Creatures that fail their saves gain vulnerability to your energy type until the end of your next turn. The DC of this save is equal to 10 + 1/2 your sorcerer level + your Charisma modifier. At 9th level, you can use this ability once per day. At 17th level, you can use this ability twice per day. At 20th level, you can use this ability three times per day. This power has a range of 60 feet.', 2, 'Ability' );
INSERT INTO ability VALUES ( 299, 'Elemental - Elemental Movement', 'At 15th level, you gain a special movement type or bonus. This ability is based on your chosen element, as indicated on the above chart.', 3, 'Ability' );
INSERT INTO ability VALUES ( 300, 'Elemental - Elemental Body', 'At 20th level, elemental power surges through your body. You gain immunity to sneak attacks, critical hits, and damage from your energy type.', 3, 'Ability' );
INSERT INTO ability VALUES ( 301, 'Fey Bloodline', 'The capricious nature of the fey runs in your family due to some intermingling of fey blood or magic. You are more emotional than most, prone to bouts of joy and rage.', 0, 'Ability' );
INSERT INTO ability VALUES ( 302, 'Fey - Class Skill', 'Knowledge (nature).', 0, 'Ability' );
INSERT INTO ability VALUES ( 303, 'Fey - Bonus Spells', 'entangle (3rd), hideous laughter (5th), deep slumber (7th), poison (9th), tree stride (11th), mislead (13th), phase door (15th), irresistible dance (17th), shapechange (19th).', 0, 'Ability' );
INSERT INTO ability VALUES ( 304, 'Fey - Bonus Feats', 'Dodge, Improved Initiative, Lightning Reflexes, Mobility, Point Blank Shot, Precise Shot, Quicken Spell, Skill Focus (Knowledge [nature]).', 0, 'Ability' );
INSERT INTO ability VALUES ( 305, 'Fey - Bloodline Arcana', 'Whenever you cast a spell of the compulsion subschool, increase the spell''s DC by +2.', 0, 'Ability' );
INSERT INTO ability VALUES ( 306, 'Fey - Bloodline Powers', 'You have always had a tie to the natural world, and as your power increases, so does the influence of the fey over your magic.', 0, 'Ability' );
INSERT INTO ability VALUES ( 307, 'Fey - Laughing Touch', 'At 1st level, you can cause a creature to burst out laughing for 1 round as a melee touch attack. A laughing creature can only take a move action but can defend itself normally. Once a creature has been affected by laughing touch, it is immune to its effects for 24 hours. You can use this ability a number of times per day equal to 3 + your Charisma modifier. This is a mind-affecting effect.', 2, 'Ability' );
INSERT INTO ability VALUES ( 308, 'Fey - Woodland Stride', 'At 3rd level, you can move through any sort of undergrowth (such as natural thorns, briars, overgrown areas, and similar terrain) at your normal speed and without taking damage or suffering any other impairment. Thorns, briars, and overgrown areas that have been magically manipulated to impede motion, however, still affect you.', 1, 'Ability' );
INSERT INTO ability VALUES ( 309, 'Fey - Fleeting Glance', 'At 9th level, you can turn invisible for a number of rounds per day equal to your sorcerer level. This ability functions as greater invisibility. These rounds need not be consecutive.', 2, 'Ability' );
INSERT INTO ability VALUES ( 310, 'Fey - Fey Magic', 'At 15th level, you may reroll any caster level check made to overcome spell resistance. You must decide to use this ability before the results are revealed by the GM. You must take the second result, even if it is worse. You can use this ability at will.', 3, 'Ability' );
INSERT INTO ability VALUES ( 311, 'Fey - Soul of the Fey', 'At 20th level, your soul becomes one with the world of the fey. You gain immunity to poison and DR 10/cold iron. Creatures of the animal type do not attack you unless compelled to do so through magic. Once per day, you can cast shadow walk as a spell-like ability using your sorcerer level as your caster level.', 3, 'Ability' );
INSERT INTO ability VALUES ( 312, 'Infernal Bloodline', 'Somewhere in your family''s history, a relative made a deal with a devil, and that pact has influenced your family line ever since. In you, it manifests in direct and obvious ways, granting you powers and abilities. While your fate is still your own, you can''t help but wonder if your ultimate reward is bound to the Pit.', 0, 'Ability' );
INSERT INTO ability VALUES ( 313, 'Infernal - Class Skill', 'Diplomacy.', 0, 'Ability' );
INSERT INTO ability VALUES ( 314, 'Infernal - Bonus Spells', 'protection from good (3rd), scorching ray (5th), suggestion (7th), charm monster (9th), dominate person (11th), planar binding (devils and creatures with the fiendish template only) (13th), greater teleport (15th), power word stun (17th), meteor swarm (19th).', 0, 'Ability' );
INSERT INTO ability VALUES ( 315, 'Infernal - Bonus Feats', 'Blind-Fight, Combat Expertise, Deceitful, Extend Spell, Improved Disarm, Iron Will, Skill Focus (Knowledge [planes]), Spell Penetration.', 0, 'Ability' );
INSERT INTO ability VALUES ( 316, 'Infernal - Bloodline Arcana', 'Whenever you cast a spell of the charm subschool, increase the spell''s DC by +2.', 0, 'Ability' );
INSERT INTO ability VALUES ( 317, 'Infernal - Bloodline Powers', 'You can draw upon the power of Hell, although you must be wary of its corrupting influence. Such power does not come without a price.', 0, 'Ability' );
INSERT INTO ability VALUES ( 318, 'Infernal - Corrupting Touch', 'At 1st level, you can cause a creature to become shaken as a melee touch attack. This effect persists for a number of rounds equal to 1/2 your sorcerer level (minimum 1). Creatures shaken by this ability radiate an aura of evil, as if they were an evil outsider (see detect evil). Multiple touches do not stack, but they do add to the duration. You can use this ability a number of times per day equal to 3 + your Charisma modifier.', 2, 'Ability' );
INSERT INTO ability VALUES ( 319, 'Infernal - Infernal Resistances', 'At 3rd level, you gain resist fire 5 and a +2 bonus on saving throws made against poison. At 9th level, your resistance to fire increases to 10 and your bonus on poison saving throws increases to +4.', 1, 'Ability' );
INSERT INTO ability VALUES ( 320, 'Infernal - Hellfire', 'At 9th level, you can call down a column of hellfire. This 10-foot-radius burst does 1d6 points of fire damage per sorcerer level. Those caught in the area of your blast receive a Reflex save for half damage. Good creatures that fail their saves are shaken for a number of rounds equal to your sorcerer level. The DC of this save is equal to 10 + 1/2 your sorcerer level + your Charisma modifier. At 9th level, you can use this ability once per day. At 17th level, you can use this ability twice per day. At 20th level, you can use this ability three times per day. This power has a range of 60 feet.', 2, 'Ability' );
INSERT INTO ability VALUES ( 321, 'Infernal - On Dark Wings', 'At 15th level, you can grow fearsome bat wings as a standard action, giving you a fly speed of 60 feet with average maneuverability. The wings can be dismissed as a free action.', 3, 'Ability' );
INSERT INTO ability VALUES ( 322, 'Infernal - Power of the Pit', 'At 20th level, your form becomes infused with vile power. You gain immunity to fire and poison. You also gain resistance to acid 10 and cold 10, and the ability to see perfectly in darkness of any kind to a range of 60 feet.', 3, 'Ability' );
INSERT INTO ability VALUES ( 323, 'Undead Bloodline', 'The taint of the grave runs through your family. Perhaps one of your ancestors became a powerful lich or vampire, or maybe you were born dead before suddenly returning to life. Either way, the forces of death move through you and touch your every action.', 0, 'Ability' );
INSERT INTO ability VALUES ( 324, 'Undead - Class Skill', 'Knowledge (religion).', 0, 'Ability' );
INSERT INTO ability VALUES ( 325, 'Undead - Bonus Spells', 'chill touch (3rd), false life (5th), vampiric touch (7th), animate dead (9th), waves of fatigue (11th), undeath to death (13th), finger of death (15th), horrid wilting (17th), energy drain (19th).', 0, 'Ability' );
INSERT INTO ability VALUES ( 326, 'Undead - Bonus Feats', 'Combat Casting, Diehard, Endurance, Iron Will, Skill Focus (Knowledge [religion]), Spell Focus, Still Spell, Toughness.', 0, 'Ability' );
INSERT INTO ability VALUES ( 327, 'Undead - Bloodline Arcana', 'Some undead are susceptible to your mind-affecting spells. Corporeal undead that were once humanoids are treated as humanoids for the purposes of determining which spells affect them.', 0, 'Ability' );
INSERT INTO ability VALUES ( 328, 'Undead - Bloodline Powers', 'You can call upon the foul powers of the afterlife. Unfortunately, the more you draw upon them, the closer you come to joining them.', 0, 'Ability' );
INSERT INTO ability VALUES ( 329, 'Undead - Grave Touch', 'Starting at 1st level, you can make a melee touch attack as a standard action that causes a living creature to become shaken for a number of rounds equal to 1/2 your sorcerer level (minimum 1). If you touch a shaken creature with this ability, it becomes frightened for 1 round if it has fewer Hit Dice than your sorcerer level. You can use this ability a number of times per day equal to 3 + your Charisma modifier.', 2, 'Ability' );
INSERT INTO ability VALUES ( 330, 'Undead - Death''s Gift', 'At 3rd level, you gain resist cold 5 and DR 5/- against nonlethal damage. At 9th level, your resistance to cold increases to 10 and your DR increases to 10/- against nonlethal damage.', 3, 'Ability' );
INSERT INTO ability VALUES ( 331, 'Undead - Grasp of the Dead', 'At 9th level, you can cause a swarm of skeletal arms to burst from the ground to rip and tear at your foes. The skeletal arms erupt from the ground in a 20-foot-radius burst. Anyone in this area takes 1d6 points of slashing damage per sorcerer level. Those caught in the area receive a Reflex save for half damage. Those who fail the save are unable to move for 1 round. The DC of this save is equal to 10 + 1/2 your sorcerer level + your Charisma modifier. The skeletal arms disappear after 1 round. The arms must burst up from a solid surface. At 9th level, you can use this ability once per day. At 17th level, you can use this ability twice per day. At 20th level, you can use this ability three times per day. This power has a range of 60 feet.', 2, 'Ability' );
INSERT INTO ability VALUES ( 332, 'Undead - Incorporeal Form', 'At 15th level, you can become incorporeal for 1 round per sorcerer level. While in this form, you gain the incorporeal subtype. You only take half damage from corporeal sources as long as they are magic (you take no damage from non-magic weapons and objects). Likewise, your spells deal only half damage to corporeal creatures. Spells and other effects that do not deal damage function normally. You can use this ability once per day.', 2, 'Ability' );
INSERT INTO ability VALUES ( 333, 'Undead - One of Us', 'At 20th level, your form begins to rot (the appearance of this decay is up to you) and undead see you as one of them. You gain immunity to cold, nonlethal damage, paralysis, and sleep. You also gain DR 5/-. Unintelligent undead do not notice you unless you attack them. You receive a +4 morale bonus on saving throws made against spells and spell-like abilities cast by undead.', 1, 'Ability' );
INSERT INTO ability VALUES ( 334, 'Spells - Wizard', 'A wizard casts arcane spells drawn from the sorcerer/wizard spell list presented in Spell Lists. A wizard must choose and prepare his spells ahead of time.<br/>To learn, prepare, or cast a spell, the wizard must have an Intelligence score equal to at least 10 + the spell level. The Difficulty Class for a saving throw against a wizard''s spell is 10 + the spell level + the wizard''s Intelligence modifier.<br/>A wizard can cast only a certain number of spells of each spell level per day. His base daily spell allotment is given on Table: Wizard. In addition, he receives bonus spells per day if he has a high Intelligence score (see Table: Ability Modifiers and Bonus Spells).<br/>A wizard may know any number of spells. He must choose and prepare his spells ahead of time by getting 8 hours of sleep and spending 1 hour studying his spellbook. While studying, the wizard decides which spells to prepare.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 335, 'Bonus Languages - Wizard', 'A wizard may substitute Draconic for one of the bonus languages available to the character because of his race.', 0, 'Ability' );
INSERT INTO ability VALUES ( 336, 'Arcane Bond', 'At 1st level, wizards form a powerful bond with an object or a creature. This bond can take one of two forms: a familiar or a bonded object. A familiar is a magical pet that enhances the wizard''s skills and senses and can aid him in magic, while a bonded object is an item a wizard can use to cast additional spells or to serve as a magical item. Once a wizard makes this choice, it is permanent and cannot be changed. Rules for bonded items are given below, while rules for familiars are at the end of this section.<br/>Wizards who select a bonded object begin play with one at no cost. Objects that are the subject of an arcane bond must fall into one of the following categories: amulet, ring, staff, wand, or weapon. These objects are always masterwork quality. Weapons acquired at 1st level are not made of any special material. If the object is an amulet or ring, it must be worn to have effect, while staves, wands, and weapons must be wielded. If a wizard attempts to cast a spell without his bonded object worn or in hand, he must make a concentration check or lose the spell. The DC for this check is equal to 20 + the spell''s level. If the object is a ring or amulet, it occupies the ring or neck slot accordingly.<br/>A bonded object can be used once per day to cast any one spell that the wizard has in his spellbook and is capable of casting, even if the spell is not prepared. This spell is treated like any other spell cast by the wizard, including casting time, duration, and other effects dependent on the wizard''s level. This spell cannot be modified by metamagic feats or other abilities. The bonded object cannot be used to cast spells from the wizard''s opposition schools (see arcane school).<br/>A wizard can add additional magic abilities to his bonded object as if he has the required item creation feats and if he meets the level prerequisites of the feat. For example, a wizard with a bonded dagger must be at least 5th level to add magic abilities to the dagger (see the Craft Magic Arms and Armor feat in Feats). If the bonded object is a wand, it loses its wand abilities when its last charge is consumed, but it is not destroyed and it retains all of its bonded object properties and can be used to craft a new wand. The magic properties of a bonded object, including any magic abilities added to the object, only function for the wizard who owns it. If a bonded object''s owner dies, or the item is replaced, the object reverts to being an ordinary masterwork item of the appropriate type.<br/>If a bonded object is damaged, it is restored to full hit points the next time the wizard prepares his spells. If the object of an arcane bond is lost or destroyed, it can be replaced after 1 week in a special ritual that costs 200 gp per wizard level plus the cost of the masterwork item. This ritual takes 8 hours to complete. Items replaced in this way do not possess any of the additional enchantments of the previous bonded item. A wizard can designate an existing magic item as his bonded item. This functions in the same way as replacing a lost or destroyed item except that the new magic item retains its abilities while gaining the benefits and drawbacks of becoming a bonded item.', 1, 'Ability' );
INSERT INTO ability VALUES ( 337, 'Arcane School', 'A wizard can choose to specialize in one school of magic, gaining additional spells and powers based on that school. This choice must be made at 1st level, and once made, it cannot be changed. A wizard that does not select a school receives the universalist school instead.<br/>A wizard that chooses to specialize in one school of magic must select two other schools as his opposition schools, representing knowledge sacrificed in one area of arcane lore to gain mastery in another. A wizard who prepares spells from his opposition schools must use two spell slots of that level to prepare the spell. For example, a wizard with evocation as an opposition school must expend two of his available 3rd-level spell slots to prepare a fireball. In addition, a specialist takes a -4 penalty on any skill checks made when crafting a magic item that has a spell from one of his opposition schools as a prerequisite. A universalist wizard can prepare spells from any school without restriction.<br/>Each arcane school gives the wizard a number of school powers. In addition, specialist wizards receive an additional spell slot of each spell level he can cast, from 1st on up. Each day, a wizard can prepare a spell from his specialty school in that slot. This spell must be in the wizard''s spellbook. A wizard can select a spell modified by a metamagic feat to prepare in his school slot, but it uses up a higher-level spell slot. Wizards with the universalist school do not receive a school slot.', 0, 'Ability' );
INSERT INTO ability VALUES ( 338, 'Cantrips - Wizard', 'Wizards can prepare a number of cantrips, or 0-level spells, each day, as noted on Table: Wizard under ''Spells per Day.'' These spells are cast like any other spell, but they are not expended when cast and may be used again. A wizard can prepare a cantrip from a prohibited school, but it uses up two of his available slots (see below).', 0, 'Ability' );
INSERT INTO ability VALUES ( 339, 'Scribe Scroll', 'At 1st level, a wizard gains Scribe Scroll as a bonus feat. ', 0, 'Ability' );
INSERT INTO ability VALUES ( 340, 'Bonus Feats - Wizard', 'At 5th, 10th, 15th, and 20th level, a wizard gains a bonus feat. At each such opportunity, he can choose a metamagic feat, an item creation feat, or Spell Mastery. The wizard must still meet all prerequisites for a bonus feat, including caster level minimums. These bonus feats are in addition to the feats that a character of any class gets from advancing levels. The wizard is not limited to the categories of item creation feats, metamagic feats, or Spell Mastery when choosing those feats.', 0, 'Ability' );
INSERT INTO ability VALUES ( 341, 'Spellbooks', 'A wizard must study his spellbook each day to prepare his spells. He cannot prepare any spell not recorded in his spellbook, except for read magic, which all wizards can prepare from memory.<br/>A wizard begins play with a spellbook containing all 0-level wizard spells (except those from his prohibited schools, if any, see Arcane Schools) plus three 1st-level spells of his choice. The wizard also selects a number of additional 1st-level spells equal to his Intelligence modifier to add to the spellbook. At each new wizard level, he gains two new spells of any spell level or levels that he can cast (based on his new wizard level) for his spellbook. At any time, a wizard can also add spells found in other wizards'' spellbooks to his own (see Magic).', 0, 'Ability' );
INSERT INTO ability VALUES ( 342, 'Abjuration School', 'The abjurer uses magic against itself, and masters the art of defensive and warding magics.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 343, 'Abjuration - Resistance', 'You gain resistance 5 to an energy type of your choice, chosen when you prepare spells. This resistance can be changed each day. At 11th level, this resistance increases to 10. At 20th level, this resistance changes to immunity to the chosen energy type.', 1, 'Ability' );
INSERT INTO ability VALUES ( 344, 'Abjuration - Protective Ward', 'As a standard action, you can create a 10-foot-radius field of protective magic centered on you that lasts for a number of rounds equal to your Intelligence modifier. All allies in this area (including you) receive a +1 deflection bonus to their AC. This bonus increases by +1 for every five wizard levels you possess. You can use this ability a number of times per day equal to 3 + your Intelligence modifier.', 3, 'Ability' );
INSERT INTO ability VALUES ( 345, 'Abjuration - Energy Absorption', 'At 6th level, you gain an amount of energy absorption equal to 3 times your wizard level per day. Whenever you take energy damage, apply immunity, vulnerability (if any), and resistance first and apply the rest to this absorption, reducing your daily total by that amount. Any damage in excess of your absorption is applied to you normally.', 3, 'Ability' );
INSERT INTO ability VALUES ( 346, 'Conjuration School', 'The conjurer focuses on the study of summoning monsters and magic alike to bend to his will.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 347, 'Conjuration - Summoner''s Charm', 'Whenever you cast a conjuration (summoning) spell, increase the duration by a number of rounds equal to 1/2 your wizard level (minimum 1). At 20th level, you can change the duration of all summon monster spells to permanent. You can have no more than one summon monster spell made permanent in this way at one time. If you designate another summon monster spell as permanent, the previous spell immediately ends.', 3, 'Ability' );
INSERT INTO ability VALUES ( 348, 'Conjuration - Acid Dart', 'As a standard action you can unleash an acid dart targeting any foe within 30 feet as a ranged touch attack. The acid dart deals 1d6 points of acid damage + 1 for every two wizard levels you possess. You can use this ability a number of times per day equal to 3 + your Intelligence modifier. This attack ignores spell resistance.', 2, 'Ability' );
INSERT INTO ability VALUES ( 349, 'Conjuration - Dimensional Steps', 'At 8th level, you can use this ability to teleport up to 30 feet per wizard level per day as a standard action. This teleportation must be used in 5-foot increments and such movement does not provoke an attack of opportunity. You can bring other willing creatures with you, but you must expend an equal amount of distance for each additional creature brought with you.', 2, 'Ability' );
INSERT INTO ability VALUES ( 350, 'Divination School', 'Diviners are masters of remote viewing, prophecies, and using magic to explore the world.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 351, 'Divination - Forewarned', 'You can always act in the surprise round even if you fail to make a Perception roll to notice a foe, but you are still considered flat-footed until you take an action. In addition, you receive a bonus on initiative checks equal to 1/2 your wizard level (minimum +1). At 20th level, anytime you roll initiative, assume the roll resulted in a natural 20.', 3, 'Ability' );
INSERT INTO ability VALUES ( 352, 'Divination - Diviner''s Fortune', 'When you activate this school power, you can touch any creature as a standard action to give it an insight bonus on all of its attack rolls, skill checks, ability checks, and saving throws equal to 1/2 your wizard level (minimum +1) for 1 round. You can use this ability a number of times per day equal to 3 + your Intelligence modifier.', 2, 'Ability' );
INSERT INTO ability VALUES ( 353, 'Divination - Scrying Adept', 'At 8th level, you are always aware when you are being observed via magic, as if you had a permanent detect scrying. In addition, whenever you scry on a subject, treat the subject as one step more familiar to you. Very familiar subjects get a -10 penalty on their save to avoid your scrying attempts.', 3, 'Ability' );
INSERT INTO ability VALUES ( 354, 'Enchantment School', 'The enchanter uses magic to control and manipulate the minds of his victims.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 355, 'Enchantment - Enchanting Smile', 'You gain a +2 enhancement bonus on Bluff, Diplomacy, and Intimidate skill checks. This bonus increases by +1 for every five wizard levels you possess, up to a maximum of +6 at 20th level. At 20th level, whenever you succeed at a saving throw against a spell of the enchantment school, that spell is reflected back at its caster, as per spell turning.', 3, 'Ability' );
INSERT INTO ability VALUES ( 356, 'Enchantment - Dazing Touch', 'You can cause a living creature to become dazed for 1 round as a melee touch attack. Creatures with more Hit Dice than your wizard level are unaffected. You can use this ability a number of times per day equal to 3 + your Intelligence modifier.', 2, 'Ability' );
INSERT INTO ability VALUES ( 357, 'Enchantment - Aura of Despair', 'At 8th level, you can emit a 30-foot aura of despair for a number of rounds per day equal to your wizard level. Enemies within this aura take a -2 penalty on ability checks, attack rolls, damage rolls, saving throws, and skill checks. These rounds do not need to be consecutive. This is a mind-affecting effect.', 3, 'Ability' );
INSERT INTO ability VALUES ( 358, 'Evocation School', 'Evokers revel in the raw power of magic, and can use it to create and destroy with shocking ease.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 359, 'Evocation - Intense Spells', 'Whenever you cast an evocation spell that deals hit point damage, add 1/2 your wizard level to the damage (minimum +1). This bonus only applies once to a spell, not once per missile or ray, and cannot be split between multiple missiles or rays. This bonus damage is not increased by Empower Spell or similar effects. This damage is of the same type as the spell. At 20th level, whenever you cast an evocation spell you can roll twice to penetrate a creature''s spell resistance and take the better result.', 3, 'Ability' );
INSERT INTO ability VALUES ( 360, 'Evocation - Force Missile', 'As a standard action you can unleash a force missile that automatically strikes a foe, as magic missile. The force missile deals 1d4 points of damage plus the damage from your intense spells evocation power. This is a force effect. You can use this ability a number of times per day equal to 3 + your Intelligence modifier.', 2, 'Ability' );
INSERT INTO ability VALUES ( 361, 'Evocation - Elemental Wall', 'At 8th level, you can create a wall of energy that lasts for a number of rounds per day equal to your wizard level. These rounds do not need to be consecutive. This wall deals acid, cold, electricity, or fire damage, determined when you create it. The elemental wall otherwise functions like wall of fire.', 2, 'Ability' );
INSERT INTO ability VALUES ( 362, 'Illusion School', 'Illusionists use magic to weave confounding images, figments, and phantoms to baffle and vex their foes.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 363, 'Illusion - Extended Illusions', 'Any illusion spell you cast with a duration of ''concentration'' lasts a number of additional rounds equal to 1/2 your wizard level after you stop maintaining concentration (minimum +1 round). At 20th level, you can make one illusion spell with a duration of ''concentration'' become permanent. You can have no more than one illusion made permanent in this way at one time. If you designate another illusion as permanent, the previous permanent illusion ends. ', 3, 'Ability' );
INSERT INTO ability VALUES ( 364, 'Illusion - Blinding Ray', 'As a standard action you can fire a shimmering ray at any foe within 30 feet as a ranged touch attack. The ray causes creatures to be blinded for 1 round. Creatures with more Hit Dice than your wizard level are dazzled for 1 round instead. You can use this ability a number of times per day equal to 3 + your Intelligence modifier.', 2, 'Ability' );
INSERT INTO ability VALUES ( 365, 'Illusion - Invisibility Field', 'At 8th level, you can make yourself invisible as a swift action for a number of rounds per day equal to your wizard level. These rounds do not need to be consecutive. This otherwise functions as greater invisibility.', 2, 'Ability' );
INSERT INTO ability VALUES ( 366, 'Necromancy School', 'The dread and feared necromancer commands undead and uses the foul power of unlife against his enemies.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 367, 'Necromancy - Power over Undead', 'You receive Command Undead or Turn Undead as a bonus feat. You can channel energy a number of times per day equal to 3 + your Intelligence modifier, but only to use the selected feat. You can take other feats to add to this ability, such as Extra Channel and Improved Channel, but not feats that alter this ability, such as Elemental Channel and Alignment Channel. The DC to save against these feats is equal to 10 + 1/2 your wizard level + your Charisma modifier. At 20th level, undead cannot add their channel resistance to the save against this ability.', 3, 'Ability' );
INSERT INTO ability VALUES ( 368, 'Necromancy - Grave Touch', 'As a standard action, you can make a melee touch attack that causes a living creature to become shaken for a number of rounds equal to 1/2 your wizard level (minimum 1). If you touch a shaken creature with this ability, it becomes frightened for 1 round if it has fewer Hit Dice than your wizard level. You can use this ability a number of times per day equal to 3 + your Intelligence modifier.', 2, 'Ability' );
INSERT INTO ability VALUES ( 369, 'Necromancy - Life Sight', 'At 8th level, you gain blindsight to a range of 10 feet for a number of rounds per day equal to your wizard level. This ability only allows you to detect living creatures and undead creatures. This sight also tells you whether a creature is living or undead. Constructs and other creatures that are neither living nor undead cannot be seen with this ability. The range of this ability increases by 10 feet at 12th level, and by an additional 10 feet for every four levels beyond 12th. These rounds do not need to be consecutive.', 3, 'Ability' );
INSERT INTO ability VALUES ( 370, 'Transmutation School', 'Transmuters use magic to change the world around them.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 371, 'Transmutation - Physical Enhancement', 'You gain a +1 enhancement bonus to one physical ability score (Strength, Dexterity, or Constitution). This bonus increases by +1 for every five wizard levels you possess to a maximum of +5 at 20th level. You can change this bonus to a new ability score when you prepare spells. At 20th level, this bonus applies to two physical ability scores of your choice.', 3, 'Ability' );
INSERT INTO ability VALUES ( 372, 'Transmutation - Telekinetic Fist', 'As a standard action you can strike with a telekinetic fist, targeting any foe within 30 feet as a ranged touch attack. The telekinetic fist deals 1d4 points of bludgeoning damage + 1 for every two wizard levels you possess. You can use this ability a number of times per day equal to 3 + your Intelligence modifier.', 2, 'Ability' );
INSERT INTO ability VALUES ( 373, 'Transmutation - Change Shape', 'At 8th level, you can change your shape for a number of rounds per day equal to your wizard level. These rounds do not need to be consecutive. This ability otherwise functions like beast shape II or elemental body I. At 12th level, this ability functions like beast shape III or elemental body II.', 2, 'Ability' );
INSERT INTO ability VALUES ( 374, 'Universalist School', 'Wizards who do not specialize (known as as universalists) have the most diversity of all arcane spellcasters.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 375, 'Universalist - Hand of the Apprentice', 'You cause your melee weapon to fly from your grasp and strike a foe before instantly returning to you. As a standard action, you can make a single attack using a melee weapon at a range of 30 feet. This attack is treated as a ranged attack with a thrown weapon, except that you add your Intelligence modifier on the attack roll instead of your Dexterity modifier (damage still relies on Strength). This ability cannot be used to perform a combat maneuver. You can use this ability a number of times per day equal to 3 + your Intelligence modifier.', 3, 'Ability' );
INSERT INTO ability VALUES ( 376, 'Universalist - Metamagic Mastery', 'At 8th level, you can apply any one metamagic feat that you know to a spell you are about to cast. This does not alter the level of the spell or the casting time. You can use this ability once per day at 8th level and one additional time per day for every two wizard levels you possess beyond 8th. Any time you use this ability to apply a metamagic feat that increases the spell level by more than 1, you must use an additional daily usage for each level above 1 that the feat adds to the spell. Even though this ability does not modify the spell''s actual level, you cannot use this ability to cast a spell whose modified spell level would be above the level of the highest-level spell that you are capable of casting.', 3, 'Ability' );
INSERT INTO ability VALUES ( 377, 'Arcane Spells and Armor', 'Armor restricts the complicated gestures required while casting any spell that has a somatic component. The armor and shield descriptions list the arcane spell failure chance for different armors and shields.<br/>If a spell doesn''t have a somatic component, an arcane spellcaster can cast it with no arcane spell failure chance while wearing armor. Such spells can also be cast even if the caster''s hands are bound or he is grappling (although concentration checks still apply normally). The metamagic feat Still Spell allows a spellcaster to prepare or cast a spell without the somatic component at one spell level higher than normal. This also provides a way to cast a spell while wearing armor without risking arcane spell failure.', 0, 'Ability' );
INSERT INTO ability VALUES ( 378, 'Spells per Day - Arcane Archer', 'At the indicated levels, an arcane archer gains new spells per day as if he had also gained a level in an arcane spellcasting class he belonged to before adding the prestige class. He does not, however, gain other benefits a character of that class would have gained, except for additional spells per day, spells known (if he is a spontaneous spellcaster), and an increased effective level of spellcasting. If a character had more than one arcane spellcasting class before becoming an arcane archer, he must decide to which class he adds the new level for purposes of determining spells per day.', 0, 'Ability' );
INSERT INTO ability VALUES ( 379, 'Enhance Arrows', 'At 1st level, every nonmagical arrow an arcane archer nocks and lets fly becomes magical, gaining a +1 enhancement bonus. Unlike magic weapons created by normal means, the archer need not spend gold pieces to accomplish this task. However, an archer''s magic arrows only function for him. <br/>In addition, the arcane archer''s arrows gain a number of additional qualities as he gains additional levels. The elemental, elemental burst, and aligned qualities can be changed once per day, when the arcane archer prepares spells or, in the case of spontaneous spellcasters, after 8 hours of rest. <br/>At 3rd level, every nonmagical arrow fired by an arcane archer gains one of the following elemental themed weapon qualities: flaming, frost, or shock. <br/>At 5th level, every nonmagical arrow fired by an arcane archer gains the distance weapon quality.<br/>At 7th level, every nonmagical arrow fired by an arcane archer gains one of the following elemental burst weapon qualities: flaming burst, icy burst, or shocking burst. This ability replaces the ability gained at 3rd level.<br/>At 9th level, every nonmagical arrow fired by an arcane archer gains one of the following aligned weapon qualities: anarchic, axiomatic, holy, or unholy. The arcane archer cannot choose an ability that is the opposite of his alignment (for example, a lawful good arcane archer could not choose anarchic or unholy as his weapon quality).<br/>The bonuses granted by a magic bow apply as normal to arrows that have been enhanced with this ability. Only the larger enhancement bonus applies. Duplicate abilities do not stack.', 3, 'Ability' );
INSERT INTO ability VALUES ( 380, 'Imbue Arrow', 'At 2nd level, an arcane archer gains the ability to place an area spell upon an arrow. When the arrow is fired, the spell''s area is centered where the arrow lands, even if the spell could normally be centered only on the caster. This ability allows the archer to use the bow''s range rather than the spell''s range. A spell cast in this way uses its standard casting time and the arcane archer can fire the arrow as part of the casting. The arrow must be fired during the round that the casting is completed or the spell is wasted. If the arrow misses, the spell is wasted.', 2, 'Ability' );
INSERT INTO ability VALUES ( 381, 'Seeker Arrow', 'At 4th level, an arcane archer can launch an arrow at a target known to him within range, and the arrow travels to the target, even around corners. Only an unavoidable obstacle or the limit of the arrow''s range prevents the arrow''s flight. This ability negates cover and concealment modifiers, but otherwise the attack is rolled normally. Using this ability is a standard action (and shooting the arrow is part of the action). An arcane archer can use this ability once per day at 4th level, and one additional time per day for every two levels beyond 4th, to a maximum of four times per day at 10th level.', 2, 'Ability' );
INSERT INTO ability VALUES ( 382, 'Phase Arrow', 'At 6th level, an arcane archer can launch an arrow once per day at a target known to him within range, and the arrow travels to the target in a straight path, passing through any nonmagical barrier or wall in its way. (Any magical barrier stops the arrow.) This ability negates cover, concealment, armor, and shield modifiers, but otherwise the attack is rolled normally. Using this ability is a standard action (and shooting the arrow is part of the action). An arcane archer can use this ability once per day at 6th level, and one additional time per day for every two levels beyond 6th, to a maximum of three times per day at 10th level.', 2, 'Ability' );
INSERT INTO ability VALUES ( 383, 'Hail of Arrows', 'In lieu of his regular attacks, once per day an arcane archer of 8th level or higher can fire an arrow at each and every target within range, to a maximum of one target for every arcane archer level she has earned. Each attack uses the archer''s primary attack bonus, and each enemy may only be targeted by a single arrow.', 2, 'Ability' );
INSERT INTO ability VALUES ( 384, 'Arrow of Death', 'At 10th level, an arcane archer can create a special type of slaying arrow that forces the target, if damaged by the arrow''s attack, to make a Fortitude save or be slain immediately. The DC of this save is equal to 20 + the arcane archer''s Charisma modifier. It takes 1 day to make a slaying arrow, and the arrow only functions for the arcane archer who created it. The slaying arrow lasts no longer than 1 year, and the archer can only have one such arrow in existence at a time.', 2, 'Ability' );
INSERT INTO ability VALUES ( 385, 'Spells per Day - Arcane Trickster', 'When a new arcane trickster level is gained, the character gains new spells per day as if she had also gained a level in a spellcasting class she belonged to before adding the prestige class. She does not, however, gain other benefits a character of that class would have gained, except for additional spells per day, spells known (if she is a spontaneous spellcaster), and an increased effective level of spellcasting. If a character had more than one spellcasting class before becoming an arcane trickster, she must decide to which class she adds the new level for purposes of determining spells per day.', 0, 'Ability' );
INSERT INTO ability VALUES ( 386, 'Ranged Legerdemain', 'An arcane trickster can use Disable Device and Sleight of Hand at a range of 30 feet. Working at a distance increases the normal skill check DC by 5, and an arcane trickster cannot take 10 on this check. Any object to be manipulated must weigh 5 pounds or less. She can only use this ability if she has at least 1 rank in the skill being used.', 3, 'Ability' );
INSERT INTO ability VALUES ( 387, 'Sneak Attack - Arcane Trickster', 'This is exactly like the rogue ability of the same name. The extra damage dealt increases by +1d6 every other level (2nd, 4th, 6th, 8th, and 10th). If an arcane trickster gets a sneak attack bonus from another source, the bonuses on damage stack.', 0, 'Ability' );
INSERT INTO ability VALUES ( 388, 'Impromptu Sneak Attack', 'Beginning at 3rd level, once per day an arcane trickster can declare one melee or ranged attack she makes to be a sneak attack (the target can be no more than 30 feet distant if the impromptu sneak attack is a ranged attack). The target of an impromptu sneak attack loses any Dexterity bonus to AC, but only against that attack. The power can be used against any target, but creatures that are not subject to critical hits take no extra damage (though they still lose any Dexterity bonus to AC against the attack).<br/>At 7th level, an arcane trickster can use this ability twice per day.', 1, 'Ability' );
INSERT INTO ability VALUES ( 389, 'Tricky Spells', 'Starting at 5th level, an arcane trickster can cast her spells without their somatic or verbal components, as if using the Still Spell and Silent Spell feats. Spells cast using this ability do not increase in spell level or casting time. She can use this ability 3 times per day at 5th level and one additional time per every two levels thereafter, to a maximum of 5 times per day at 9th level. The arcane trickster decides to use this ability at the time of casting.', 3, 'Ability' );
INSERT INTO ability VALUES ( 390, 'Invisible Thief', 'At 9th level, an arcane trickster can become invisible, as if under the effects of greater invisibility, as a free action. She can remain invisible for a number of rounds per day equal to her arcane trickster level. Her caster level for this effect is equal to her caster level. These rounds need not be consecutive.', 3, 'Ability' );
INSERT INTO ability VALUES ( 391, 'Surprise Spells', 'At 10th level, an arcane trickster can add her sneak attack damage to any spell that deals damage, if the targets are flat-footed. This additional damage only applies to spells that deal hit point damage, and the additional damage is of the same type as the spell. If the spell allows a saving throw to negate or halve the damage, it also negates or halves the sneak attack damage.', 0, 'Ability' );
INSERT INTO ability VALUES ( 392, 'Sneak Attack - Assassin', 'This is exactly like the rogue ability of the same name. The extra damage dealt increases by +1d6 every other level (1st, 3rd, 5th, 7th, and 9th). If an assassin gets a sneak attack bonus from another source, the bonuses on damage stack.', 0, 'Ability' );
INSERT INTO ability VALUES ( 393, 'Death Attack', 'If an assassin studies his victim for 3 rounds and then makes a sneak attack with a melee weapon that successfully deals damage, the sneak attack has the additional effect of possibly either paralyzing or killing the target (assassin''s choice). Studying the victim is a standard action. The death attack fails if the target detects the assassin or recognizes the assassin as an enemy (although the attack might still be a sneak attack if the target is denied his Dexterity bonus to his Armor Class or is flanked). If the victim of such a death attack fails a Fortitude save (DC 10 + the assassin''s class level + the assassin''s Int modifier) against the kill effect, she dies. If the saving throw fails against the paralysis effect, the victim is rendered helpless and unable to act for 1d6 rounds plus 1 round per level of the assassin. If the victim''s saving throw succeeds, the attack is just a normal sneak attack. Once the assassin has completed the 3 rounds of study, he must make the death attack within the next 3 rounds.<br/>If a death attack is attempted and fails (the victim makes her save) or if the assassin does not launch the attack within 3 rounds of completing the study, 3 new rounds of study are required before he can attempt another death attack.', 1, 'Ability' );
INSERT INTO ability VALUES ( 394, 'Poison Use', 'Assassins are trained in the use of poison and cannot accidentally poison themselves when applying poison to a blade (see Poison).', 0, 'Ability' );
INSERT INTO ability VALUES ( 395, 'Save Bonus against Poison', 'At 2nd level, the assassin gains a +1 saving throw bonus against poisons. This bonus increase by +1 every two levels.', 0, 'Ability' );
INSERT INTO ability VALUES ( 397, 'Hidden Weapons', 'At 4th level, an assassin becomes a master at hiding weapons on his body. He adds his assassin level to all Sleight of Hand skill checks made to prevent others from noticing them.', 1, 'Ability' );
INSERT INTO ability VALUES ( 398, 'True Death', 'Starting at 4th level, anyone slain by an assassin''s death attack becomes more difficult to bring back from the dead. Spellcasters attempting to bring a creature back from the dead using raise dead or similar magic must make a caster level check with a DC equal to 15 + the assassin''s level or the spell fails and the material component is wasted. Casting remove curse the round before attempting to bring the creature back from the dead negates this chance. The DC of the remove curse is 10 + the assassin''s level.', 3, 'Ability' );
INSERT INTO ability VALUES ( 400, 'Quiet Death', 'At 6th level, whenever an assassin kills a creature using his death attack during a surprise round, he can also make a Stealth check, opposed by Perception checks of those in the vicinity to prevent them from identifying him as the assailant. If successful, those nearby might not even notice that the target is dead for a few moments, allowing the assassin to avoid detection. ', 1, 'Ability' );
INSERT INTO ability VALUES ( 401, 'Hide in Plain Sight - Shadow', 'A character can use the Stealth skill even while being observed. As long as she is within 10 feet of an area of dim light, a character can hide herself from view in the open without anything to actually hide behind. She cannot, however, hide in her own shadow.', 3, 'Ability' );
INSERT INTO ability VALUES ( 402, 'Swift Death', 'At 9th level, once per day, an assassin can make a death attack against a foe without studying the foe beforehand. He must still sneak attack his foe using a melee weapon that deals damage.', 1, 'Ability' );
INSERT INTO ability VALUES ( 403, 'Angel of Death', 'At 10th level, the assassin becomes a master of death. Once per day, when the assassin makes a successful death attack, he can cause the target''s body to crumble to dust. This prevents raise dead and resurrection (although true resurrection works as normal). The assassin must declare the use of this ability before the attack is made. If the attack misses or the target successfully saves against the death attack, this ability is wasted with no effect.', 3, 'Ability' );
INSERT INTO ability VALUES ( 404, 'Spells per Day - Dragon Disciple', 'At the indicated levels, a dragon disciple gains new spells per day as if he had also gained a level in an arcane spellcasting class he belonged to before adding the prestige class. He does not, however, gain other benefits a character of that class would have gained, except for additional spells per day, spells known (if he is a spontaneous spellcaster), and an increased effective level of spellcasting. If a character had more than one arcane spellcasting class before becoming a dragon disciple, he must decide to which class he adds the new level for purposes of determining spells per day.', 0, 'Ability' );
INSERT INTO ability VALUES ( 405, 'Blood of Dragons', 'A dragon disciple adds his level to his sorcerer levels when determining the powers gained from his bloodline. If the dragon disciple does not have levels of sorcerer, he instead gains bloodline powers of the draconic bloodline, using his dragon disciple level as his sorcerer level to determine the bonuses gained. He must choose a dragon type upon gaining his first level in this class and that type must be the same as his sorcerer type. This ability does not grant bonus spells to a sorcerer unless he possesses spell slots of an appropriate level. Such bonus spells are automatically granted if the sorcerer gains spell slots of the spell''s level.', 0, 'Ability' );
INSERT INTO ability VALUES ( 406, 'Natural Armor Increase', 'As his skin thickens, a dragon disciple takes on more and more of his progenitor''s physical aspect. At 1st, 4th, and 7th level, a dragon disciple gains an increase to the character''s existing natural armor (if any), as indicated on Table: Dragon Disciple. These armor bonuses stack. ', 1, 'Ability' );
INSERT INTO ability VALUES ( 407, 'Ability Boost', 'As a dragon disciple gains levels in this prestige class, his ability scores increase as noted on Table: Dragon Disciple. These increases stack and are gained as if through level advancement.', 1, 'Ability' );
INSERT INTO ability VALUES ( 408, 'Bloodline Feat', 'Upon reaching 2nd level, and every three levels thereafter, a dragon disciple receives one bonus feat, chosen from the draconic bloodline''s bonus feat list.', 1, 'Ability' );
INSERT INTO ability VALUES ( 409, 'Dragon Bite', 'At 2nd level, whenever the dragon disciple uses his bloodline to grow claws, he also gains a bite attack. This is a primary natural attack that deals 1d6 points of damage (1d4 if the dragon disciple is Small), plus 1-1/2 times the dragon disciple''s Strength modifier. Upon reaching 6th level, this bite also deals 1d6 points of energy damage. The type of damage dealt is determined by the dragon disciple''s bloodline.', 1, 'Ability' );
INSERT INTO ability VALUES ( 410, 'Breath Weapon', 'At 3rd level, a dragon disciple gains the breath weapon bloodline power, even if his level does not yet grant that power. Once his level is high enough to grant this ability through the bloodline, the dragon disciple gains an additional use of his breath weapon each day. The type and shape of the breath weapon depends on the type of dragon selected by the dragon disciple, as detailed under the Draconic sorcerer bloodline description. ', 3, 'Ability' );
INSERT INTO ability VALUES ( 411, 'Blindsense', 'At 5th level, the dragon disciple gains line of effect to that creature.<br/>Any opponent the dragon disciple cannot see still has total concealment against him, and the dragon disciple still has the normal miss chance when attacking foes that have concealment. Visibility still affects the movement of a creature with blindsense. A creature with blindsense is still denied its Dexterity bonus to Armor Class against attacks from creatures it cannot see. At 10th level, the range of this ability increases to 60 feet.', 1, 'Ability' );
INSERT INTO ability VALUES ( 412, 'Dragon Form', 'At 7th level, a dragon disciple can assume the form of a dragon. This ability works like form of the dragon II and the dragon disciple can use this ability twice per day. His caster level for this effect is equal to his effective sorcerer levels for his draconic bloodline. Whenever he casts form of the dragon, he must assume the form of a dragon of the same type as his bloodline.', 3, 'Ability' );
INSERT INTO ability VALUES ( 413, 'Wings', 'At 9th level, a dragon disciple gains the wings bloodline power, even if his level does not yet grant that power. Once his level is high enough to grant this ability through the bloodline, the dragon disciple''s speed increases to 90 feet.', 3, 'Ability' );
INSERT INTO ability VALUES ( 414, 'Canny Defense', 'When wearing light or no armor and not using a shield, a duelist adds 1 point of Intelligence bonus (if any) per duelist class level to her Dexterity bonus to modify Armor Class while wielding a melee weapon. If a duelist is caught flat-footed or otherwise denied her Dexterity bonus, she also loses this bonus.', 1, 'Ability' );
INSERT INTO ability VALUES ( 415, 'Precise Strike', 'A duelist gains the ability to strike precisely with a light or one-handed piercing weapon, adding her duelist level to her damage roll.<br/>When making a precise strike, a duelist cannot attack with a weapon in her other hand or use a shield. A duelist''s precise strike only works against living creatures with discernible anatomies. Any creature that is immune to critical hits is also immune to a precise strike, and any item or ability that protects a creature from critical hits also protects a creature from a precise strike.', 1, 'Ability' );
INSERT INTO ability VALUES ( 416, 'Improved Reaction', 'At 2nd level, a duelist gains a +2 bonus on initiative checks. At 8th level, the bonus increases to +4. This bonus stacks with the benefit provided by the Improved Initiative feat.', 1, 'Ability' );
INSERT INTO ability VALUES ( 417, 'Parry', 'At 2nd level, a duelist learns to parry the attacks of other creatures, causing them to miss. Whenever the duelist takes a full attack action with a light or one-handed piercing weapon, she can elect not to take one of her attacks. At any time before her next turn, she can attempt to parry an attack against her or an adjacent ally as an immediate action. To parry the attack, the duelist makes an attack roll, using the same bonuses as the attack she chose to forego during her previous action. If her attack roll is greater than the roll of the attacking creature, the attack automatically misses. For each size category that the attacking creature is larger than the duelist, the duelist takes a -4 penalty on her attack roll. The duelist also takes a -4 penalty when attempting to parry an attack made against an adjacent ally. The duelist must declare the use of this ability after the attack is announced, but before the roll is made.', 1, 'Ability' );
INSERT INTO ability VALUES ( 418, 'Enhanced Mobility', 'Starting at 3rd level, when wearing light or no armor and not using a shield, a duelist gains an additional +4 bonus to AC against attacks of opportunity caused when she moves out of a threatened square.', 1, 'Ability' );
INSERT INTO ability VALUES ( 419, 'Combat Reflexes', 'At 4th level, a duelist gains the benefit of the Combat Reflexes feat when using a light or one-handed piercing weapon.', 1, 'Ability' );
INSERT INTO ability VALUES ( 420, 'Grace', 'At 4th level, a duelist gains an additional +2 competence bonus on all Reflex saving throws. This ability functions for a duelist only when she is wearing light or no armor and not using a shield.', 1, 'Ability' );
INSERT INTO ability VALUES ( 421, 'Riposte', 'Starting at 5th level, a duelist can make an attack of opportunity against any creature whose attack she successfully parries, so long as the creature she is attacking is within reach.', 1, 'Ability' );
INSERT INTO ability VALUES ( 422, 'Acrobatic Charge', 'At 6th level, a duelist gains the ability to charge in situations where others cannot. She may charge over difficult terrain that normally slows movement. Depending on the circumstance, she may still need to make appropriate checks to successfully move over the terrain.', 1, 'Ability' );
INSERT INTO ability VALUES ( 423, 'Elaborate Defense', 'At 7th level and higher, if a duelist chooses to fight defensively or use total defense in melee combat, she gains an additional +1 dodge bonus to AC for every 3 levels of duelist she has attained.', 1, 'Ability' );
INSERT INTO ability VALUES ( 424, 'Deflect Arrows', 'At 9th level, a duelist gains the benefit of the Deflect Arrows feat when using a light or one-handed piercing weapon. The duelist does not need a free hand to use this feat.', 1, 'Ability' );
INSERT INTO ability VALUES ( 425, 'No Retreat', 'At 9th level, enemies adjacent to the duelist that take a withdraw action provoke an attack of opportunity from the duelist.', 1, 'Ability' );
INSERT INTO ability VALUES ( 426, 'Crippling Critical', 'When you confirm a critical hit using a light or one-handed piercing weapon, you can apply one of the following penalties in addition to the damage dealt: reduce all of the target''s speeds by 10 feet (minimum 5 feet), 1d4 points of Strength or Dexterity damage, -4 penalty on all saving throws, -4 penalty to Armor Class, or 2d6 points of bleed damage. These penalties last for 1 minute, except for ability damage, which must be healed normally, and bleed damage, which continues until the target receives magic healing or a DC 15 Heal skill check.', 1, 'Ability' );
INSERT INTO ability VALUES ( 427, 'Bonus Feat - Eldritch Knight', 'At 1st level, an eldritch knight may choose a bonus combat feat. This is in addition to the feats that a character of any class normally gets from advancing levels. The character must still meet any prerequisites for these bonus feats. An eldritch knight gains an additional combat feat at 5th and 9th level.', 0, 'Ability' );
INSERT INTO ability VALUES ( 428, 'Diverse Training', 'An eldritch knight adds his level to any levels of fighter he might have for the purpose of meeting the prerequisites for feats (if he has no fighter levels, treat his eldritch knight levels as levels of fighter). He also adds his level to any levels in an arcane spellcasting class for the purpose of meeting the prerequisites for feats.', 0, 'Ability' );
INSERT INTO ability VALUES ( 429, 'Spells per Day - Eldritch Knight', 'At the indicated levels, an eldritch knight gains new spells per day as if he had also gained a level in an arcane spellcasting class he belonged to before adding the prestige class. He does not, however, gain any other benefit a character of that class would have gained, except for additional spells per day, spells known (if he is a spontaneous spellcaster), and an increased effective level of spellcasting. If a character had more than one arcane spellcasting class before becoming an eldritch knight, he must decide to which class he adds the new level for purposes of determining spells per day.', 0, 'Ability' );
INSERT INTO ability VALUES ( 430, 'Spell Critical', 'At 10th level, whenever an eldritch knight successfully confirms a critical hit, he can cast a spell as a swift action. The spell must include the target of the attack as one of its targets or in its area of effect. Casting this spell does not provoke an attack of opportunity. The caster must still meet all of the spell''s components and must roll for arcane spell failure if necessary.', 1, 'Ability' );
INSERT INTO ability VALUES ( 431, 'Spells per Day/Spells Known', 'When a new loremaster level is gained, the character gains new spells per day as if he had also gained a level in a spellcasting class he belonged to before adding the prestige class. He does not, however, gain other benefits a character of that class would have gained, except for additional spells per day, spells known (if he is a spontaneous spellcaster), and an increased effective level of spellcasting. If a character had more than one spellcasting class before becoming a loremaster, he must decide to which class he adds the new level for purposes of determining spells per day.', 0, 'Ability' );
INSERT INTO ability VALUES ( 432, 'Secret', 'At 1st level and every two levels higher than 1st (3rd, 5th, 7th, and 9th), the loremaster chooses one secret from the Loremaster Secrets table. His level plus Intelligence modifier determines which secrets he can choose. He can''t choose the same secret twice.', 0, 'Ability' );
INSERT INTO ability VALUES ( 433, 'Lore', 'At 2nd level, a loremaster adds half his level to all Knowledge skill checks and may make such checks untrained. The bonuses gained from this ability stack with those gained from Bardic Knowledge.', 0, 'Ability' );
INSERT INTO ability VALUES ( 434, 'Bonus Languages - Loremaster', 'A loremaster can learn any new language at 4th and 8th level.', 0, 'Ability' );
INSERT INTO ability VALUES ( 435, 'Greater Lore', 'At 6th level, a loremaster gains the ability to understand magic items. Whenever a loremaster examines a magic item to determine its properties, he gains a +10 circumstance bonus on his Spellcraft skill check.', 1, 'Ability' );
INSERT INTO ability VALUES ( 436, 'True Lore', 'At 10th level, a loremaster''s knowledge becomes vast indeed. Once per day a loremaster can use his knowledge to gain the effect of a legend lore spell or an analyze dweomer spell. If used to create a legend lore effect, this ability has a casting time of 1 minute, regardless of how much information is already known about the subject in question.', 1, 'Ability' );
INSERT INTO ability VALUES ( 437, 'Spells per Day - Mystic Theurge', 'When a new mystic theurge level is gained, the character gains new spells per day as if he had also gained a level in any one arcane spellcasting class he belonged to before he added the prestige class and any one divine spellcasting class he belonged to previously. He does not, however, gain other benefits a character of that class would have gained. This essentially means that he adds the level of mystic theurge to the level of whatever other arcane spellcasting class and divine spellcasting class the character has, then determines spells per day, spells known, and caster level accordingly. If a character had more than one arcane spellcasting class or more than one divine spellcasting class before he became a mystic theurge, he must decide to which class he adds each level of mystic theurge for the purpose of determining spells per day.', 0, 'Ability' );
INSERT INTO ability VALUES ( 438, 'Combined Spells', 'A mystic theurge can prepare and cast spells from one of his spellcasting classes using the available slots from any of his other spellcasting classes. Spells prepared or cast in this way take up a slot one level higher than they originally occupied. This ability cannot be used to cast a spell at a lower level if that spell exists on both spell lists. At 1st level, a mystic theurge can prepare 1st-level spells from one of his spellcasting classes using the 2nd-level slots of the other spellcasting class. Every two levels thereafter, the level of spells that can be cast in this way increases by one, to a maximum of 5th-level spells at 9th level (these spells would take up 6th-level spell slots). The components of these spells do not change, but they otherwise follow the rules for the spellcasting class used to cast the spell. <br/>Spontaneous spellcasters can only select spells that they have prepared that day using non-spontaneous classes for this ability, even if the spells have already been cast. For example, a cleric/sorcerer/mystic theurge can use this ability to spontaneously cast a bless spell using a 2nd-level sorcerer spell slot, if the character had a prepared bless spell using a 1st-level cleric spell slot, even if that spell had already been cast that day.', 3, 'Ability' );
INSERT INTO ability VALUES ( 439, 'Spell Synthesis', 'At 10th level, a mystic theurge can cast two spells, one from each of his spellcasting classes, using one action. Both of the spells must have the same casting time. The mystic theurge can make any decisions concerning the spells independently. Any target affected by both of the spells takes a -2 penalty on saves made against each spell. The mystic theurge receives a +2 bonus on caster level checks made to overcome spell resistance with these two spells. A mystic theurge may use this ability once per day.', 3, 'Ability' );
INSERT INTO ability VALUES ( 441, 'Deep Pockets', 'A Pathfinder chronicler collects items as well as lore, picking up small amounts of this or that throughout her travels. As a result, she may carry unspecified equipment worth up to 100 gp per class level. This can be any kind of gear that can reasonably fit into a backpack, including potions and scrolls (but not any other sort of magic item). As a full-round action, the chronicler may dig through her pockets to retrieve an item she specifies at that time, deducting its value from the allocated amount of cost. This item cannot weigh more than 10 pounds. When the total remaining cost reaches 0, the chronicler can retrieve no more items until she refills her deep pockets by spending a few hours and an amount of gold to bring her total up to 100 gp per class level.<br/>In addition, if she takes 1 hour to pack her gear each day, she gains a +4 bonus to Strength to determine her light encumbrance. This does not affect her maximum carrying capacity. The efficient distribution of weight simply encumbers her less than the same amount of weight normally should. Finally, the Pathfinder chronicler gains a +4 bonus on Sleight of Hand checks made to conceal small objects on her person.', 1, 'Ability' );
INSERT INTO ability VALUES ( 442, 'Master Scribe', 'A Pathfinder chronicler adds her class level as a bonus on all Linguistics and Profession (scribe) checks, as well as Use Magic Device checks involving scrolls or other written magical items. A Pathfinder chronicler can make Linguistics checks to decipher text as a full-round action and can always take 10 on Linguistics and Profession (scribe) checks, even if distracted or endangered. ', 1, 'Ability' );
INSERT INTO ability VALUES ( 443, 'Live to Tell the Tale', 'At 2nd level, once per day per two class levels, a Pathfinder chronicler can attempt a new saving throw against any ongoing condition against which she failed a saving throw in a previous round, even if the effect is normally permanent. This ability has no effect on conditions that do not allow saving throws or against instantaneous effects. ', 1, 'Ability' );
INSERT INTO ability VALUES ( 444, 'Pathfinding', 'Beginning at 2nd level, a Pathfinder chronicler develops an excellent sense of direction and skill at leading others through difficult terrain or by following ancient maps. A Pathfinder chronicler gains a +5 bonus on Survival checks made to avoid becoming lost and to Intelligence checks to escape a maze spell. In addition, she always uses the ''road or trail'' overland movement modifier even when in trackless terrain, whether on foot or mounted. With a DC 15 Survival check, the Pathfinder chronicler can extend this benefit to one companion per class level. ', 1, 'Ability' );
INSERT INTO ability VALUES ( 445, 'Bardic Performance - Pathfinder Chronicler', 'At 3rd level, a Pathfinder chronicler gains this ability, which functions like the bard class feature of the same name, except that the chronicler''s effective bard level is 2 lower than her class level. Levels in this class stack with levels in any other class that grants a similar ability to determine her effective bard level.', 3, 'Ability' );
INSERT INTO ability VALUES ( 446, 'Improved Aid', 'Pathfinder chroniclers frequently serve as the companions of great heroes, standing by their sides and recording their deeds, but often lending a crucial helping hand. Starting at 3rd level, a Pathfinder chronicler using the aid another action grants a +4 bonus, rather than the normal +2. ', 1, 'Ability' );
INSERT INTO ability VALUES ( 447, 'Epic Tales', 'A 4th-level Pathfinder chronicler can inscribe a tale so evocative and so moving that it conveys the effects of bardic music through the written word. To create an epic tale requires the pathfinder chronicler to expend a number of rounds of her bardic performance ability equal to twice the duration of the epic tale (maximum duration of 10 rounds), and any relevant Performance skill check is made with Profession (scribe) instead. An epic tale affects only the reader, but it grants all the benefits that would normally apply for hearing a performance. A Pathfinder chronicler may apply the effects of any feats that affect bardic music to her epic tales. An epic tale retains its supernatural potency for 1 day per class level. It requires 1 hour to inscribe, a full-round action to activate, and has a duration equal to 1/2 the number of bardic performance rounds expended during its creation. Once activated, an epic tale''s magic is consumed.', 3, 'Ability' );
INSERT INTO ability VALUES ( 448, 'Whispering Campaign', 'Pathfinder chroniclers influence the world through their control of information and ability to shape public perception. At 5th level, as a special use of bardic music, the Pathfinder chronicler can create the effect of a doom spell as cast by a sorcerer of her class level by denouncing a creature in person. This is a language-dependent effect.<br/>Alternatively, the chronicler can denounce a particular target (an individual or a definable group of creatures) to others. This form of bardic music creates the effect of the enthrall spell, but at the end of the performance all creatures who failed to save shift their attitude toward the target of the oration by one step (in the direction of the Pathfinder chronicler''s choice) for 1 day per class level.', 1, 'Ability' );
INSERT INTO ability VALUES ( 449, 'Inspire Action', 'As a special use of bardic music, a 6th-level Pathfinder chronicler can exhort any one ally within hearing to a sudden surge of action, allowing her ally to immediately take an extra move action. This does not count against the ally''s number of actions on his own turn.<br/>At 9th level, she can enable an ally to immediately take a standard action instead. ', 3, 'Ability' );
INSERT INTO ability VALUES ( 450, 'Call Down the Legends', 'At 7th level, once per week as a full-round action, a Pathfinder chronicler can summon 2d4 4th-level human barbarians, as if she used a bronze horn of Valhalla, these summoned barbarians serve her with complete loyalty. The barbarians are constructs, not actual people (although they seem to be). They arrive with the normal starting equipment for barbarians and attack anyone the chronicler designates.', 3, 'Ability' );
INSERT INTO ability VALUES ( 451, 'Greater Epic Tales', 'At 8th level, the Pathfinder chronicler''s written word gains power. This ability functions like the chronicler''s epic tales ability, except that if read aloud, the bardic music takes effect as if the author had used the ability, but the effects are targeted by the reader and use the reader''s Charisma score where applicable. ', 3, 'Ability' );
INSERT INTO ability VALUES ( 452, 'Lay of the Exalted Dead', 'Once per week as a full-round action, a 10th-level Pathfinder chronicler can summon 1d4+1 5th-level human barbarians, as if she used an iron horn of Valhalla. The summoned barbarians serve her with complete and unquestioning loyalty. They are constructs, with the incorporeal subtype (they take 50% of the damage from corporeal sources, and no damage from nonmagical sources). They arrive wearing +2 studded leather and wielding +1 ghost touch greataxes (allowing them to deal full damage to corporeal creatures) and attack anyone the chronicler designates. To the chronicler and their allies, these exalted dead appear like a noble troop of spectral warriors. Her enemies, however, behold the terrible wrath of the ancient heroes and must succeed at Will saves or become shaken for 1 round per summoned barbarian (DC 15 + the Pathfinder chronicler''s Charisma modifier).', 3, 'Ability' );
INSERT INTO ability VALUES ( 457, 'Rogue Talent', 'At 3rd level, and every three levels thereafter, a shadowdancer gains a special ability that allows her to confound her foes. This functions as the rogue talent class feature. A shadowdancer cannot select an individual talent more than once. If a shadowdancer has the advanced talents rogue class feature, she can chose from the advanced talents list instead.', 0, 'Ability' );
INSERT INTO ability VALUES ( 458, 'Shadow Illusion', 'When a shadowdancer reaches 3rd level, she can create visual illusions. This ability functions as silent image, using the shadowdancer''s level as the caster level. A shadowdancer can use this ability once per day for every two shadowdancer levels she has attained. The DC for this ability is Charisma-based.', 2, 'Ability' );
INSERT INTO ability VALUES ( 459, 'Summon Shadow', 'At 3rd level, a shadowdancer can summon a shadow, an undead shade. Unlike a normal shadow, this shadow''s alignment matches that of the shadowdancer, and the creature cannot create spawn. The summoned shadow receives a +4 bonus on Will saves made to halve the damage from positive channeled energy and the shadow cannot be turned or commanded. This shadow serves as a companion to the shadowdancer and can communicate intelligibly with the shadowdancer. This shadow has a number of hit points equal to half the shadowdancer''s total. The shadow uses the shadowdancer''s base attack bonus and base save bonuses.<br/>If a shadow companion is destroyed, or the shadowdancer chooses to dismiss it, the shadowdancer must attempt a DC 15 Fortitude save. If the saving throw fails, the shadowdancer gains one permanent negative level. A successful saving throw avoids this negative level. A destroyed or dismissed shadow companion cannot be replaced for 30 days.', 3, 'Ability' );
INSERT INTO ability VALUES ( 460, 'Shadow Call', 'At 4th level, a shadowdancer can create creatures and effects out of raw shadow. This ability functions as shadow conjuration, using the shadowdancer''s level as the caster level. A shadowdancer can use this ability once per day at 4th level, plus one additional time per day for every two levels attained beyond 4th (2/day at 6th level, 3/day at 8th level, and 4/day at 10th level). Upon reaching 10th level, this ability functions as greater shadow conjuration. The DC for this ability is Charisma-based.', 2, 'Ability' );
INSERT INTO ability VALUES ( 461, 'Shadow Jump', 'At 4th level, a shadowdancer gains the ability to travel between shadows as if by means of a dimension door spell. The limitation is that the magical transport must begin and end in an area with at least some dim light. A shadowdancer can jump up to a total of 40 feet each day in this way, this may be a single jump of 40 feet or four jumps of 10 feet each. Every two levels higher than 4th, the distance a shadowdancer can jump each day doubles (80 feet at 6th, 160 feet at 8th, and 320 feet at 10th). This amount can be split among many jumps, but each one, no matter how small, counts as a 10-foot increment.', 3, 'Ability' );
INSERT INTO ability VALUES ( 465, 'Shadow Power', 'At 8th level, a shadowdancer can use raw shadow to damage her foes. This ability functions as shadow evocation, using the shadowdancer''s level as the caster level. A shadowdancer can use this ability once per day at 8th level, and one additional time per day upon reaching 10th level. The DC for this ability is Charisma-based. ', 2, 'Ability' );
INSERT INTO ability VALUES ( 467, 'Shadow Master', 'At 10th level, whenever a shadowdancer is in an area of dim light, she gains DR 10/- and a +2 luck bonus on all saving throws. In addition, whenever she successfully scores a critical hit against a foe who is in an area of dim light, that foe is blinded for 1d6 rounds.', 3, 'Ability' );
INSERT INTO ability VALUES ( 469, 'Spells - Air Domain', 'You can manipulate lightning, mist, and wind, traffic with air creatures, and are resistant to electricity damage.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 471, 'Spells - Animal Domain', 'You can speak with and befriend animals with ease. In addition, you treat Knowledge (nature) as a class skill.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 473, 'Spells - Artifice Domain', 'You can repair damage to objects, animate objects with life, and create objects from nothing.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 475, 'Spells - Chaos Domain', 'Your touch infuses life and weapons with chaos, and you revel in all things anarchic.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 477, 'Spells - Charm Domain', 'You can baffle and befuddle foes with a touch or a smile, and your beauty and grace are divine.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 479, 'Spells - Community Domain', 'Your touch can heal wounds, and your presence instills unity and strengthens emotional bonds.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 481, 'Spells - Darkness Domain', 'You manipulate shadows and darkness. In addition, you receive Blind-Fight as a bonus feat.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 483, 'Spells - Death Domain', 'You can cause the living to bleed at a touch, and find comfort in the presence of the dead.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 485, 'Spells - Destruction Domain', 'You revel in ruin and devastation, and can deliver particularly destructive attacks.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 487, 'Spells - Earth Domain', 'You have mastery over earth, metal, and stone, can fire darts of acid, and command earth creatures.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 489, 'Spells - Evil Domain', 'You are sinister and cruel, and have wholly pledged your soul to the cause of evil.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 491, 'Spells - Fire Domain', 'You can call forth fire, command creatures of the inferno, and your flesh does not burn.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 493, 'Spells - Glory Domain', 'You are infused with the glory of the divine, and are a true foe of the undead. In addition, when you channel positive energy to harm undead creatures, the save DC to halve the damage is increased by 2.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 495, 'Spells - Good Domain', 'You have pledged your life and soul to goodness and purity.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 497, 'Spells - Healing Domain', 'Your touch staves off pain and death, and your healing magic is particularly vital and potent.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 499, 'Spells - Knowledge Domain', 'You are a scholar and a sage of legends. In addition, you treat all Knowledge skills as class skills.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 501, 'Spells - Law Domain', 'You follow a strict and ordered code of laws, and in so doing, achieve enlightenment.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 503, 'Spells - Liberation Domain', 'You are a spirit of freedom and a staunch foe against all who would enslave and oppress.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 505, 'Spells - Luck Domain', 'You are infused with luck, and your mere presence can spread good fortune.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 507, 'Spells - Madness Domain', 'You embrace the madness that lurks deep in your heart, and can unleash it to drive your foes insane or to sacrifice certain abilities to hone others.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 509, 'Spells - Magic Domain', 'You are a true student of all things mystical, and see divinity in the purity of magic.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 511, 'Spells - Nobility Domain', 'You are a great leader, an inspiration to all who follow the teachings of your faith.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 513, 'Spells - Plant Domain', 'You find solace in the green, can grow defensive thorns, and can communicate with plants.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 515, 'Spells - Protection Domain', 'Your faith is your greatest source of protection, and you can use that faith to defend others. In addition, you receive a +1 resistance bonus on saving throws. This bonus increases by 1 for every 5 levels you possess. ', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 517, 'Spells - Repose Domain', 'You see death not as something to be feared, but as a final rest and reward for a life well spent. The taint of undeath is a mockery of what you hold dear.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 519, 'Spells - Rune Domain', 'In strange and eldritch runes you find potent magic. You gain Scribe Scroll as a bonus feat.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 521, 'Spells - Strength Domain', 'In strength and brawn there is truth—your faith gives you incredible might and power. ', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 523, 'Spells - Sun Domain', 'You see truth in the pure and burning light of the sun, and can call upon its blessing or wrath to work great deeds.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 525, 'Spells - Travel Domain', 'You are an explorer and find enlightenment in the simple joy of travel, be it by foot or conveyance or magic. Increase your base speed by 10 feet.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 527, 'Spells - Trickery Domain', 'You are a master of illusions and deceptions. Bluff, Disguise, and Stealth are class skills.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 529, 'Spells - War Domain', 'You are a crusader for your god, always ready and willing to fight to defend your faith.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 531, 'Spells - Water Domain', 'You can manipulate water and mist and ice, conjure creatures of water, and resist cold.', 0, 'SpelllistAbility' );
INSERT INTO ability VALUES ( 533, 'Spells - Weather Domain', 'With power over storm and sky, you can call down the wrath of the gods upon the world below.', 0, 'SpelllistAbility' );
